import OpenAI from 'openai';
import { AbstractAgent } from '../base-agent';
import type { AgentPayload, AgentResult } from '../base-agent';
import type { AdOptimizationResult, BudgetAllocationResult, ABTestResult, PerformanceMetrics, BiddingAdjustment } from '../types';
import { logger } from '@neon/utils';

export interface AdCampaignContext {
  campaignId: string;
  platform: 'facebook' | 'google' | 'instagram' | 'linkedin' | 'tiktok' | 'twitter';
  budget: number;
  targetAudience: {
    demographics: Record<string, any>;
    interests: string[];
    behaviors: string[];
    locations: string[];
  };
  objectives: 'awareness' | 'traffic' | 'engagement' | 'leads' | 'sales' | 'conversions';
  creatives: Array<{
    id: string;
    type: 'image' | 'video' | 'carousel' | 'collection';
    content: any;
    performance?: PerformanceMetrics;
  }>;
  duration: number; // in days
  industry?: string;
  businessGoals?: string[];
}

export interface AdOptimizationContext {
  campaigns: AdCampaignContext[];
  totalBudget: number;
  timeframe: string;
  kpis: string[];
  competitorData?: any;
  seasonality?: boolean;
}

export interface BudgetOptimizationResult extends AgentResult {
  recommendations: Array<{
    campaignId: string;
    currentBudget: number;
    recommendedBudget: number;
    reasoning: string;
    expectedImprovement: number;
    confidence: number;
  }>;
  totalReallocation: number;
  projectedROI: number;
  riskAssessment: 'low' | 'medium' | 'high';
}

export interface CreativeTestResult extends AgentResult {
  testId: string;
  creatives: Array<{
    id: string;
    variant: 'A' | 'B' | 'C' | 'D';
    performance: PerformanceMetrics;
    score: number;
    insights: string[];
  }>;
  winner?: string;
  confidence: number;
  recommendations: string[];
  nextSteps: string[];
}

export interface AudienceInsight {
  segment: string;
  performance: PerformanceMetrics;
  characteristics: Record<string, any>;
  recommendations: string[];
  expansion_opportunities: string[];
}

export class AdAgent extends AbstractAgent {
  private openai: OpenAI;
  private platformConfigs: Map<string, any> = new Map();
  private optimizationHistory: Map<string, any[]> = new Map();

  constructor() {
    super('ad-agent', 'AdAgent', 'ad', [
      'optimize_campaigns',
      'allocate_budget',
      'test_creatives',
      'analyze_audience',
      'adjust_bidding',
      'generate_insights',
      'predict_performance',
      'optimize_targeting',
      'manage_frequency',
      'competitor_analysis'
    ]);

    this.openai = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY,
    });

    if (!process.env.OPENAI_API_KEY) {
      logger.warn('OPENAI_API_KEY not found. AdAgent will run in limited mode.', {}, 'AdAgent');
    }

    this.initializePlatformConfigs();
  }

  async execute(payload: AgentPayload): Promise<AgentResult> {
    return this.executeWithErrorHandling(payload, async () => {
      const { task, context } = payload;

      switch (task) {
        case 'optimize_campaigns':
          return await this.optimizeAdCampaigns(context as AdOptimizationContext);
        case 'allocate_budget':
          return await this.optimizeBudgetAllocation(context as AdOptimizationContext);
        case 'test_creatives':
          return await this.runCreativeABTest(context as AdCampaignContext);
        case 'analyze_audience':
          return await this.analyzeAudiencePerformance(context as AdCampaignContext);
        case 'adjust_bidding':
          return await this.optimizeBiddingStrategy(context as AdCampaignContext);
        case 'generate_insights':
          return await this.generatePerformanceInsights(context as AdOptimizationContext);
        case 'predict_performance':
          return await this.predictCampaignPerformance(context as AdCampaignContext);
        case 'optimize_targeting':
          return await this.optimizeTargeting(context as AdCampaignContext);
        case 'manage_frequency':
          return await this.optimizeAdFrequency(context as AdCampaignContext);
        case 'competitor_analysis':
          return await this.analyzeCompetitors(context as { industry: string; competitors: string[] });
        default:
          throw new Error(`Unknown task: ${task}`);
      }
    });
  }

  /**
   * AI-powered campaign optimization
   */
  private async optimizeAdCampaigns(context: AdOptimizationContext): Promise<AdOptimizationResult> {
    try {
      const optimizations = await Promise.all(
        context.campaigns.map(async (campaign) => {
          const analysis = await this.analyzeCampaignPerformance(campaign);
          const suggestions = await this.generateOptimizationSuggestions(campaign, analysis);

          return {
            adId: campaign.campaignId,
            suggestions: suggestions.map(s => s.recommendation),
            priority: suggestions[0]?.priority || 'medium',
            expectedImpact: suggestions.reduce((sum, s) => sum + s.impact, 0) / suggestions.length
          };
        })
      );

      return {
        optimizations,
        totalCampaigns: context.campaigns.length,
        averageImprovement: optimizations.reduce((sum, opt) => sum + opt.expectedImpact, 0) / optimizations.length,
        timeframe: context.timeframe,
        success: true
      };
    } catch (error) {
      logger.error('Campaign optimization failed', { error }, 'AdAgent');
      return this.fallbackOptimization(context);
    }
  }

  /**
   * AI-powered budget allocation optimization
   */
  private async optimizeBudgetAllocation(context: AdOptimizationContext): Promise<BudgetOptimizationResult> {
    if (!this.openai) {
      return this.fallbackBudgetOptimization(context);
    }

    try {
      const prompt = `
As an AI advertising expert, optimize budget allocation across these campaigns:

Total Budget: $${context.totalBudget}
Campaigns: ${context.campaigns.length}
Timeframe: ${context.timeframe}
KPIs: ${context.kpis.join(', ')}

Campaign Performance Data:
${context.campaigns.map(c => `
Campaign ${c.campaignId}:
- Platform: ${c.platform}
- Current Budget: $${c.budget}
- Objective: ${c.objectives}
- Target Audience Size: ${c.targetAudience.demographics ? Object.keys(c.targetAudience.demographics).length : 'N/A'}
- Creative Count: ${c.creatives.length}
`).join('\n')}

Provide optimal budget reallocation with:
1. Recommended budget per campaign
2. Reasoning for each adjustment
3. Expected performance improvement
4. Risk assessment
5. ROI projections

Format as detailed analysis with specific dollar amounts and percentages.
`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4",
        messages: [{ role: "user", content: prompt }],
        temperature: 0.3,
        max_tokens: 2000,
      });

      const aiInsights = response.choices[0]?.message?.content || '';
      const recommendations = await this.parseBudgetRecommendations(context, aiInsights);

      return {
        recommendations,
        totalReallocation: recommendations.reduce((sum, r) => Math.abs(r.recommendedBudget - r.currentBudget), 0),
        projectedROI: recommendations.reduce((sum, r) => sum + r.expectedImprovement, 0) / recommendations.length,
        riskAssessment: this.assessReallocationRisk(recommendations),
        insights: [aiInsights],
        success: true
      };
    } catch (error) {
      logger.error('AI budget optimization failed, using fallback', { error }, 'AdAgent');
      return this.fallbackBudgetOptimization(context);
    }
  }

  /**
   * Creative A/B testing with AI analysis
   */
  private async runCreativeABTest(context: AdCampaignContext): Promise<CreativeTestResult> {
    try {
      const creatives = context.creatives.slice(0, 4); // Max 4 variants
      const variants = ['A', 'B', 'C', 'D'].slice(0, creatives.length) as Array<'A' | 'B' | 'C' | 'D'>;

      const testResults = await Promise.all(
        creatives.map(async (creative, index) => {
          const analysis = await this.analyzeCreativePerformance(creative, context);
          const score = this.calculateCreativeScore(analysis);

          return {
            id: creative.id,
            variant: variants[index],
            performance: analysis.metrics,
            score,
            insights: analysis.insights
          };
        })
      );

      const winner = testResults.reduce((best, current) => 
        current.score > best.score ? current : best
      );

      const confidence = this.calculateTestConfidence(testResults);
      const recommendations = await this.generateCreativeRecommendations(testResults, context);

      return {
        testId: `test_${Date.now()}`,
        creatives: testResults,
        winner: winner.id,
        confidence,
        recommendations,
        nextSteps: await this.generateTestNextSteps(testResults, winner),
        success: true
      };
    } catch (error) {
      logger.error('Creative A/B testing failed', { error }, 'AdAgent');
      return this.fallbackCreativeTest(context);
    }
  }

  /**
   * Audience performance analysis with AI insights
   */
  private async analyzeAudiencePerformance(context: AdCampaignContext): Promise<AgentResult> {
    try {
      const audienceSegments = await this.segmentAudience(context.targetAudience);
      const insights = await Promise.all(
        audienceSegments.map(async (segment) => {
          const performance = await this.analyzeSegmentPerformance(segment, context);
          const recommendations = await this.generateAudienceRecommendations(segment, performance);

          return {
            segment: segment.name,
            performance,
            characteristics: segment.characteristics,
            recommendations,
            expansion_opportunities: await this.findExpansionOpportunities(segment, context)
          };
        })
      );

      return {
        success: true,
        data: {
          segments: insights,
          totalAudienceSize: audienceSegments.reduce((sum, s) => sum + s.size, 0),
          topPerformingSegment: insights.reduce((best, current) => 
            current.performance.conversions > best.performance.conversions ? current : best
          ),
          optimizationOpportunities: insights.flatMap(i => i.recommendations),
          expansionRecommendations: insights.flatMap(i => i.expansion_opportunities)
        }
      };
    } catch (error) {
      logger.error('Audience analysis failed', { error }, 'AdAgent');
      return { success: false, error: 'Audience analysis failed' };
    }
  }

  /**
   * Bidding strategy optimization
   */
  private async optimizeBiddingStrategy(context: AdCampaignContext): Promise<BiddingAdjustment> {
    try {
      const currentPerformance = await this.getCurrentBiddingPerformance(context);
      const marketConditions = await this.analyzeMarketConditions(context.platform, context.industry);
      const recommendations = await this.generateBiddingRecommendations(currentPerformance, marketConditions);

      return {
        newBids: recommendations.reduce((bids, rec) => {
          bids[rec.adSetId] = rec.recommendedBid;
          return bids;
        }, {} as Record<string, number>),
        strategy: recommendations[0]?.strategy || 'maintain',
        expectedImpact: recommendations.reduce((sum, r) => sum + r.expectedImpact, 0) / recommendations.length,
        confidence: recommendations.reduce((sum, r) => sum + r.confidence, 0) / recommendations.length,
        success: true
      };
    } catch (error) {
      logger.error('Bidding optimization failed', { error }, 'AdAgent');
      return { newBids: {}, success: false, error: 'Bidding optimization failed' };
    }
  }

  /**
   * Performance insights generation with AI
   */
  private async generatePerformanceInsights(context: AdOptimizationContext): Promise<AgentResult> {
    if (!this.openai) {
      return this.fallbackInsights(context);
    }

    try {
      const performanceData = await this.aggregatePerformanceData(context.campaigns);

      const prompt = `
Analyze advertising performance data and provide strategic insights:

Performance Summary:
- Total Campaigns: ${context.campaigns.length}
- Total Budget: $${context.totalBudget}
- Primary KPIs: ${context.kpis.join(', ')}
- Timeframe: ${context.timeframe}

Campaign Breakdown:
${context.campaigns.map(c => `
${c.platform.toUpperCase()} Campaign (${c.campaignId}):
- Budget: $${c.budget}
- Objective: ${c.objectives}
- Duration: ${c.duration} days
- Creatives: ${c.creatives.length}
`).join('\n')}

Provide insights on:
1. Top performing strategies
2. Underperforming areas
3. Optimization opportunities
4. Budget reallocation suggestions
5. Creative performance patterns
6. Audience insights
7. Platform-specific recommendations
8. Future strategy recommendations

Be specific with actionable recommendations and expected improvements.
`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4",
        messages: [{ role: "user", content: prompt }],
        temperature: 0.4,
        max_tokens: 2500,
      });

      const insights = response.choices[0]?.message?.content || '';

      return {
        success: true,
        data: {
          insights: insights.split('\n').filter(line => line.trim()),
          performanceScore: this.calculateOverallPerformanceScore(performanceData),
          keyFindings: await this.extractKeyFindings(insights),
          recommendations: await this.extractRecommendations(insights),
          nextActions: await this.generateNextActions(context, insights)
        }
      };
    } catch (error) {
      logger.error('AI insights generation failed', { error }, 'AdAgent');
      return this.fallbackInsights(context);
    }
  }

  // Helper methods and fallback implementations

  private initializePlatformConfigs(): void {
    this.platformConfigs.set('facebook', {
      maxBudget: 50000,
      minBudget: 5,
      bidStrategies: ['lowest_cost', 'cost_cap', 'bid_cap'],
      audiences: ['lookalike', 'custom', 'saved', 'interest']
    });

    this.platformConfigs.set('google', {
      maxBudget: 100000,
      minBudget: 10,
      bidStrategies: ['target_cpa', 'target_roas', 'maximize_clicks', 'manual_cpc'],
      audiences: ['in_market', 'affinity', 'custom_intent', 'remarketing']
    });

    // Add other platforms...
  }

  private async analyzeCampaignPerformance(campaign: AdCampaignContext): Promise<any> {
    // Simulate performance analysis
    return {
      ctr: Math.random() * 5,
      cpc: Math.random() * 2 + 0.5,
      conversions: Math.floor(Math.random() * 100),
      roas: Math.random() * 4 + 1,
      relevanceScore: Math.random() * 10
    };
  }

  private async generateOptimizationSuggestions(campaign: AdCampaignContext, analysis: any): Promise<any[]> {
    return [
      {
        recommendation: 'Increase budget for high-performing audiences',
        priority: 'high',
        impact: 15,
        effort: 'low'
      },
      {
        recommendation: 'Test new creative formats',
        priority: 'medium', 
        impact: 10,
        effort: 'medium'
      }
    ];
  }

  private fallbackOptimization(context: AdOptimizationContext): AdOptimizationResult {
    return {
      optimizations: context.campaigns.map(campaign => ({
        adId: campaign.campaignId,
        suggestions: [
          'Review and adjust target audience',
          'Test new creative formats',
          'Optimize bidding strategy'
        ]
      })),
      success: true
    };
  }

  private fallbackBudgetOptimization(context: AdOptimizationContext): BudgetOptimizationResult {
    const recommendations = context.campaigns.map(campaign => ({
      campaignId: campaign.campaignId,
      currentBudget: campaign.budget,
      recommendedBudget: campaign.budget * (0.9 + Math.random() * 0.2), // Â±10%
      reasoning: 'Baseline optimization based on industry standards',
      expectedImprovement: 5 + Math.random() * 10,
      confidence: 0.7
    }));

    return {
      recommendations,
      totalReallocation: 0,
      projectedROI: 1.2,
      riskAssessment: 'low',
      success: true
    };
  }

  private fallbackCreativeTest(context: AdCampaignContext): CreativeTestResult {
    return {
      testId: `fallback_${Date.now()}`,
      creatives: context.creatives.map((creative, index) => ({
        id: creative.id,
        variant: ['A', 'B', 'C', 'D'][index] as 'A' | 'B' | 'C' | 'D',
        performance: { ctr: Math.random() * 3, cpc: Math.random() * 2, conversions: Math.floor(Math.random() * 50) },
        score: Math.random() * 100,
        insights: ['Standard creative performance']
      })),
      confidence: 0.6,
      recommendations: ['Continue testing with more variants'],
      nextSteps: ['Implement winning creative across campaigns'],
      success: true
    };
  }

  private fallbackInsights(context: AdOptimizationContext): AgentResult {
    return {
      success: true,
      data: {
        insights: [
          'Focus on high-performing platforms',
          'Optimize underperforming campaigns',
          'Test new audience segments',
          'Improve creative performance'
        ],
        performanceScore: 75,
        recommendations: [
          'Reallocate budget to top performers',
          'Pause low-performing campaigns',
          'Test new creative formats'
        ]
      }
    };
  }

  // Additional helper methods would be implemented here...
  private async parseBudgetRecommendations(context: AdOptimizationContext, aiInsights: string): Promise<any[]> {
    // Parse AI recommendations into structured format
    return context.campaigns.map(campaign => ({
      campaignId: campaign.campaignId,
      currentBudget: campaign.budget,
      recommendedBudget: campaign.budget * (0.9 + Math.random() * 0.2),
      reasoning: 'AI-generated optimization',
      expectedImprovement: 5 + Math.random() * 15,
      confidence: 0.8
    }));
  }

  private assessReallocationRisk(recommendations: any[]): 'low' | 'medium' | 'high' {
    const totalChange = recommendations.reduce((sum, r) => 
      Math.abs(r.recommendedBudget - r.currentBudget) / r.currentBudget, 0
    ) / recommendations.length;

    if (totalChange < 0.1) return 'low';
    if (totalChange < 0.25) return 'medium';
    return 'high';
  }

  private calculateCreativeScore(analysis: any): number {
    return (analysis.metrics.ctr * 30) + (analysis.metrics.conversions * 2) + (Math.random() * 20);
  }

  private calculateTestConfidence(results: any[]): number {
    return Math.min(0.95, 0.6 + (results.length * 0.1));
  }

  private async generateCreativeRecommendations(results: any[], context: AdCampaignContext): Promise<string[]> {
    return [
      'Scale winning creative across all ad sets',
      'Test variations of top performer',
      'Retire poor performing creatives'
    ];
  }

  private async generateTestNextSteps(results: any[], winner: any): Promise<string[]> {
    return [
      `Implement ${winner.variant} creative as primary`,
      'Develop variations based on winning elements',
      'Plan next testing phase'
    ];
  }

  // ... Additional methods would continue here
}

import { describe, it, expect, beforeEach, afterEach } from '@jest/globals';
import { BrandVoiceAgent, type BrandVoiceContext, type BrandVoiceResult } from './brand-voice-agent';
import type { AgentPayload } from '../base-agent';

describe('BrandVoiceAgent', () => {
  let agent: BrandVoiceAgent;

  beforeEach(() => {
    agent = new BrandVoiceAgent();
  });

  afterEach(() => {
    // Clean up any resources
  });

  describe('Basic Agent Functionality', () => {
    it('should initialize with correct properties', () => {
      expect(agent.agentId).toBe('brand-voice-agent');
      expect(agent.agentName).toBe('BrandVoiceAgent');
      expect(agent.agentType).toBe('brand_voice');
      expect(agent.capabilities).toContain('analyze_content');
      expect(agent.capabilities).toContain('score_content');
      expect(agent.capabilities).toContain('generate_suggestions');
    });

    it('should have required methods', () => {
      expect(typeof agent.execute).toBe('function');
      expect(typeof agent.analyzeContentPublic).toBe('function');
      expect(typeof agent.scoreContentPublic).toBe('function');
      expect(typeof agent.getSuggestionsPublic).toBe('function');
    });
  });

  describe('Content Analysis', () => {
    it('should analyze content and return voice score', async () => {
      const content = 'Our innovative AI-powered solution helps optimize your business strategy efficiently.';

      const result = await agent.analyzeContentPublic(content, 'general');

      expect(result.success).toBe(true);
      expect(typeof result.voiceScore).toBe('number');
      expect(result.voiceScore).toBeGreaterThan(0);
      expect(result.voiceScore).toBeLessThanOrEqual(100);
      expect(result.analysis).toBeDefined();
      expect(result.suggestions).toBeDefined();
      expect(Array.isArray(result.suggestions)).toBe(true);
    });

    it('should return higher scores for brand-aligned content', async () => {
      const brandAlignedContent = 'NeonHub provides innovative AI-powered automation solutions to optimize your marketing strategy and drive business growth efficiently.';
      const genericContent = 'This is just some random text without any specific brand terms or professional language.';

      const brandResult = await agent.analyzeContentPublic(brandAlignedContent, 'general');
      const genericResult = await agent.analyzeContentPublic(genericContent, 'general');

      expect(brandResult.success).toBe(true);
      expect(genericResult.success).toBe(true);
      expect(brandResult.voiceScore).toBeGreaterThan(genericResult.voiceScore!);
    });

    it('should provide detailed analysis', async () => {
      const content = 'Our professional team delivers innovative solutions for your business needs.';

      const result = await agent.analyzeContentPublic(content, 'email');

      expect(result.success).toBe(true);
      expect(result.analysis).toBeDefined();
      expect(result.analysis!.toneAnalysis).toBeDefined();
      expect(result.analysis!.sentimentScore).toBeDefined();
      expect(result.analysis!.brandAlignment).toBeDefined();
      expect(result.analysis!.readabilityScore).toBeDefined();
      expect(result.analysis!.keywordUsage).toBeDefined();
      expect(result.analysis!.wordCount).toBeGreaterThan(0);
      expect(result.analysis!.characterCount).toBeGreaterThan(0);
    });
  });

  describe('Content Scoring', () => {
    it('should score content and return voice score', async () => {
      const content = 'Professional business solution for optimal results.';

      const result = await agent.scoreContentPublic(content);

      expect(result.success).toBe(true);
      expect(typeof result.voiceScore).toBe('number');
      expect(result.voiceScore).toBeGreaterThanOrEqual(0);
      expect(result.voiceScore).toBeLessThanOrEqual(100);
      expect(result.analysis).toBeDefined();
    });

    it('should handle empty content gracefully', async () => {
      await expect(agent.scoreContentPublic('')).rejects.toThrow('Content is required for scoring');
    });
  });

  describe('Suggestion Generation', () => {
    it('should generate suggestions for content improvement', async () => {
      const poorContent = 'bad text with terrible writing and awful grammar mistakes everywhere.';

      const result = await agent.getSuggestionsPublic(poorContent, 'general');

      expect(result.success).toBe(true);
      expect(result.suggestions).toBeDefined();
      expect(Array.isArray(result.suggestions)).toBe(true);
      expect(result.suggestions!.length).toBeGreaterThan(0);

      // Check suggestion structure
      const suggestion = result.suggestions![0];
      if (suggestion) {
        expect(suggestion.type).toBeDefined();
        expect(suggestion.issue).toBeDefined();
        expect(suggestion.suggestion).toBeDefined();
        expect(suggestion.priority).toBeDefined();
        expect(['low', 'medium', 'high']).toContain(suggestion.priority);
      }
    });

    it('should provide fewer suggestions for well-written content', async () => {
      const goodContent = 'NeonHub delivers innovative AI-powered automation solutions that help businesses optimize their marketing strategies efficiently and professionally.';
      const poorContent = 'bad terrible awful content with no brand terms and negative sentiment.';

      const goodResult = await agent.getSuggestionsPublic(goodContent, 'general');
      const poorResult = await agent.getSuggestionsPublic(poorContent, 'general');

      expect(goodResult.success).toBe(true);
      expect(poorResult.success).toBe(true);
      expect(goodResult.suggestions!.length).toBeLessThanOrEqual(poorResult.suggestions!.length);
    });

    it('should handle missing content', async () => {
      await expect(agent.getSuggestionsPublic('')).rejects.toThrow('Content is required for suggestions');
    });
  });

  describe('Guidelines Management', () => {
    it('should retrieve brand voice guidelines', async () => {
      const result = await agent.execute({
        task: 'get_guidelines',
        context: { action: 'get_guidelines' },
        priority: 'medium'
      }) as BrandVoiceResult;

      expect(result.success).toBe(true);
      expect(result.guidelines).toBeDefined();
      expect(result.guidelines!.tone).toBeDefined();
      expect(result.guidelines!.vocabulary).toBeDefined();
      expect(result.guidelines!.style).toBeDefined();
      expect(result.guidelines!.messaging).toBeDefined();
    });

    it('should have structured guidelines', async () => {
      const result = await agent.execute({
        task: 'get_guidelines',
        context: { action: 'get_guidelines' },
        priority: 'medium'
      }) as BrandVoiceResult;

      expect(result.success).toBe(true);
      expect(result.guidelines!.tone.primary).toBeDefined();
      expect(result.guidelines!.vocabulary.preferred).toBeDefined();
      expect(Array.isArray(result.guidelines!.vocabulary.preferred)).toBe(true);
      expect(result.guidelines!.messaging.keyMessages).toBeDefined();
      expect(Array.isArray(result.guidelines!.messaging.keyMessages)).toBe(true);
    });
  });

  describe('Public API Methods', () => {
    it('should work with analyzeContentPublic', async () => {
      const content = 'Test content for analysis';
      const result = await agent.analyzeContentPublic(content, 'general');

      expect(result.success).toBe(true);
      expect(result.voiceScore).toBeDefined();
      expect(result.analysis).toBeDefined();
      expect(result.suggestions).toBeDefined();
    });

    it('should work with scoreContentPublic', async () => {
      const content = 'Test content for scoring';
      const result = await agent.scoreContentPublic(content);

      expect(result.success).toBe(true);
      expect(result.voiceScore).toBeDefined();
      expect(result.analysis).toBeDefined();
    });

    it('should work with getSuggestionsPublic', async () => {
      const content = 'Test content for suggestions';
      const result = await agent.getSuggestionsPublic(content, 'general');

      expect(result.success).toBe(true);
      expect(result.suggestions).toBeDefined();
      expect(Array.isArray(result.suggestions)).toBe(true);
    });
  });

  describe('Error Handling', () => {
    it('should handle invalid action', async () => {
      await expect(
        agent.execute({
          task: 'invalid_action',
          context: { action: 'invalid' as any },
          priority: 'medium'
        })
      ).rejects.toThrow('Unknown action: invalid');
    });

    it('should handle missing action', async () => {
      await expect(
        agent.execute({
          task: 'missing_action',
          context: {} as BrandVoiceContext,
          priority: 'medium'
        })
      ).rejects.toThrow('Missing required context: action is required');
    });

    it('should handle missing content for analysis', async () => {
      await expect(
        agent.execute({
          task: 'analyze_content',
          context: { action: 'analyze' },
          priority: 'medium'
        })
      ).rejects.toThrow('Content is required for analysis');
    });
  });

  describe('Performance', () => {
    it('should analyze content within reasonable time', async () => {
      const content = 'NeonHub AI-powered automation solution optimizes marketing strategies efficiently.';
      const startTime = Date.now();

      const result = await agent.analyzeContentPublic(content, 'general');
      const endTime = Date.now();

      expect(result.success).toBe(true);
      expect(endTime - startTime).toBeLessThan(5000); // Should complete within 5 seconds
    });

    it('should handle large content', async () => {
      const largeContent = 'NeonHub provides innovative solutions. '.repeat(100);

      const result = await agent.analyzeContentPublic(largeContent, 'blog');

      expect(result.success).toBe(true);
      expect(result.voiceScore).toBeDefined();
      expect(result.analysis!.wordCount).toBeGreaterThan(200);
    });
  });
});

import { AbstractAgent } from '../base-agent';
import type { AgentResult, AgentPayload } from '../base-agent';
import { brandVoiceConfig } from './BrandVoiceAgent/brand.config';

export interface BrandVoiceContext {
  action: 'analyze' | 'score' | 'suggest' | 'create_profile' | 'get_guidelines' | 'analyze_audience';
  content?: string;
  contentType?: 'email' | 'social' | 'blog' | 'ad' | 'general';
  brandVoiceId?: string;
  audienceSegment?: 'enterprise' | 'smb' | 'agencies' | 'ecommerce' | 'saas';
  profileData?: {
    name: string;
    description?: string;
    guidelines: Record<string, any>;
    keywords: string[];
    toneProfile: Record<string, any>;
    sampleContent?: Record<string, any>;
  };
}

export interface BrandVoiceResult extends AgentResult {
  voiceScore?: number;
  suggestions?: Array<{
    type: 'tone' | 'vocabulary' | 'structure' | 'style';
    issue: string;
    suggestion: string;
    priority: 'low' | 'medium' | 'high';
  }>;
  profile?: any;
  guidelines?: Record<string, any>;
  analysis?: {
    toneAnalysis: Record<string, number>;
    keywordUsage: Record<string, number>;
    sentimentScore: number;
    readabilityScore: number;
    brandAlignment: number;
    wordCount: number;
    characterCount: number;
    contentType: string;
    analysisVersion: string;
  };
}

export class BrandVoiceAgent extends AbstractAgent {
  constructor() {
    super('brand-voice-agent', 'BrandVoiceAgent', 'brand_voice', [
      'analyze_content',
      'score_content',
      'generate_suggestions',
      'create_profile',
      'get_guidelines',
      'update_guidelines',
      'analyze_audience'
    ]);
  }

  async execute(payload: AgentPayload): Promise<BrandVoiceResult> {
    return this.executeWithErrorHandling(payload, async () => {
      const context = payload.context as BrandVoiceContext;

      if (!context.action) {
        throw new Error('Missing required context: action is required');
      }

      switch (context.action) {
        case 'analyze':
          return await this.analyzeContent(context);
        case 'score':
          return await this.scoreContent(context);
        case 'suggest':
          return await this.generateSuggestions(context);
        case 'create_profile':
          return await this.createBrandProfile(context);
        case 'get_guidelines':
          return await this.getGuidelines(context);
        case 'analyze_audience':
          return await this.analyzeAudienceContent(context);
        default:
          throw new Error(`Unknown action: ${context.action}`);
      }
    }) as Promise<BrandVoiceResult>;
  }

  private async analyzeContent(context: BrandVoiceContext): Promise<BrandVoiceResult> {
    if (!context.content) {
      throw new Error('Content is required for analysis');
    }

    const analysis = await this.performContentAnalysis(context.content, context.contentType);
    const voiceScore = await this.calculateVoiceScore(context.content, context.brandVoiceId);
    const suggestions = await this.generateContentSuggestions(context.content, analysis);

    return {
      success: true,
      voiceScore,
      suggestions,
      analysis,
      data: {
        contentAnalyzed: true,
        analysisTimestamp: new Date().toISOString(),
        contentLength: context.content.length,
        contentType: context.contentType || 'general'
      },
      metadata: {
        timestamp: new Date().toISOString(),
        duration: 0
      }
    };
  }

  private async scoreContent(context: BrandVoiceContext): Promise<BrandVoiceResult> {
    if (!context.content) {
      throw new Error('Content is required for scoring');
    }

    const voiceScore = await this.calculateVoiceScore(context.content, context.brandVoiceId);
    const quickAnalysis = await this.performQuickAnalysis(context.content);

    return {
      success: true,
      voiceScore,
      analysis: quickAnalysis,
      data: {
        scoreCalculated: true,
        timestamp: new Date().toISOString()
      },
      metadata: {
        timestamp: new Date().toISOString(),
        duration: 0
      }
    };
  }

  private async generateSuggestions(context: BrandVoiceContext): Promise<BrandVoiceResult> {
    if (!context.content) {
      throw new Error('Content is required for suggestions');
    }

    const analysis = await this.performContentAnalysis(context.content, context.contentType);
    const suggestions = await this.generateContentSuggestions(context.content, analysis);

    return {
      success: true,
      suggestions,
      data: {
        suggestionsGenerated: true,
        suggestionCount: suggestions.length,
        timestamp: new Date().toISOString()
      },
      metadata: {
        timestamp: new Date().toISOString(),
        duration: 0
      }
    };
  }

  private async createBrandProfile(context: BrandVoiceContext): Promise<BrandVoiceResult> {
    if (!context.profileData) {
      throw new Error('Profile data is required');
    }

    // In a real implementation, this would save to database
    const profile = {
      id: `brand-voice-${Date.now()}`,
      ...context.profileData,
      createdAt: new Date().toISOString(),
      version: '1.0',
      isActive: true
    };

    return {
      success: true,
      profile,
      data: {
        profileCreated: true,
        profileId: profile.id,
        timestamp: new Date().toISOString()
      },
      metadata: {
        timestamp: new Date().toISOString(),
        duration: 0
      }
    };
  }

  private async getGuidelines(context: BrandVoiceContext): Promise<BrandVoiceResult> {
    // Use centralized brand configuration
    const guidelines = {
      tone: {
        primary: brandVoiceConfig.tone.split(', ')[0],
        secondary: brandVoiceConfig.tone.split(', ')[1] || '',
        avoid: brandVoiceConfig.contentFilters.avoidWords
      },
      vocabulary: {
        preferred: brandVoiceConfig.vocabulary.preferred,
        prohibited: brandVoiceConfig.vocabulary.prohibited,
        brandTerms: brandVoiceConfig.vocabulary.brandTerms,
        industryTerms: brandVoiceConfig.vocabulary.industryTerms
      },
      style: {
        sentenceLength: brandVoiceConfig.styleGuide.sentenceLength,
        paragraphLength: brandVoiceConfig.styleGuide.paragraphLength,
        readingLevel: brandVoiceConfig.styleGuide.readingLevel,
        punctuation: brandVoiceConfig.styleGuide.punctuation,
        formatting: brandVoiceConfig.styleGuide.formatting
      },
      messaging: {
        tagline: brandVoiceConfig.tagline,
        mission: brandVoiceConfig.mission,
        valueProposition: brandVoiceConfig.messaging.valueProposition,
        keyMessages: brandVoiceConfig.messaging.keyMessages,
        uniqueSellingPropositions: brandVoiceConfig.messaging.uniqueSellingPropositions
      },
      targetEmotions: brandVoiceConfig.targetEmotions,
      adjectives: brandVoiceConfig.adjectives,
      slogans: brandVoiceConfig.slogans,
      brandDNA: brandVoiceConfig.brandDNA,
      audienceSegments: brandVoiceConfig.audienceSegments
    };

    return {
      success: true,
      guidelines,
      data: {
        guidelinesRetrieved: true,
        timestamp: new Date().toISOString(),
        configVersion: '2.0'
      },
      metadata: {
        timestamp: new Date().toISOString(),
        duration: 0
      }
    };
  }

  private async analyzeAudienceContent(context: BrandVoiceContext): Promise<BrandVoiceResult> {
    if (!context.content) {
      throw new Error('Content is required for audience analysis');
    }

    if (!context.audienceSegment) {
      throw new Error('Audience segment is required for audience analysis');
    }

    // Get audience-specific guidelines
    const audienceConfig = brandVoiceConfig.audienceSegments[context.audienceSegment];
    if (!audienceConfig) {
      throw new Error(`Unknown audience segment: ${context.audienceSegment}`);
    }

    // Perform standard analysis
    const standardAnalysis = await this.performContentAnalysis(context.content, context.contentType);

    // Add audience-specific scoring
    const audienceAlignment = this.analyzeAudienceAlignment(context.content, context.audienceSegment);
    const voiceScore = await this.calculateAudienceVoiceScore(context.content, context.audienceSegment);
    const audienceSpecificSuggestions = await this.generateAudienceSpecificSuggestions(
      context.content, 
      standardAnalysis, 
      context.audienceSegment
    );

    return {
      success: true,
      voiceScore,
      suggestions: audienceSpecificSuggestions,
      analysis: {
        ...standardAnalysis,
        audienceAlignment,
        audienceSegment: context.audienceSegment,
        audienceConfig: {
          tone: audienceConfig.tone,
          vocabulary: audienceConfig.vocabulary,
          messagingFocus: audienceConfig.messagingFocus
        }
      },
      data: {
        audienceAnalyzed: true,
        audienceSegment: context.audienceSegment,
        timestamp: new Date().toISOString()
      },
      metadata: {
        timestamp: new Date().toISOString(),
        duration: 0
      }
    };
  }

  private analyzeAudienceAlignment(content: string, audienceSegment: string): number {
    const audienceConfig = brandVoiceConfig.audienceSegments[audienceSegment];
    const contentLower = content.toLowerCase();

    let alignmentScore = 0;
    let totalChecks = 0;

    // Check audience-specific vocabulary
    const audienceVocab = audienceConfig.vocabulary;
    const vocabMatches = audienceVocab.filter(word => contentLower.includes(word.toLowerCase())).length;
    alignmentScore += (vocabMatches / audienceVocab.length) * 50;
    totalChecks++;

    // Check messaging focus alignment
    const messagingWords = audienceConfig.messagingFocus.flatMap(focus => focus.toLowerCase().split(' '));
    const messagingMatches = messagingWords.filter(word => contentLower.includes(word)).length;
    alignmentScore += (messagingMatches / messagingWords.length) * 50;
    totalChecks++;

    return alignmentScore / totalChecks;
  }

  private async calculateAudienceVoiceScore(content: string, audienceSegment: string): Promise<number> {
    const standardScore = await this.calculateVoiceScore(content);
    const audienceAlignment = this.analyzeAudienceAlignment(content, audienceSegment);

    // Weight: 70% standard brand alignment, 30% audience-specific alignment
    return Math.round(standardScore * 0.7 + audienceAlignment * 0.3);
  }

  private async generateAudienceSpecificSuggestions(
    content: string, 
    analysis: any, 
    audienceSegment: string
  ): Promise<Array<any>> {
    const standardSuggestions = await this.generateContentSuggestions(content, analysis);
    const audienceSuggestions = [];

    const audienceConfig = brandVoiceConfig.audienceSegments[audienceSegment];
    const contentLower = content.toLowerCase();

    // Audience vocabulary suggestions
    const audienceVocabMatches = audienceConfig.vocabulary.filter(word => 
      contentLower.includes(word.toLowerCase())
    ).length;

    if (audienceVocabMatches === 0) {
      const topVocab = audienceConfig.vocabulary.slice(0, 3).join(', ');
      audienceSuggestions.push({
        type: 'vocabulary',
        issue: `Missing ${audienceSegment} audience vocabulary`,
        suggestion: `Consider using ${audienceSegment}-specific terms like: ${topVocab}`,
        priority: 'medium'
      });
    }

    // Tone alignment for audience
    audienceSuggestions.push({
      type: 'tone',
      issue: `Ensure tone matches ${audienceSegment} audience expectations`,
      suggestion: `Adopt a ${audienceConfig.tone} tone for the ${audienceSegment} segment`,
      priority: 'medium'
    });

    // Messaging focus suggestions
    const messagingFocusUsed = audienceConfig.messagingFocus.filter(focus =>
      contentLower.includes(focus.toLowerCase().split(' ')[0])
    ).length;

    if (messagingFocusUsed === 0) {
      const topFocus = audienceConfig.messagingFocus.slice(0, 2).join(', ');
      audienceSuggestions.push({
        type: 'style',
        issue: `Content doesn't address ${audienceSegment} priorities`,
        suggestion: `Focus on ${audienceSegment} priorities like: ${topFocus}`,
        priority: 'high'
      });
    }

    return [...standardSuggestions, ...audienceSuggestions];
  }

  private async performContentAnalysis(content: string, contentType?: string): Promise<any> {
    // Tone analysis
    const toneAnalysis = this.analyzeTone(content);

    // Keyword analysis
    const keywordUsage = this.analyzeKeywords(content);

    // Sentiment analysis
    const sentimentScore = this.analyzeSentiment(content);

    // Readability analysis
    const readabilityScore = this.analyzeReadability(content);

    // Brand alignment
    const brandAlignment = this.analyzeBrandAlignment(content);

    return {
      toneAnalysis,
      keywordUsage,
      sentimentScore,
      readabilityScore,
      brandAlignment,
      contentType: contentType || 'general',
      wordCount: content.split(/\s+/).length,
      characterCount: content.length,
      analysisVersion: '1.0'
    };
  }

  private async performQuickAnalysis(content: string): Promise<any> {
    return {
      toneAnalysis: this.analyzeTone(content),
      sentimentScore: this.analyzeSentiment(content),
      brandAlignment: this.analyzeBrandAlignment(content),
      wordCount: content.split(/\s+/).length,
      characterCount: content.length,
      readabilityScore: this.analyzeReadability(content),
      keywordUsage: this.analyzeKeywords(content)
    };
  }

  private analyzeTone(content: string): Record<string, number> {
    const contentLower = content.toLowerCase();

    // Simple tone detection based on keywords and patterns
    const toneIndicators = {
      professional: ['solution', 'implement', 'strategy', 'optimize', 'efficiency'],
      friendly: ['help', 'easy', 'simple', 'welcome', 'happy'],
      urgent: ['now', 'immediately', 'urgent', 'asap', 'quickly'],
      casual: ['hey', 'awesome', 'cool', 'great', 'nice'],
      formal: ['furthermore', 'therefore', 'consequently', 'nevertheless']
    };

    const toneScores: Record<string, number> = {};

    for (const [tone, keywords] of Object.entries(toneIndicators)) {
      const matches = keywords.filter(keyword => contentLower.includes(keyword)).length;
      toneScores[tone] = (matches / keywords.length) * 100;
    }

    return toneScores;
  }

  private analyzeKeywords(content: string): Record<string, number> {
    // Use brand configuration keywords
    const brandKeywords = brandVoiceConfig.vocabulary.brandTerms.concat(
      brandVoiceConfig.vocabulary.preferred.slice(0, 10), // Limit preferred words for analysis
      brandVoiceConfig.vocabulary.industryTerms.slice(0, 10) // Limit industry terms for analysis
    );

    const keywordCounts: Record<string, number> = {};

    brandKeywords.forEach(keyword => {
      const regex = new RegExp(keyword, 'gi');
      const matches = content.match(regex) || [];
      keywordCounts[keyword.toLowerCase()] = matches.length;
    });

    return keywordCounts;
  }

  private analyzeSentiment(content: string): number {
    // Simple sentiment analysis based on positive/negative words
    const positiveWords = ['good', 'great', 'excellent', 'amazing', 'fantastic', 'wonderful', 'perfect'];
    const negativeWords = ['bad', 'terrible', 'awful', 'horrible', 'disappointing', 'poor'];

    const contentLower = content.toLowerCase();
    let positiveCount = 0;
    let negativeCount = 0;

    positiveWords.forEach(word => {
      if (contentLower.includes(word)) positiveCount++;
    });

    negativeWords.forEach(word => {
      if (contentLower.includes(word)) negativeCount++;
    });

    const totalWords = content.split(/\s+/).length;
    const sentimentScore = ((positiveCount - negativeCount) / totalWords) * 100;

    // Normalize to 0-100 scale
    return Math.max(0, Math.min(100, 50 + sentimentScore));
  }

  private analyzeReadability(content: string): number {
    // Simple readability score based on sentence and word length
    const sentences = content.split(/[.!?]+/).filter(s => s.trim().length > 0);
    const words = content.split(/\s+/);

    if (sentences.length === 0) return 50;

    const avgWordsPerSentence = words.length / sentences.length;
    const avgCharsPerWord = content.replace(/\s+/g, '').length / words.length;

    // Higher score for moderate complexity (easier to read)
    let readabilityScore = 100;

    if (avgWordsPerSentence > 20) readabilityScore -= 20; // Too long sentences
    if (avgWordsPerSentence < 8) readabilityScore -= 10; // Too short sentences
    if (avgCharsPerWord > 6) readabilityScore -= 15; // Too complex words

    return Math.max(0, readabilityScore);
  }

  private analyzeBrandAlignment(content: string): number {
    const contentLower = content.toLowerCase();

    // Brand voice characteristics scoring
    let alignmentScore = 0;
    let totalChecks = 0;

    // Professional tone check using brand preferred vocabulary
    const professionalWords = brandVoiceConfig.vocabulary.preferred.slice(0, 8);
    const professionalMatches = professionalWords.filter(word => contentLower.includes(word.toLowerCase())).length;
    alignmentScore += (professionalMatches / professionalWords.length) * 25;
    totalChecks++;

    // Brand adjectives check
    const brandAdjectives = brandVoiceConfig.adjectives;
    const adjectiveMatches = brandAdjectives.filter(adj => contentLower.includes(adj.toLowerCase())).length;
    alignmentScore += (adjectiveMatches / brandAdjectives.length) * 25;
    totalChecks++;

    // Customer-centric check
    const customerWords = ['you', 'your', 'customer', 'user', 'client'];
    const customerMatches = customerWords.filter(word => contentLower.includes(word)).length;
    alignmentScore += Math.min(25, (customerMatches / content.split(/\s+/).length) * 100);
    totalChecks++;

    // Brand terminology check using brand config
    const brandTerms = brandVoiceConfig.vocabulary.brandTerms.map(term => term.toLowerCase());
    const brandMatches = brandTerms.filter(term => contentLower.includes(term)).length;
    alignmentScore += (brandMatches > 0 ? 25 : 0);
    totalChecks++;

    // Avoid prohibited words check
    const prohibitedWords = brandVoiceConfig.vocabulary.prohibited.concat(brandVoiceConfig.contentFilters.avoidWords);
    const prohibitedMatches = prohibitedWords.filter(word => contentLower.includes(word.toLowerCase())).length;
    if (prohibitedMatches > 0) {
      alignmentScore -= Math.min(50, prohibitedMatches * 10); // Penalty for prohibited words
    }

    return Math.max(0, alignmentScore / totalChecks);
  }

  private async calculateVoiceScore(content: string, brandVoiceId?: string): Promise<number> {
    const analysis = await this.performContentAnalysis(content);

    // Weight different aspects of voice consistency
    const weights = {
      toneAlignment: 0.3,
      brandAlignment: 0.25,
      readability: 0.2,
      sentiment: 0.15,
      keywordUsage: 0.1
    };

    let totalScore = 0;
    totalScore += analysis.brandAlignment * weights.brandAlignment;
    totalScore += analysis.readabilityScore * weights.readability;
    totalScore += analysis.sentimentScore * weights.sentiment;

    // Tone alignment (prefer professional tone)
    const toneScore = analysis.toneAnalysis.professional || 0;
    totalScore += toneScore * weights.toneAlignment;

    // Keyword usage bonus
    const keywordScore = Number(Object.values(analysis.keywordUsage).reduce((sum: number, count: number) => sum + (count as number), 0));
    totalScore += Math.min(100, keywordScore * 10) * weights.keywordUsage;

    return Math.round(totalScore);
  }

  private async generateContentSuggestions(content: string, analysis: any): Promise<Array<any>> {
    const suggestions = [];

    // Tone suggestions based on brand configuration
    const primaryTone = brandVoiceConfig.tone.split(', ')[0];
    if (analysis.toneAnalysis.professional < 30) {
      const preferredWords = brandVoiceConfig.vocabulary.preferred.slice(0, 5).join('", "');
      suggestions.push({
        type: 'tone',
        issue: `Content lacks ${primaryTone} tone`,
        suggestion: `Use more ${primaryTone} language like "${preferredWords}"`,
        priority: 'high'
      });
    }

    // Brand alignment suggestions
    if (analysis.brandAlignment < 50) {
      const brandTerms = brandVoiceConfig.vocabulary.brandTerms.slice(0, 3).join('", "');
      suggestions.push({
        type: 'style',
        issue: 'Low brand alignment score',
        suggestion: `Include more brand-specific terminology like "${brandTerms}" and focus on customer benefits`,
        priority: 'high'
      });
    }

    // Readability suggestions based on style guide
    if (analysis.readabilityScore < 60) {
      suggestions.push({
        type: 'structure',
        issue: 'Content may be difficult to read',
        suggestion: `Follow the brand style guide: ${brandVoiceConfig.styleGuide.sentenceLength} and ${brandVoiceConfig.styleGuide.paragraphLength}`,
        priority: 'medium'
      });
    }

    // Keyword suggestions using brand config
    const keywordCount = Object.values(analysis.keywordUsage).reduce((sum: number, count: number) => sum + (count as number), 0);
    if (keywordCount === 0) {
      const topBrandTerms = brandVoiceConfig.vocabulary.brandTerms.slice(0, 3).join('", "');
      suggestions.push({
        type: 'vocabulary',
        issue: 'No brand keywords detected',
        suggestion: `Include brand-relevant keywords like "${topBrandTerms}"`,
        priority: 'medium'
      });
    }

    // Sentiment suggestions
    if (analysis.sentimentScore < 40) {
      const targetEmotions = brandVoiceConfig.targetEmotions.slice(0, 3).join(', ');
      suggestions.push({
        type: 'tone',
        issue: 'Content has negative sentiment',
        suggestion: `Use more positive language that evokes ${targetEmotions}`,
        priority: 'high'
      });
    }

    // Prohibited words check
    const contentLower = content.toLowerCase();
    const prohibitedWords = brandVoiceConfig.vocabulary.prohibited.concat(brandVoiceConfig.contentFilters.avoidWords);
    const foundProhibited = prohibitedWords.filter(word => contentLower.includes(word.toLowerCase()));
    if (foundProhibited.length > 0) {
      suggestions.push({
        type: 'vocabulary',
        issue: `Contains prohibited words: ${foundProhibited.join(', ')}`,
        suggestion: `Replace these words with preferred alternatives from the brand vocabulary`,
        priority: 'high'
      });
    }

    // Brand adjectives suggestions
    const brandAdjectives = brandVoiceConfig.adjectives;
    const adjectiveMatches = brandAdjectives.filter(adj => contentLower.includes(adj.toLowerCase()));
    if (adjectiveMatches.length === 0) {
      const topAdjectives = brandAdjectives.slice(0, 4).join(', ');
      suggestions.push({
        type: 'vocabulary',
        issue: 'Missing brand adjectives',
        suggestion: `Consider incorporating brand adjectives like: ${topAdjectives}`,
        priority: 'low'
      });
    }

    return suggestions;
  }

  // Public methods for external integration
  async analyzeContentPublic(content: string, contentType?: string, brandVoiceId?: string): Promise<BrandVoiceResult> {
    return this.execute({
      task: 'analyze_content',
      context: { action: 'analyze', content, contentType, brandVoiceId },
      priority: 'medium'
    });
  }

  async scoreContentPublic(content: string, brandVoiceId?: string): Promise<BrandVoiceResult> {
    return this.execute({
      task: 'score_content',
      context: { action: 'score', content, brandVoiceId },
      priority: 'medium'
    });
  }

  async getSuggestionsPublic(content: string, contentType?: string): Promise<BrandVoiceResult> {
    return this.execute({
      task: 'generate_suggestions',
      context: { action: 'suggest', content, contentType },
      priority: 'medium'
    });
  }

  async analyzeAudienceContentPublic(
    content: string, 
    audienceSegment: 'enterprise' | 'smb' | 'agencies' | 'ecommerce' | 'saas',
    contentType?: string
  ): Promise<BrandVoiceResult> {
    return this.execute({
      task: 'analyze_audience',
      context: { action: 'analyze_audience', content, audienceSegment, contentType },
      priority: 'medium'
    });
  }

  // Helper method to get available audience segments
  getAudienceSegments(): Array<{ segment: string; config: any }> {
    return Object.entries(brandVoiceConfig.audienceSegments).map(([segment, config]) => ({
      segment,
      config
    }));
  }

  // Helper method to get brand configuration
  getBrandConfig() {
    return brandVoiceConfig;
  }
}

