import { AbstractAgent } from '../base-agent';
import type { AgentResult, AgentPayload } from '../base-agent';
import OpenAI from 'openai';
import { logger } from '@neon/utils';
import * as fs from 'fs/promises';
import * as path from 'path';

// Core interfaces for email marketing
export interface EmailTemplate {
  id: string;
  name: string;
  subject: string;
  content: string;
  htmlContent?: string;
  variables: string[];
  type: 'welcome' | 'nurture' | 'promotion' | 'retention' | 'follow_up' | 'newsletter' | 'abandoned_cart';
  industry?: string;
  tone?: 'professional' | 'casual' | 'friendly' | 'urgent' | 'promotional';
  estimatedReadTime?: number;
}

export interface EmailSequence {
  id: string;
  name: string;
  description: string;
  emails: Array<{
    templateId: string;
    delayDays: number;
    delayHours?: number;
    condition?: string;
    subject: string;
    content: string;
    htmlContent?: string;
  }>;
  triggerType: 'signup' | 'purchase' | 'abandonment' | 'manual' | 'behavior' | 'date_based';
  targetAudience?: string;
  estimatedDuration: number;
}

export interface EmailRecipient {
  email: string;
  firstName?: string;
  lastName?: string;
  company?: string;
  customFields?: Record<string, any>;
  segmentTags?: string[];
  preferences?: {
    frequency?: 'daily' | 'weekly' | 'monthly';
    categories?: string[];
    unsubscribed?: boolean;
  };
  behaviorData?: {
    lastOpen?: Date;
    lastClick?: Date;
    totalOpens?: number;
    totalClicks?: number;
    avgEngagement?: number;
  };
}

export interface EmailSequenceInput {
  topic: string;
  audience: string;
  businessType?: string;
  sequenceLength?: number;
  tone?: 'professional' | 'casual' | 'friendly' | 'urgent';
  goals?: string[];
  industry?: string;
}

export interface EmailSequenceOutput {
  sequenceId: string;
  name: string;
  description: string;
  emails: Array<{
    step: number;
    subject: string;
    content: string;
    htmlContent?: string;
    delayDays: number;
    purpose: string;
    keyPoints: string[];
  }>;
  estimatedPerformance: {
    openRate: string;
    clickRate: string;
    conversionRate: string;
  };
  recommendations: string[];
}

export interface PersonalizationInput {
  baseEmail: string;
  userTraits: Record<string, any>;
  segmentData?: {
    segment: string;
    characteristics: string[];
    preferences?: string[];
  };
  businessContext?: string;
}

export interface PersonalizationOutput {
  personalizedSubject: string;
  personalizedContent: string;
  personalizedHtml?: string;
  personalizationScore: number;
  appliedPersonalizations: Array<{
    type: string;
    field: string;
    originalValue: string;
    personalizedValue: string;
  }>;
  recommendations: string[];
}

export interface EmailPerformanceData {
  campaignId: string;
  sent: number;
  delivered: number;
  opens: number;
  clicks: number;
  conversions?: number;
  unsubscribes?: number;
  bounces?: number;
  complaints?: number;
  timeRange: string;
}

export interface PerformanceAnalysis {
  score: number;
  metrics: {
    deliveryRate: number;
    openRate: number;
    clickRate: number;
    conversionRate: number;
    unsubscribeRate: number;
    bounceRate: number;
    engagementScore: number;
  };
  insights: string[];
  recommendations: string[];
  benchmarks: {
    industry: string;
    openRateBenchmark: number;
    clickRateBenchmark: number;
    performance: 'above' | 'below' | 'average';
  };
  optimizationSuggestions: Array<{
    category: string;
    suggestion: string;
    impact: 'low' | 'medium' | 'high';
    effort: 'easy' | 'medium' | 'hard';
    priority: number;
  }>;
}

export interface ABTestInput {
  name: string;
  variants: Array<{
    name: string;
    subject?: string;
    content?: string;
    sendTime?: string;
    fromName?: string;
  }>;
  testMetric: 'open_rate' | 'click_rate' | 'conversion_rate';
  sampleSize: number;
  duration: number;
  audience: EmailRecipient[];
}

export interface ABTestResult {
  testId: string;
  status: 'running' | 'completed' | 'stopped';
  winner?: string;
  variants: Array<{
    id: string;
    name: string;
    performance: {
      sent: number;
      opens: number;
      clicks: number;
      conversions: number;
      openRate: number;
      clickRate: number;
      conversionRate: number;
    };
    confidence: number;
    isWinner?: boolean;
  }>;
  insights: string[];
  recommendations: string[];
}

