        ticketId,
        status: ticket.status,
        priority: ticket.priority,
        category: ticket.category,
        resolutionTime: ticket.resolvedAt 
          ? (ticket.resolvedAt.getTime() - ticket.createdAt.getTime()) / 60000 
          : null,
