interface SupportTicket {
  id: string;
  customer: {
    id: string;
    name: string;
    email: string;
    phone?: string;
    avatar?: string;
    tier: 'basic' | 'premium' | 'enterprise';
    segments: string[];
    totalTickets: number;
    averageRating: number;
    lastContact: Date;
