const AGENTS = [
  'Sarah Thompson',
  'Michael Rodriguez',
  'Emily Davis', 
  'David Kim',
  'Jessica Brown'
];

export default function CustomerSupportInboxPage(): JSX.Element {
  // State
  const [selectedTicket, setSelectedTicket] = useState<string | null>(null);
  const [tickets, setTickets] = useState<SupportTicket[]>(MOCK_TICKETS);
  const [searchQuery, setSearchQuery] = useState('');
  const [statusFilter, setStatusFilter] = useState('all');
  const [newMessage, setNewMessage] = useState('');
  const [showCustomerProfile, setShowCustomerProfile] = useState(false);
  const [agentSuggestion, setAgentSuggestion] = useState<AgentSuggestion | null>(null);
  const [showToast, setShowToast] = useState<{ show: boolean; message: string; type: 'success' | 'error' }>({ show: false, message: '', type: 'success' });

  const selectedTicketData = selectedTicket ? tickets.find(t => t.id === selectedTicket) : null;

  // tRPC hooks
  const _classifyMessageMutation = trpc.support.classifyMessage.useMutation();
  const generateReplyMutation = trpc.support.generateReply.useMutation({
    onSuccess: (data) => {
      if (data.success && data.data) {
        setAgentSuggestion({
          reply: data.data.reply,
          confidence: data.data.confidence,
          tone: data.data.tone,
          escalationRecommended: data.data.escalationRecommended,
          suggestedActions: data.data.suggestedActions || []
        });
        showToastMessage('AI reply generated successfully!', 'success');
      }
    },
    onError: (error) => {
      showToastMessage(error.message, 'error');
    },
  });

  const analyzeSentimentMutation = trpc.support.analyzeSentiment.useMutation({
    onSuccess: (data) => {
      if (data.success && data.data && selectedTicketData) {
        // Update the selected ticket with sentiment data
        setTickets(prev => prev.map(ticket => 
          ticket.id === selectedTicket 
            ? { ...ticket, sentiment: data.data }
            : ticket
        ));
      }
    },
  });

  const escalateTicketMutation = trpc.support.escalateTicket.useMutation({
    onSuccess: (data) => {
      if (data.success && selectedTicketData) {
        setTickets(prev => prev.map(ticket => 
          ticket.id === selectedTicket 
            ? { ...ticket, escalated: true, priority: 'critical' }
            : ticket
        ));
        showToastMessage('Ticket escalated successfully!', 'success');
      }
    },
    onError: (error) => {
      showToastMessage(error.message, 'error');
    },
  });

  // Helper functions
  const showToastMessage = (message: string, type: 'success' | 'error'): void => {
    setShowToast({ show: true, message, type });
    setTimeout(() => setShowToast({ show: false, message: '', type: 'success' }), 3000);
  };

  const filteredTickets = tickets.filter(ticket => {
    const matchesSearch = 
      ticket.customer.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      ticket.subject.toLowerCase().includes(searchQuery.toLowerCase()) ||
      ticket.customer.email.toLowerCase().includes(searchQuery.toLowerCase());

    const matchesFilter = statusFilter === 'all' || ticket.status === statusFilter;

    return matchesSearch && matchesFilter;
  });

  const handleGenerateAIReply = useCallback(async (): Promise<void> => {
    if (!selectedTicketData || !selectedTicketData.messages.length) return;

    const lastCustomerMessage = selectedTicketData.messages
      .filter(m => m.sender === 'customer')
      .pop();

    if (!lastCustomerMessage) return;

    generateReplyMutation.mutate({
      message: lastCustomerMessage.content,
      tone: 'professional',
      customer: {
        name: selectedTicketData.customer.name,
        tier: selectedTicketData.customer.tier,
      },
      context: {
        ticketHistory: selectedTicketData.messages.map(m => ({
          message: m.content,
          response: m.sender === 'agent' ? m.content : undefined,
          timestamp: m.timestamp
        }))
      }
    });
  }, [selectedTicketData, generateReplyMutation]);

  const handleSendMessage = useCallback(async (): Promise<void> => {
    if (!newMessage.trim() || !selectedTicketData) return;

    const newMsg: Message = {
      id: `msg_${Date.now()}`,
      sender: 'agent',
      content: newMessage,
      timestamp: new Date(),
      type: 'text'
    };

    setTickets(prev => prev.map(ticket => 
      ticket.id === selectedTicket 
        ? { 
            ...ticket, 
            messages: [...ticket.messages, newMsg],
            lastMessage: newMessage,
            lastMessageTime: new Date()
          }
        : ticket
    ));

    setNewMessage('');
    setAgentSuggestion(null);
    showToastMessage('Message sent successfully!', 'success');
  }, [newMessage, selectedTicketData, selectedTicket]);

  const handleStatusChange = (newStatus: string): void => {
    if (!selectedTicketData) return;

    setTickets(prev => prev.map(ticket => 
      ticket.id === selectedTicket 
        ? { ...ticket, status: newStatus as SupportTicket['status'] }
        : ticket
    ));
  };

  const handleEscalateTicket = useCallback((): void => {
    if (!selectedTicketData) return;

    escalateTicketMutation.mutate({
      ticketId: selectedTicket!,
      reason: 'Customer requires specialized assistance',
      priority: 'critical',
      urgency: 'high'
    });
  }, [selectedTicketData, selectedTicket, escalateTicketMutation]);

  const handleAssignAgent = (agentName: string): void => {
    if (!selectedTicketData) return;

    setTickets(prev => prev.map(ticket => 
      ticket.id === selectedTicket 
        ? { ...ticket, assignedTo: agentName }
        : ticket
    ));
  };

  const useSuggestedReply = (): void => {
    if (agentSuggestion) {
      setNewMessage(agentSuggestion.reply);
      setAgentSuggestion(null);
    }
  };

  const getSentimentColor = (sentiment?: { sentiment: string; score: number }): string => {
    if (!sentiment) return 'bg-gray-100 text-gray-800';

    switch (sentiment.sentiment) {
      case 'positive': return 'bg-green-100 text-green-800';
      case 'negative': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getSentimentIcon = (sentiment?: { sentiment: string }): string => {
    if (!sentiment) return 'ðŸ˜';

    switch (sentiment.sentiment) {
      case 'positive': return 'ðŸ˜Š';
      case 'negative': return 'ðŸ˜Ÿ';
      default: return 'ðŸ˜';
    }
  };

  const getPriorityColor = (priority: string): string => {
    switch (priority) {
      case 'critical': return 'bg-red-600 text-white';
      case 'high': return 'bg-red-100 text-red-800';
      case 'medium': return 'bg-yellow-100 text-yellow-800';
      case 'low': return 'bg-green-100 text-green-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusColor = (status: string): string => {
    switch (status) {
      case 'open': return 'bg-blue-100 text-blue-800';
      case 'in_progress': return 'bg-yellow-100 text-yellow-800';
      case 'pending_customer': return 'bg-orange-100 text-orange-800';
      case 'resolved': return 'bg-green-100 text-green-800';
      case 'closed': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const formatTime = (date: Date): string => {
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffMins = Math.floor(diffMs / (1000 * 60));
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));

    if (diffMins < 1) return 'Just now';
    if (diffMins < 60) return `${diffMins}m ago`;
    if (diffHours < 24) return `${diffHours}h ago`;

    return format(date, 'MMM dd, h:mm a');
  };

  // Auto-analyze sentiment when ticket is selected
  useEffect(() => {
    if (selectedTicketData && selectedTicketData.messages.length > 0) {
      const lastMessage = selectedTicketData.messages[selectedTicketData.messages.length - 1];
      if (lastMessage.sender === 'customer' && !lastMessage.sentiment) {
        analyzeSentimentMutation.mutate({
          message: lastMessage.content
        });
      }
    }
  }, [selectedTicket]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-blue-900 to-slate-900 flex">
      {/* Toast Notification */}
      {showToast.show && (
        <div className={`fixed top-4 right-4 z-50 p-4 rounded-lg shadow-lg transition-all duration-300 ${
          showToast.type === 'success' ? 'bg-green-600' : 'bg-red-600'
        } text-white`}>
          {showToast.message}
        </div>
      )}

      {/* Sidebar - Ticket List */}
      <div className="w-80 bg-slate-800/50 backdrop-blur-sm border-r border-slate-700">
      {/* Header */}
        <div className="p-4 border-b border-slate-700">
          <h1 className="text-xl font-bold text-white mb-4">ðŸ“ž Support Inbox</h1>

          {/* Search */}
          <div className="relative mb-4">
            <input
              type="text"
              placeholder="Search tickets..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 pl-10 text-white placeholder:text-slate-400 focus:outline-none focus:border-blue-500"
            />
            <span className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400">ðŸ”</span>
          </div>

          {/* Filter */}
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-white focus:outline-none focus:border-blue-500"
          >
            <option value="all">All Tickets</option>
            <option value="open">Open</option>
            <option value="in_progress">In Progress</option>
            <option value="pending_customer">Pending Customer</option>
            <option value="resolved">Resolved</option>
            <option value="closed">Closed</option>
          </select>
        </div>

        {/* Ticket List */}
        <div className="flex-1 overflow-y-auto">
          {filteredTickets.map((ticket) => (
            <div
              key={ticket.id}
              onClick={() => setSelectedTicket(ticket.id)}
              className={`p-4 border-b border-slate-700 cursor-pointer hover:bg-slate-700/30 transition-colors ${
                selectedTicket === ticket.id ? 'bg-blue-600/20 border-r-2 border-blue-500' : ''
              }`}
            >
              <div className="flex items-start gap-3">
                <div className="w-10 h-10 bg-slate-600 rounded-full flex items-center justify-center text-white text-sm font-medium">
                  {ticket.customer.name.split(' ').map(n => n[0]).join('')}
                </div>

                <div className="flex-1 min-w-0">
                  <div className="flex items-center justify-between mb-1">
                    <h3 className="font-medium text-white truncate">{ticket.customer.name}</h3>
                    <div className="flex items-center gap-1">
                      {ticket.escalated && <span className="text-red-400">ðŸš¨</span>}
                      <span className={`px-2 py-1 rounded-full text-xs ${getSentimentColor(ticket.sentiment)}`}>
                        {getSentimentIcon(ticket.sentiment)}
                      </span>
                    </div>
                  </div>

                  <p className="text-sm font-medium text-slate-200 mb-1 truncate">{ticket.subject}</p>
                  <p className="text-sm text-slate-400 mb-2 line-clamp-2">{ticket.lastMessage}</p>

                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-2">
                      <span className={`px-2 py-1 text-xs rounded-full ${getPriorityColor(ticket.priority)}`}>
                        {ticket.priority}
                      </span>
                      <span className={`px-2 py-1 text-xs rounded-full ${getStatusColor(ticket.status)}`}>
                        {ticket.status.replace('_', ' ')}
                      </span>
                    </div>
                    <span className="text-xs text-slate-500">{formatTime(ticket.lastMessageTime)}</span>
          </div>
          </div>
          </div>
          </div>
          ))}
        </div>
      </div>

      {/* Main Content Area */}
      <div className="flex-1 flex">
        {/* Ticket Detail Panel */}
        <div className="flex-1 flex flex-col">
          {selectedTicketData ? (
            <>
              {/* Ticket Header */}
              <div className="bg-slate-800/50 backdrop-blur-sm border-b border-slate-700 p-6">
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 bg-slate-600 rounded-full flex items-center justify-center text-white font-medium">
                      {selectedTicketData.customer.name.split(' ').map(n => n[0]).join('')}
                    </div>
                    <div>
                      <h2 className="text-xl font-semibold text-white">{selectedTicketData.customer.name}</h2>
                      <p className="text-slate-300">{selectedTicketData.subject}</p>
                      <div className="flex items-center gap-4 mt-1">
                        <span className="text-slate-400 text-sm">{selectedTicketData.customer.email}</span>
                        <span className="text-slate-400 text-sm capitalize">{selectedTicketData.channel}</span>
                        <span className={`px-2 py-1 rounded-full text-xs ${getSentimentColor(selectedTicketData.sentiment)}`}>
                          {getSentimentIcon(selectedTicketData.sentiment)} Sentiment: {selectedTicketData.sentiment?.sentiment || 'Unknown'}
                        </span>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-3">
                    {/* Status Selector */}
                    <select
                      value={selectedTicketData.status}
                      onChange={(e) => handleStatusChange(e.target.value)}
                      className="bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-white focus:outline-none focus:border-blue-500"
                    >
                      <option value="open">Open</option>
                      <option value="in_progress">In Progress</option>
                      <option value="pending_customer">Pending Customer</option>
                      <option value="resolved">Resolved</option>
                      <option value="closed">Closed</option>
                    </select>

                    {/* Agent Assignment */}
                    <select
                      value={selectedTicketData.assignedTo || ''}
                      onChange={(e) => handleAssignAgent(e.target.value)}
                      className="bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-white focus:outline-none focus:border-blue-500"
                    >
                      <option value="">Assign Agent</option>
                      {AGENTS.map(agent => (
                        <option key={agent} value={agent}>{agent}</option>
                      ))}
                    </select>

                    {/* Escalation */}
                <button
                      onClick={handleEscalateTicket}
                      disabled={selectedTicketData.escalated || escalateTicketMutation.isLoading}
                      className={`px-4 py-2 rounded-lg font-medium transition-colors ${
                        selectedTicketData.escalated 
                          ? 'bg-red-600/20 text-red-400 cursor-not-allowed' 
                          : 'bg-orange-600 hover:bg-orange-700 text-white'
                      }`}
                    >
                      {selectedTicketData.escalated ? 'ðŸš¨ Escalated' : 'ðŸ”¼ Escalate'}
                    </button>

                    {/* Customer Profile Toggle */}
                    <button
                      onClick={() => setShowCustomerProfile(!showCustomerProfile)}
                      className="px-4 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-medium transition-colors"
                    >
                      ðŸ‘¤ Profile
                </button>
                  </div>
        </div>
      </div>

              {/* Messages Area */}
              <div className="flex-1 overflow-y-auto p-6 space-y-4">
                {selectedTicketData.messages.map((message) => (
                  <div
                    key={message.id}
                    className={`flex gap-3 ${message.sender === 'customer' ? '' : 'flex-row-reverse'}`}
                  >
                    <div className="w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0">
                      {message.sender === 'customer' ? (
                        <div className="w-8 h-8 bg-slate-600 rounded-full flex items-center justify-center text-white text-xs">
                          ðŸ‘¤
                        </div>
                      ) : message.sender === 'ai' ? (
                        <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center text-white text-xs">
                          ðŸ¤–
                        </div>
                      ) : (
                        <div className="w-8 h-8 bg-blue-600 rounded-full flex items-center justify-center text-white text-xs">
                          ðŸ‘¨â€ðŸ’¼
                        </div>
                      )}
                    </div>

                    <div className={`max-w-md ${message.sender === 'customer' ? '' : 'text-right'}`}>
                      <div className={`inline-block px-4 py-2 rounded-lg ${
                        message.sender === 'customer'
                          ? 'bg-slate-700 text-white'
                          : message.sender === 'ai'
                          ? 'bg-purple-600 text-white'
                          : 'bg-blue-600 text-white'
                      }`}>
                        <p className="text-sm">{message.content}</p>
                      </div>
                      <div className="mt-1 flex items-center gap-2 text-xs text-slate-400">
                        <span>{formatTime(message.timestamp)}</span>
                        {message.sentiment && (
                          <span className={`px-2 py-1 rounded-full ${getSentimentColor(message.sentiment)}`}>
                            {getSentimentIcon(message.sentiment)}
                          </span>
                        )}
                      </div>
                    </div>
                  </div>
                ))}
              </div>

              {/* AI Suggestion Banner */}
              {agentSuggestion && (
                <div className="bg-purple-900/20 border-t border-purple-600/30 p-4">
                  <div className="flex items-start gap-3">
                    <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center text-white text-sm">
                      ðŸ¤–
            </div>
            <div className="flex-1">
                      <h4 className="text-purple-400 font-medium mb-2">
                        AI Suggested Reply (Confidence: {Math.round(agentSuggestion.confidence * 100)}%)
                      </h4>
                      <div className="bg-slate-800/50 rounded-lg p-3 mb-3">
                        <p className="text-slate-300 text-sm">{agentSuggestion.reply}</p>
                      </div>
                      <div className="flex items-center gap-2">
                        <button
                          onClick={useSuggestedReply}
                          className="px-3 py-1 bg-purple-600 hover:bg-purple-700 text-white rounded text-sm"
                        >
                          Use Reply
                        </button>
                        <button
                          onClick={() => setAgentSuggestion(null)}
                          className="px-3 py-1 border border-slate-600 text-slate-300 hover:bg-slate-700 rounded text-sm"
                        >
                          Dismiss
                        </button>
                        {agentSuggestion.escalationRecommended && (
                          <span className="px-3 py-1 bg-orange-600/20 text-orange-400 rounded text-sm">
                            âš ï¸ Escalation Recommended
                          </span>
                        )}
                      </div>
                    </div>
            </div>
          </div>
        )}

              {/* Message Composer */}
              <div className="bg-slate-800/50 backdrop-blur-sm border-t border-slate-700 p-6">
                <div className="flex items-start gap-3">
                  <div className="flex-1">
                    <textarea
                      value={newMessage}
                      onChange={(e) => setNewMessage(e.target.value)}
                      placeholder="Type your response..."
                      rows={3}
                      className="w-full bg-slate-700 border border-slate-600 rounded-lg px-3 py-2 text-white placeholder:text-slate-400 focus:outline-none focus:border-blue-500 resize-none"
                      onKeyDown={(e) => {
                        if (e.key === 'Enter' && !e.shiftKey) {
                          e.preventDefault();
                          handleSendMessage();
                        }
                      }}
                    />
                    <div className="flex items-center justify-between mt-2">
                      <div className="flex items-center gap-2">
                        <button
                          onClick={handleGenerateAIReply}
                          disabled={generateReplyMutation.isLoading}
                          className="px-3 py-1 bg-purple-600 hover:bg-purple-700 disabled:opacity-50 text-white rounded text-sm transition-colors"
                        >
                          {generateReplyMutation.isLoading ? 'ðŸ¤– Generating...' : 'ðŸ¤– Generate AI Reply'}
                        </button>
                      </div>
                      <span className="text-xs text-slate-500">Press Enter to send, Shift+Enter for new line</span>
                    </div>
                  </div>

                  <button
                    onClick={handleSendMessage}
                    disabled={!newMessage.trim()}
                    className="px-4 py-2 bg-blue-600 hover:bg-blue-700 disabled:opacity-50 disabled:cursor-not-allowed text-white rounded-lg font-medium transition-colors"
                  >
                    Send
                  </button>
                </div>
              </div>
            </>
          ) : (
            <div className="flex-1 flex items-center justify-center bg-slate-800/30">
              <div className="text-center">
                <div className="text-6xl mb-4 opacity-50">ðŸ“ž</div>
                <h3 className="text-xl font-medium text-white mb-2">Select a ticket</h3>
                <p className="text-slate-400">Choose a support ticket to start helping customers</p>
              </div>
            </div>
          )}
        </div>

        {/* Customer Profile Sidebar */}
        {showCustomerProfile && selectedTicketData && (
          <div className="w-80 bg-slate-800/50 backdrop-blur-sm border-l border-slate-700 p-6 overflow-y-auto">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-lg font-semibold text-white">Customer Profile</h3>
              <button
                onClick={() => setShowCustomerProfile(false)}
                className="text-slate-400 hover:text-white"
              >
                âœ•
              </button>
            </div>

            {/* Customer Info */}
            <div className="space-y-6">
              <div>
                <div className="w-16 h-16 bg-slate-600 rounded-full flex items-center justify-center text-white text-xl font-medium mb-4">
                  {selectedTicketData.customer.name.split(' ').map(n => n[0]).join('')}
          </div>
                <h4 className="text-white font-medium">{selectedTicketData.customer.name}</h4>
                <p className="text-slate-400 text-sm">{selectedTicketData.customer.email}</p>
                {selectedTicketData.customer.phone && (
                  <p className="text-slate-400 text-sm">{selectedTicketData.customer.phone}</p>
                )}
              </div>

              {/* Customer Tier */}
              <div>
                <h5 className="text-white font-medium mb-2">Customer Tier</h5>
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                  selectedTicketData.customer.tier === 'enterprise' ? 'bg-purple-600/20 text-purple-400' :
                  selectedTicketData.customer.tier === 'premium' ? 'bg-blue-600/20 text-blue-400' :
                  'bg-gray-600/20 text-gray-400'
                }`}>
                  {selectedTicketData.customer.tier.charAt(0).toUpperCase() + selectedTicketData.customer.tier.slice(1)}
                </span>
              </div>

              {/* Segments */}
              <div>
                <h5 className="text-white font-medium mb-2">Segments</h5>
                <div className="flex flex-wrap gap-2">
                  {selectedTicketData.customer.segments.map((segment, index) => (
                    <span key={index} className="px-2 py-1 bg-green-600/20 text-green-400 rounded text-sm">
                      {segment}
                    </span>
                  ))}
                </div>
              </div>

              {/* Stats */}
              <div className="grid grid-cols-2 gap-4">
                <div className="bg-slate-700/50 rounded-lg p-3 text-center">
                  <div className="text-white font-semibold">{selectedTicketData.customer.totalTickets}</div>
                  <div className="text-slate-400 text-xs">Total Tickets</div>
                </div>
                <div className="bg-slate-700/50 rounded-lg p-3 text-center">
                  <div className="text-white font-semibold">{selectedTicketData.customer.averageRating}</div>
                  <div className="text-slate-400 text-xs">Avg Rating</div>
                </div>
              </div>

              {/* Last Contact */}
              <div>
                <h5 className="text-white font-medium mb-2">Last Contact</h5>
                <p className="text-slate-400 text-sm">{formatTime(selectedTicketData.customer.lastContact)}</p>
              </div>

              {/* Ticket History */}
              <div>
                <h5 className="text-white font-medium mb-2">Recent Tickets</h5>
                <div className="space-y-2">
                  {tickets
                    .filter(t => t.customer.id === selectedTicketData.customer.id)
                    .slice(0, 3)
                    .map(ticket => (
                      <div key={ticket.id} className="bg-slate-700/50 rounded-lg p-3">
                        <p className="text-white text-sm font-medium truncate">{ticket.subject}</p>
                        <div className="flex items-center justify-between mt-1">
                          <span className={`px-2 py-1 text-xs rounded-full ${getStatusColor(ticket.status)}`}>
                            {ticket.status.replace('_', ' ')}
                          </span>
                          <span className="text-slate-400 text-xs">{formatTime(ticket.lastMessageTime)}</span>
                        </div>
                      </div>
                    ))}
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Form,
  FormControl,
  FormDescription,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Slider } from '@/components/ui/slider';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { X, Plus, Lightbulb, Save, Upload } from 'lucide-react';

const profileSchema = z.object({
  name: z.string().min(1, 'Name is required').max(100, 'Name must be less than 100 characters'),
  description: z.string().optional(),
  keywords: z.array(z.string()),
  tone: z.object({
    professional: z.number().min(0).max(100),
    friendly: z.number().min(0).max(100),
    authoritative: z.number().min(0).max(100),
    casual: z.number().min(0).max(100),
    innovative: z.number().min(0).max(100),
  }),
  vocabulary: z.object({
    preferred: z.array(z.string()),
    prohibited: z.array(z.string()),
    brandTerms: z.array(z.string()),
  }),
  style: z.object({
    sentenceLength: z.enum(['short', 'medium', 'long']),
    readingLevel: z.enum(['elementary', 'middle', 'high-school', 'college']),
    formality: z.enum(['casual', 'semi-formal', 'formal']),
  }),
  sampleContent: z.string().optional(),
});

type ProfileFormData = z.infer<typeof profileSchema>;

interface BrandVoiceProfileModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onSuccess: () => void;
  profile?: Partial<ProfileFormData>;
}

export function BrandVoiceProfileModal({
  open,
  onOpenChange,
  onSuccess,
  profile,
}: BrandVoiceProfileModalProps) {
  const [isLoading, setIsLoading] = useState(false);
  const [newKeyword, setNewKeyword] = useState('');
  const [newPreferred, setNewPreferred] = useState('');
  const [newProhibited, setNewProhibited] = useState('');
  const [newBrandTerm, setNewBrandTerm] = useState('');

  const form = useForm<ProfileFormData>({
    resolver: zodResolver(profileSchema),
    defaultValues: {
      name: profile?.name || '',
      description: profile?.description || '',
      keywords: profile?.keywords || [],
      tone: {
        professional: profile?.tone?.professional || 70,
        friendly: profile?.tone?.friendly || 50,
        authoritative: profile?.tone?.authoritative || 60,
        casual: profile?.tone?.casual || 30,
        innovative: profile?.tone?.innovative || 80,
      },
      vocabulary: {
        preferred: profile?.vocabulary?.preferred || ['solution', 'innovative', 'efficient'],
        prohibited: profile?.vocabulary?.prohibited || ['cheap', 'basic'],
        brandTerms: profile?.vocabulary?.brandTerms || ['NeonHub', 'AI-powered'],
      },
      style: {
        sentenceLength: profile?.style?.sentenceLength || 'medium',
        readingLevel: profile?.style?.readingLevel || 'college',
        formality: profile?.style?.formality || 'semi-formal',
      },
      sampleContent: profile?.sampleContent || '',
    },
  });

  const onSubmit = async (data: ProfileFormData) => {
    setIsLoading(true);
    try {
      // In real implementation, call tRPC mutation
      console.log('Creating profile:', data);

      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      onSuccess();
    } catch (error) {
      console.error('Failed to create profile:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const addArrayItem = (fieldName: keyof Pick<ProfileFormData, 'keywords'> | 'preferred' | 'prohibited' | 'brandTerms', value: string, setter: (value: string) => void) => {
    if (!value.trim()) return;

    const currentValue = form.getValues();

    if (fieldName === 'keywords') {
      const current = currentValue.keywords || [];
      if (!current.includes(value.trim())) {
        form.setValue('keywords', [...current, value.trim()]);
      }
    } else {
      const current = currentValue.vocabulary[fieldName as keyof typeof currentValue.vocabulary] as string[];
      if (!current.includes(value.trim())) {
        form.setValue(`vocabulary.${fieldName}`, [...current, value.trim()]);
      }
    }

    setter('');
  };

  const removeArrayItem = (fieldName: keyof Pick<ProfileFormData, 'keywords'> | 'preferred' | 'prohibited' | 'brandTerms', index: number) => {
    const currentValue = form.getValues();

    if (fieldName === 'keywords') {
      const current = currentValue.keywords || [];
      form.setValue('keywords', current.filter((_, i) => i !== index));
    } else {
      const current = currentValue.vocabulary[fieldName as keyof typeof currentValue.vocabulary] as string[];
      form.setValue(`vocabulary.${fieldName}`, current.filter((_, i) => i !== index));
    }
  };

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle>
            {profile ? 'Edit Brand Voice Profile' : 'Create Brand Voice Profile'}
          </DialogTitle>
          <DialogDescription>
            Define your brand voice characteristics to maintain consistency across all content.
          </DialogDescription>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
            <Tabs defaultValue="basic" className="w-full">
              <TabsList className="grid w-full grid-cols-4">
                <TabsTrigger value="basic">Basic Info</TabsTrigger>
                <TabsTrigger value="tone">Tone Profile</TabsTrigger>
                <TabsTrigger value="vocabulary">Vocabulary</TabsTrigger>
                <TabsTrigger value="sample">Sample Content</TabsTrigger>
              </TabsList>

              <TabsContent value="basic" className="space-y-4">
                <FormField
                  control={form.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Profile Name</FormLabel>
                      <FormControl>
                        <Input placeholder="e.g., Corporate Professional" {...field} />
                      </FormControl>
                      <FormDescription>
                        A descriptive name for this brand voice profile
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Describe the characteristics of this brand voice..."
                          className="min-h-[100px]"
                          {...field}
                        />
                      </FormControl>
                      <FormDescription>
                        Optional description to help identify when to use this profile
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="keywords"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Brand Keywords</FormLabel>
                      <div className="space-y-2">
                        <div className="flex gap-2">
                          <Input
                            placeholder="Add a keyword..."
                            value={newKeyword}
                            onChange={(e) => setNewKeyword(e.target.value)}
                            onKeyDown={(e) => {
                              if (e.key === 'Enter') {
                                e.preventDefault();
                                addArrayItem('keywords', newKeyword, setNewKeyword);
                              }
                            }}
                          />
                          <Button
                            type="button"
                            variant="outline"
                            onClick={() => addArrayItem('keywords', newKeyword, setNewKeyword)}
                          >
                            <Plus className="h-4 w-4" />
                          </Button>
                        </div>
                        <div className="flex flex-wrap gap-2">
                          {field.value?.map((keyword, index) => (
                            <Badge key={index} variant="secondary" className="flex items-center gap-1">
                              {keyword}
                              <X
                                className="h-3 w-3 cursor-pointer"
                                onClick={() => removeArrayItem('keywords', index)}
                              />
                            </Badge>
                          ))}
                        </div>
                      </div>
                      <FormDescription>
                        Keywords that should appear in content following this voice
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />
              </TabsContent>

              <TabsContent value="tone" className="space-y-4">
                <Alert>
                  <Lightbulb className="h-4 w-4" />
                  <AlertDescription>
                    Adjust the sliders to define your brand's tone characteristics. Higher values indicate stronger emphasis.
                  </AlertDescription>
                </Alert>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  {Object.entries(form.watch('tone')).map(([toneType, value]) => (
                    <FormField
                      key={toneType}
                      control={form.control}
                      name={`tone.${toneType as keyof ProfileFormData['tone']}`}
                      render={({ field }) => (
                        <FormItem>
                          <div className="flex items-center justify-between">
                            <FormLabel className="capitalize">{toneType}</FormLabel>
                            <span className="text-sm font-medium">{field.value}%</span>
                          </div>
                          <FormControl>
                            <Slider
                              min={0}
                              max={100}
                              step={5}
                              value={[field.value]}
                              onValueChange={(value) => field.onChange(value[0])}
                              className="w-full"
                            />
                          </FormControl>
                          <FormMessage />
                        </FormItem>
                      )}
                    />
                  ))}
                </div>
              </TabsContent>

              <TabsContent value="vocabulary" className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                  {/* Preferred Words */}
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-lg text-green-700">Preferred Words</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      <div className="flex gap-2">
                        <Input
                          placeholder="Add preferred word..."
                          value={newPreferred}
                          onChange={(e) => setNewPreferred(e.target.value)}
                          onKeyDown={(e) => {
                            if (e.key === 'Enter') {
                              e.preventDefault();
                              addArrayItem('preferred', newPreferred, setNewPreferred);
                            }
                          }}
                        />
                        <Button
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={() => addArrayItem('preferred', newPreferred, setNewPreferred)}
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                      <div className="flex flex-wrap gap-1">
                        {form.watch('vocabulary.preferred')?.map((word, index) => (
                          <Badge key={index} variant="default" className="flex items-center gap-1">
                            {word}
                            <X
                              className="h-3 w-3 cursor-pointer"
                              onClick={() => removeArrayItem('preferred', index)}
                            />
                          </Badge>
                        ))}
                      </div>
                    </CardContent>
                  </Card>

                  {/* Prohibited Words */}
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-lg text-red-700">Prohibited Words</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      <div className="flex gap-2">
                        <Input
                          placeholder="Add prohibited word..."
                          value={newProhibited}
                          onChange={(e) => setNewProhibited(e.target.value)}
                          onKeyDown={(e) => {
                            if (e.key === 'Enter') {
                              e.preventDefault();
                              addArrayItem('prohibited', newProhibited, setNewProhibited);
                            }
                          }}
                        />
                        <Button
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={() => addArrayItem('prohibited', newProhibited, setNewProhibited)}
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                      <div className="flex flex-wrap gap-1">
                        {form.watch('vocabulary.prohibited')?.map((word, index) => (
                          <Badge key={index} variant="destructive" className="flex items-center gap-1">
                            {word}
                            <X
                              className="h-3 w-3 cursor-pointer"
                              onClick={() => removeArrayItem('prohibited', index)}
                            />
                          </Badge>
                        ))}
                      </div>
                    </CardContent>
                  </Card>

                  {/* Brand Terms */}
                  <Card>
                    <CardHeader>
                      <CardTitle className="text-lg text-blue-700">Brand Terms</CardTitle>
                    </CardHeader>
                    <CardContent className="space-y-3">
                      <div className="flex gap-2">
                        <Input
                          placeholder="Add brand term..."
                          value={newBrandTerm}
                          onChange={(e) => setNewBrandTerm(e.target.value)}
                          onKeyDown={(e) => {
                            if (e.key === 'Enter') {
                              e.preventDefault();
                              addArrayItem('brandTerms', newBrandTerm, setNewBrandTerm);
                            }
                          }}
                        />
                        <Button
                          type="button"
                          variant="outline"
                          size="sm"
                          onClick={() => addArrayItem('brandTerms', newBrandTerm, setNewBrandTerm)}
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                      <div className="flex flex-wrap gap-1">
                        {form.watch('vocabulary.brandTerms')?.map((term, index) => (
                          <Badge key={index} variant="outline" className="flex items-center gap-1">
                            {term}
                            <X
                              className="h-3 w-3 cursor-pointer"
                              onClick={() => removeArrayItem('brandTerms', index)}
                            />
                          </Badge>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                </div>
              </TabsContent>

              <TabsContent value="sample" className="space-y-4">
                <FormField
                  control={form.control}
                  name="sampleContent"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Sample Content</FormLabel>
                      <FormControl>
                        <Textarea
                          placeholder="Paste some sample content that represents your brand voice..."
                          className="min-h-[200px]"
                          {...field}
                        />
                      </FormControl>
                      <FormDescription>
                        Provide sample content to help train the voice analysis. This will improve accuracy for future content.
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <Alert>
                  <Upload className="h-4 w-4" />
                  <AlertDescription>
                    You can also upload existing content documents to analyze and extract voice patterns automatically.
                  </AlertDescription>
                </Alert>
              </TabsContent>
            </Tabs>

            <div className="flex justify-end space-x-3">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                disabled={isLoading}
              >
                Cancel
              </Button>
              <Button type="submit" disabled={isLoading}>
                {isLoading ? 'Saving...' : (
                  <>
                    <Save className="h-4 w-4 mr-2" />
                    {profile ? 'Update Profile' : 'Create Profile'}
                  </>
                )}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { 
  XMarkIcon,
  ArrowLeftIcon,
  ArrowRightIcon,
  CheckCircleIcon,
  EnvelopeIcon,
  GlobeAltIcon,
  SparklesIcon,
  CalendarIcon,
  UserGroupIcon,
  ChartBarIcon,
  Cog6ToothIcon
} from '@heroicons/react/24/outline';
import { trpc } from '../lib/trpc';

// Form schemas
const campaignBasicsSchema = z.object({
  name: z.string().min(1, 'Campaign name is required').max(100),
  type: z.enum(['email', 'social', 'mixed']),
  description: z.string().optional(),
  budget: z.number().min(1, 'Budget must be at least $1'),
  startDate: z.date(),
  endDate: z.date(),
  targetAudience: z.string().min(1, 'Target audience is required'),
});

const emailConfigSchema = z.object({
  templateType: z.enum(['newsletter', 'promotional', 'welcome', 'follow-up', 'reminder', 'announcement']),
  subject: z.string().min(1, 'Subject is required').max(200),
  fromName: z.string().min(1, 'From name is required'),
  fromEmail: z.string().email('Invalid email'),
  recipients: z.array(z.object({
    email: z.string().email(),
    name: z.string().optional(),
  })).min(1, 'At least one recipient is required'),
  scheduleType: z.enum(['immediate', 'scheduled', 'sequence']),
  scheduledTime: z.date().optional(),
});

const socialConfigSchema = z.object({
  platforms: z.array(z.enum(['FACEBOOK', 'INSTAGRAM', 'TIKTOK', 'TWITTER', 'LINKEDIN'])).min(1, 'Select at least one platform'),
  contentType: z.enum(['post', 'story', 'reel', 'thread']),
  tone: z.enum(['professional', 'casual', 'humorous', 'inspirational', 'promotional']),
  includeHashtags: z.boolean().default(true),
  includeEmojis: z.boolean().default(true),
  scheduleType: z.enum(['immediate', 'scheduled', 'calendar']),
  scheduledTime: z.date().optional(),
});

type CampaignBasicsForm = z.infer<typeof campaignBasicsSchema>;
type EmailConfigForm = z.infer<typeof emailConfigSchema>;
type SocialConfigForm = z.infer<typeof socialConfigSchema>;

interface CampaignCreationModalProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess?: (campaign: any) => void;
}

export default function CampaignCreationModal({ isOpen, onClose, onSuccess }: CampaignCreationModalProps) {
  const [currentStep, setCurrentStep] = useState(1);
  const [campaignType, setCampaignType] = useState<'email' | 'social' | 'mixed'>('email');
  const [generatedContent, setGeneratedContent] = useState<any>(null);
  const [createdCampaign, setCreatedCampaign] = useState<any>(null);

  // Form setup
  const basicsForm = useForm<CampaignBasicsForm>({
    resolver: zodResolver(campaignBasicsSchema),
    defaultValues: {
      name: '',
      type: 'email',
      description: '',
      budget: 1000,
      startDate: new Date(),
      endDate: new Date(Date.now() + 30 * 24 * 60 * 60 * 1000), // 30 days from now
      targetAudience: '',
    },
  });

  const emailForm = useForm<EmailConfigForm>({
    resolver: zodResolver(emailConfigSchema),
    defaultValues: {
      templateType: 'newsletter',
      subject: '',
      fromName: 'NeonHub',
      fromEmail: 'noreply@neonhub.ai',
      recipients: [{ email: '', name: '' }],
      scheduleType: 'immediate',
    },
  });

  const socialForm = useForm<SocialConfigForm>({
    resolver: zodResolver(socialConfigSchema),
    defaultValues: {
      platforms: ['INSTAGRAM'],
      contentType: 'post',
      tone: 'professional',
      includeHashtags: true,
      includeEmojis: true,
      scheduleType: 'immediate',
    },
  });

  // tRPC mutations
  const generateEmailTemplate = trpc.email.generateTemplate.useMutation({
    onSuccess: (data) => {
      setGeneratedContent({ type: 'email', data });
    },
  });

  const generateSocialContent = trpc.social.generateContent.useMutation({
    onSuccess: (data) => {
      setGeneratedContent({ type: 'social', data });
    },
  });

  const createCampaign = trpc.campaign.create.useMutation({
    onSuccess: (data) => {
      setCreatedCampaign(data);
      onSuccess?.(data);
    },
  });

  const steps = [
    { id: 1, name: 'Campaign Basics', icon: ChartBarIcon },
    { id: 2, name: 'Content Generation', icon: SparklesIcon },
    { id: 3, name: 'Configuration', icon: Cog6ToothIcon },
    { id: 4, name: 'Review & Launch', icon: CheckCircleIcon },
  ];

  const platformConfig = {
    FACEBOOK: { name: 'Facebook', color: 'blue' },
    INSTAGRAM: { name: 'Instagram', color: 'pink' },
    TIKTOK: { name: 'TikTok', color: 'black' },
    TWITTER: { name: 'Twitter', color: 'sky' },
    LINKEDIN: { name: 'LinkedIn', color: 'blue' },
  };

  const handleNext = () => {
    if (currentStep < 4) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrevious = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleBasicsSubmit = (data: CampaignBasicsForm) => {
    setCampaignType(data.type);
    handleNext();
  };

  const handleGenerateContent = () => {
    const basicsData = basicsForm.getValues();

    if (campaignType === 'email' || campaignType === 'mixed') {
      const emailData = emailForm.getValues();
      generateEmailTemplate.mutate({
        type: emailData.templateType,
        brand: {
          name: emailData.fromName,
        },
        content: {
          headline: `${basicsData.name} - Special Offer`,
          message: `Exciting news for ${basicsData.targetAudience}! Join our ${basicsData.name} campaign.`,
          ctaText: 'Learn More',
          ctaUrl: 'https://neonhub.ai',
        },
        personalization: true,
        mobileOptimized: true,
      });
    }

    if (campaignType === 'social' || campaignType === 'mixed') {
      const socialData = socialForm.getValues();
      generateSocialContent.mutate({
        platform: socialData.platforms[0],
        contentType: socialData.contentType,
        topic: basicsData.name,
        tone: socialData.tone,
        targetAudience: basicsData.targetAudience,
        includeHashtags: socialData.includeHashtags,
        includeEmojis: socialData.includeEmojis,
      });
    }
  };

  const handleLaunchCampaign = () => {
    const basicsData = basicsForm.getValues();
    const emailData = campaignType !== 'social' ? emailForm.getValues() : null;
    const socialData = campaignType !== 'email' ? socialForm.getValues() : null;

    createCampaign.mutate({
      name: basicsData.name,
      type: basicsData.type,
      description: basicsData.description,
      budget: basicsData.budget,
      startDate: basicsData.startDate,
      endDate: basicsData.endDate,
      targetAudience: basicsData.targetAudience,
      emailConfig: emailData,
      socialConfig: socialData,
      generatedContent,
    });
  };

  const addRecipient = () => {
    const current = emailForm.getValues('recipients');
    emailForm.setValue('recipients', [...current, { email: '', name: '' }]);
  };

  const removeRecipient = (index: number) => {
    const current = emailForm.getValues('recipients');
    if (current.length > 1) {
      emailForm.setValue('recipients', current.filter((_, i) => i !== index));
    }
  };

  const togglePlatform = (platform: string) => {
    const current = socialForm.getValues('platforms');
    const updated = current.includes(platform as any)
      ? current.filter(p => p !== platform)
      : [...current, platform as any];

    if (updated.length > 0) {
      socialForm.setValue('platforms', updated);
    }
  };

  if (!isOpen) return null;

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white rounded-xl shadow-2xl max-w-2xl w-full max-h-[90vh] overflow-hidden">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div>
            <h2 className="text-xl font-semibold text-gray-900">Create New Campaign</h2>
            <p className="text-sm text-gray-600">Set up your AI-powered marketing campaign</p>
          </div>
          <button
            onClick={onClose}
            className="p-2 text-gray-400 hover:text-gray-600 rounded-lg hover:bg-gray-100"
          >
            <XMarkIcon className="h-5 w-5" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6">
          <div className="text-center py-12">
            <SparklesIcon className="h-16 w-16 mx-auto text-gray-300 mb-4" />
            <h3 className="text-xl font-medium text-gray-900 mb-2">Campaign Creation Wizard</h3>
            <p className="text-gray-600 mb-6">Advanced campaign creation with AI agent integration coming soon!</p>

            <div className="space-y-4 text-left max-w-md mx-auto">
              <div className="flex items-center gap-3 p-3 bg-blue-50 rounded-lg">
                <EnvelopeIcon className="h-5 w-5 text-blue-600" />
                <div>
                  <div className="font-medium text-blue-900">Email Campaign Builder</div>
                  <div className="text-sm text-blue-700">Template generation, audience targeting, scheduling</div>
                </div>
              </div>

              <div className="flex items-center gap-3 p-3 bg-pink-50 rounded-lg">
                <GlobeAltIcon className="h-5 w-5 text-pink-600" />
                <div>
                  <div className="font-medium text-pink-900">Social Media Campaigns</div>
                  <div className="text-sm text-pink-700">Multi-platform content, scheduling, analytics</div>
                </div>
              </div>

              <div className="flex items-center gap-3 p-3 bg-green-50 rounded-lg">
                <ChartBarIcon className="h-5 w-5 text-green-600" />
                <div>
                  <div className="font-medium text-green-900">Cross-Agent Integration</div>
                  <div className="text-sm text-green-700">ContentAgent + EmailAgent + SocialAgent coordination</div>
                </div>
              </div>
            </div>

            <button
              onClick={onClose}
              className="mt-6 bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { api } from '../utils/trpc';
import { 
  DocumentTextIcon, 
  SparklesIcon,
  ArrowDownTrayIcon,
  ClipboardDocumentIcon,
  CheckCircleIcon
} from '@heroicons/react/24/outline';

interface Post {
  id: string;
  platform: string;
  content: string;
  hashtags: string[];
  imageSuggestions: string[];
  engagementScore: number;
  estimatedReach: number;
}

export default function ContentAgentTab() {
  const [platform, setPlatform] = useState<'instagram' | 'facebook' | 'tiktok' | 'twitter' | 'linkedin'>('instagram');
  const [topic, setTopic] = useState('custom neon signs');
  const [tone, setTone] = useState<'professional' | 'casual' | 'funny' | 'inspiring' | 'urgent'>('professional');
  const [generatedPosts, setGeneratedPosts] = useState<Post[]>([]);
  const [copiedId, setCopiedId] = useState<string | null>(null);

  const generatePosts = api.content.generatePosts.useMutation({
    onSuccess: (data) => {
      setGeneratedPosts(data.posts);
    },
  });

  const agentStatus = api.content.getAgentStatus.useQuery();

  const handleGenerate = () => {
    generatePosts.mutate({
      platform,
      topic,
      tone,
      includeHashtags: true,
    });
  };

  const copyToClipboard = (text: string, id: string) => {
    navigator.clipboard.writeText(text);
    setCopiedId(id);
    setTimeout(() => setCopiedId(null), 2000);
  };

  return (
    <div className="space-y-6">
      {/* Agent Status Header */}
      <div className="card-glow">
        <div className="flex items-center justify-between mb-4">
          <div className="flex items-center space-x-3">
            <DocumentTextIcon className="h-8 w-8 text-blue-400" />
            <div>
              <h2 className="text-xl font-semibold text-white">Content Agent</h2>
              <p className="text-dark-400 text-sm">AI-powered content generation</p>
            </div>
          </div>
          <div className="flex items-center space-x-2">
            <div className="status-indicator active"></div>
            <span className="text-green-400 text-sm">
              {agentStatus.data?.status || 'Active'}
            </span>
          </div>
        </div>

        {agentStatus.data && (
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="text-center">
              <p className="text-dark-400 text-xs">Uptime</p>
              <p className="text-white font-semibold">{agentStatus.data.uptime}</p>
            </div>
            <div className="text-center">
              <p className="text-dark-400 text-xs">Success Rate</p>
              <p className="text-white font-semibold">{agentStatus.data.successRate}</p>
            </div>
            <div className="text-center">
              <p className="text-dark-400 text-xs">Total Executions</p>
              <p className="text-white font-semibold">{agentStatus.data.totalExecutions}</p>
            </div>
            <div className="text-center">
              <p className="text-dark-400 text-xs">Avg Response</p>
              <p className="text-white font-semibold">{agentStatus.data.avgResponseTime}</p>
            </div>
          </div>
        )}
      </div>

      {/* Content Generation Form */}
      <div className="card-glow">
        <h3 className="text-lg font-semibold text-white mb-4">Generate Social Media Posts</h3>

        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          {/* Platform Selection */}
          <div>
            <label className="block text-dark-300 text-sm font-medium mb-2">Platform</label>
            <select
              value={platform}
              onChange={(e) => setPlatform(e.target.value as any)}
              className="input w-full"
            >
              <option value="instagram">Instagram</option>
              <option value="facebook">Facebook</option>
              <option value="tiktok">TikTok</option>
              <option value="twitter">Twitter</option>
              <option value="linkedin">LinkedIn</option>
            </select>
          </div>

          {/* Topic Input */}
          <div>
            <label className="block text-dark-300 text-sm font-medium mb-2">Topic</label>
            <input
              type="text"
              value={topic}
              onChange={(e) => setTopic(e.target.value)}
              placeholder="e.g., custom neon signs"
              className="input w-full"
            />
          </div>

          {/* Tone Selection */}
          <div>
            <label className="block text-dark-300 text-sm font-medium mb-2">Tone</label>
            <select
              value={tone}
              onChange={(e) => setTone(e.target.value as any)}
              className="input w-full"
            >
              <option value="professional">Professional</option>
              <option value="casual">Casual</option>
              <option value="funny">Funny</option>
              <option value="inspiring">Inspiring</option>
              <option value="urgent">Urgent</option>
            </select>
          </div>
        </div>

        <button
          onClick={handleGenerate}
          disabled={generatePosts.isLoading}
          className="btn-primary flex items-center space-x-2"
        >
          {generatePosts.isLoading ? (
            <>
              <SparklesIcon className="h-5 w-5 animate-spin" />
              <span>Generating...</span>
            </>
          ) : (
            <>
              <SparklesIcon className="h-5 w-5" />
              <span>Generate Content</span>
            </>
          )}
        </button>
      </div>

      {/* Generated Posts */}
      {generatedPosts.length > 0 && (
        <div className="card-glow">
          <div className="flex items-center justify-between mb-4">
            <h3 className="text-lg font-semibold text-white">Generated Posts</h3>
            <div className="text-dark-400 text-sm">
              {generatedPosts.length} posts generated
            </div>
          </div>

          <div className="space-y-4">
            {generatedPosts.map((post) => (
              <div key={post.id} className="agent-card">
                <div className="flex items-start justify-between mb-3">
                  <div className="flex items-center space-x-2">
                    <span className="px-2 py-1 bg-neon-400/20 text-neon-400 text-xs rounded-full capitalize">
                      {post.platform}
                    </span>
                    <span className="text-dark-400 text-xs">
                      Score: {post.engagementScore}/100
                    </span>
                    <span className="text-dark-400 text-xs">
                      Est. Reach: {post.estimatedReach.toLocaleString()}
                    </span>
                  </div>
                  <button
                    onClick={() => copyToClipboard(`${post.content  }\n\n${  post.hashtags.join(' ')}`, post.id)}
                    className="btn-pill flex items-center space-x-1"
                  >
                    {copiedId === post.id ? (
                      <CheckCircleIcon className="h-4 w-4 text-green-400" />
                    ) : (
                      <ClipboardDocumentIcon className="h-4 w-4" />
                    )}
                    <span className="text-xs">{copiedId === post.id ? 'Copied!' : 'Copy'}</span>
                  </button>
                </div>

                <div className="mb-3">
                  <p className="text-white leading-relaxed">{post.content}</p>
                </div>

                {post.hashtags.length > 0 && (
                  <div className="mb-3">
                    <p className="text-neon-400 text-sm">
                      {post.hashtags.join(' ')}
                    </p>
                  </div>
                )}

                {post.imageSuggestions.length > 0 && (
                  <div>
                    <p className="text-dark-400 text-xs mb-1">Image suggestions:</p>
                    <div className="flex flex-wrap gap-1">
                      {post.imageSuggestions.map((suggestion, index) => (
                        <span key={index} className="px-2 py-1 bg-dark-700 text-dark-300 text-xs rounded">
                          {suggestion}
                        </span>
                      ))}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Error Display */}
      {generatePosts.error && (
        <div className="card-glow border border-red-500/50">
          <div className="flex items-center space-x-2 text-red-400">
            <span className="text-sm">Error: {generatePosts.error.message}</span>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useState } from 'react';

// Simple Content Voice Analyzer component
interface ContentVoiceAnalyzerProps {
  profiles: Array<{
    id: string;
    name: string;
    isActive: boolean;
  }>;
}

export function ContentVoiceAnalyzer({ profiles }: ContentVoiceAnalyzerProps) {
  const [content, setContent] = useState('');
  const [selectedProfile, setSelectedProfile] = useState('');
  const [analysis, setAnalysis] = useState<any>(null);
  const [isAnalyzing, setIsAnalyzing] = useState(false);

  const analyzeContent = async () => {
    if (!content.trim()) return;

    setIsAnalyzing(true);
    try {
      // Simulate API call
      await new Promise(resolve => setTimeout(resolve, 1000));

      // Mock analysis result
      setAnalysis({
        voiceScore: Math.floor(Math.random() * 30) + 70, // 70-100
        suggestions: [
          {
            type: 'tone',
            issue: 'Content could be more professional',
            suggestion: 'Use more business-oriented language',
            priority: 'medium'
          }
        ]
      });
    } catch (error) {
      console.error('Analysis failed:', error);
    } finally {
      setIsAnalyzing(false);
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h3 className="text-lg font-semibold mb-4">Content Voice Analyzer</h3>
        <p className="text-gray-600 mb-6">
          Analyze your content against brand voice profiles in real-time
        </p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Input Section */}
        <div className="space-y-4">
          <div>
            <label className="block text-sm font-medium mb-2">Select Brand Voice Profile</label>
            <select
              value={selectedProfile}
              onChange={(e) => setSelectedProfile(e.target.value)}
              className="w-full p-2 border rounded-md"
            >
              <option value="">Choose a profile...</option>
              {profiles.map((profile) => (
                <option key={profile.id} value={profile.id}>
                  {profile.name} {profile.isActive ? '(Active)' : ''}
                </option>
              ))}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium mb-2">Content to Analyze</label>
            <textarea
              value={content}
              onChange={(e) => setContent(e.target.value)}
              placeholder="Paste your content here for voice analysis..."
              className="w-full h-64 p-3 border rounded-md"
            />
          </div>

          <button
            onClick={analyzeContent}
            disabled={!content.trim() || !selectedProfile || isAnalyzing}
            className="w-full bg-blue-600 text-white py-2 px-4 rounded-md disabled:opacity-50"
          >
            {isAnalyzing ? 'Analyzing...' : 'Analyze Brand Voice'}
          </button>
        </div>

        {/* Results Section */}
        <div className="space-y-4">
          <h4 className="text-md font-semibold">Analysis Results</h4>

          {analysis ? (
            <div className="space-y-4">
              <div className="p-4 border rounded-lg">
                <div className="flex justify-between items-center mb-2">
                  <span className="font-medium">Voice Consistency Score</span>
                  <span className={`text-lg font-bold ${
                    analysis.voiceScore >= 80 ? 'text-green-600' : 
                    analysis.voiceScore >= 60 ? 'text-yellow-600' : 'text-red-600'
                  }`}>
                    {analysis.voiceScore}%
                  </span>
                </div>
                <div className="w-full bg-gray-200 rounded-full h-2">
                  <div 
                    className={`h-2 rounded-full ${
                      analysis.voiceScore >= 80 ? 'bg-green-600' : 
                      analysis.voiceScore >= 60 ? 'bg-yellow-600' : 'bg-red-600'
                    }`}
                    style={{ width: `${analysis.voiceScore}%` }}
                  ></div>
                </div>
              </div>

              {analysis.suggestions && analysis.suggestions.length > 0 && (
                <div className="p-4 border rounded-lg">
                  <h5 className="font-medium mb-3">Suggestions for Improvement</h5>
                  <div className="space-y-2">
                    {analysis.suggestions.map((suggestion: any, index: number) => (
                      <div key={index} className="p-3 bg-amber-50 rounded-md">
                        <div className="flex justify-between items-start mb-1">
                          <span className="font-medium text-sm capitalize">{suggestion.type}</span>
                          <span className={`text-xs px-2 py-1 rounded ${
                            suggestion.priority === 'high' ? 'bg-red-100 text-red-700' :
                            suggestion.priority === 'medium' ? 'bg-yellow-100 text-yellow-700' :
                            'bg-green-100 text-green-700'
                          }`}>
                            {suggestion.priority}
                          </span>
                        </div>
                        <p className="text-sm text-gray-700 mb-1">{suggestion.issue}</p>
                        <p className="text-sm text-blue-700 font-medium">{suggestion.suggestion}</p>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </div>
          ) : (
            <div className="text-center py-12 text-gray-500">
              <p>Select a profile and enter content to see analysis results</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { useForm, Controller } from 'react-hook-form';
import { zodResolver } from '@hookform/resolvers/zod';
import { z } from 'zod';
import { 
  EnvelopeIcon,
  PaperAirplaneIcon,
  CalendarIcon,
  ChartBarIcon,
  UserGroupIcon,
  ClockIcon,
  CheckCircleIcon,
  XCircleIcon
} from '@heroicons/react/24/outline';
import { trpc } from '../lib/trpc';

// Form schemas
const emailTemplateSchema = z.object({
  type: z.enum(['newsletter', 'promotional', 'welcome', 'follow-up', 'reminder', 'announcement']),
  subject: z.string().min(1, 'Subject is required').max(200),
  content: z.object({
    headline: z.string().min(1, 'Headline is required').max(200),
    message: z.string().min(1, 'Message is required'),
    ctaText: z.string().min(1, 'CTA text is required').max(50),
    ctaUrl: z.string().url('Invalid URL'),
    footerText: z.string().optional(),
  }),
  brand: z.object({
    name: z.string().min(1, 'Brand name is required').max(100),
    primaryColor: z.string().optional(),
    secondaryColor: z.string().optional(),
  }),
  personalization: z.boolean().default(true),
  mobileOptimized: z.boolean().default(true),
});

const campaignSchema = z.object({
  recipients: z.array(z.object({
    email: z.string().email(),
    name: z.string().optional(),
  })).min(1, 'At least one recipient is required'),
  scheduleAt: z.date().optional(),
  testMode: z.boolean().default(false),
});

type EmailTemplateForm = z.infer<typeof emailTemplateSchema>;
type CampaignForm = z.infer<typeof campaignSchema>;

export default function EmailAgentTab() {
  const [activeSection, setActiveSection] = useState<'template' | 'campaign' | 'analytics'>('template');
  const [generatedTemplate, setGeneratedTemplate] = useState<any>(null);
  const [campaignResult, setCampaignResult] = useState<any>(null);

  // Form setup
  const templateForm = useForm<EmailTemplateForm>({
    resolver: zodResolver(emailTemplateSchema),
    defaultValues: {
      type: 'newsletter',
      subject: '',
      content: {
        headline: '',
        message: '',
        ctaText: 'Learn More',
        ctaUrl: '',
        footerText: '',
      },
      brand: {
        name: '',
        primaryColor: '#3B82F6',
        secondaryColor: '#1E40AF',
      },
      personalization: true,
      mobileOptimized: true,
    },
  });

  const campaignForm = useForm<CampaignForm>({
    resolver: zodResolver(campaignSchema),
    defaultValues: {
      recipients: [{ email: '', name: '' }],
      testMode: true,
    },
  });

  // tRPC mutations
  const generateTemplate = trpc.email.generateTemplate.useMutation({
    onSuccess: (data) => {
      setGeneratedTemplate(data);
    },
    onError: (error) => {
      console.error('Failed to generate template:', error);
    },
  });

  const sendCampaign = trpc.email.sendCampaign.useMutation({
    onSuccess: (data) => {
      setCampaignResult(data);
    },
    onError: (error) => {
      console.error('Failed to send campaign:', error);
    },
  });

  // tRPC queries
  const { data: performance, isLoading: performanceLoading } = trpc.email.trackPerformance.useQuery(
    { timeRange: '30d' },
    { enabled: activeSection === 'analytics' }
  );

  // Form handlers
  const onGenerateTemplate = (data: EmailTemplateForm) => {
    generateTemplate.mutate(data);
  };

  const onSendCampaign = (data: CampaignForm) => {
    if (!generatedTemplate) return;

    sendCampaign.mutate({
      campaignId: 'temp-campaign-id', // In real app, get from context
      emailTemplate: {
        subject: templateForm.getValues('subject'),
        htmlContent: generatedTemplate.htmlTemplate,
        textContent: generatedTemplate.textTemplate,
        fromName: templateForm.getValues('brand.name'),
        fromEmail: 'noreply@neonhub.ai',
      },
      recipients: data.recipients,
      scheduleAt: data.scheduleAt,
      testMode: data.testMode,
    });
  };

  const addRecipient = () => {
    const current = campaignForm.getValues('recipients');
    campaignForm.setValue('recipients', [...current, { email: '', name: '' }]);
  };

  const removeRecipient = (index: number) => {
    const current = campaignForm.getValues('recipients');
    if (current.length > 1) {
      campaignForm.setValue('recipients', current.filter((_, i) => i !== index));
    }
  };

  return (
    <div className="max-w-6xl mx-auto">
      {/* Header */}
      <div className="flex items-center justify-between mb-6">
        <div className="flex items-center gap-3">
          <div className="w-10 h-10 bg-blue-100 rounded-lg flex items-center justify-center">
            <EnvelopeIcon className="h-5 w-5 text-blue-600" />
          </div>
          <div>
            <h1 className="text-xl font-semibold text-gray-900">Email Marketing Agent</h1>
            <p className="text-sm text-gray-600">Create campaigns, manage templates, and track performance</p>
          </div>
        </div>

        <div className="flex items-center gap-2">
          <div className="h-2 w-2 bg-green-500 rounded-full"></div>
          <span className="text-sm text-gray-600">Agent Online</span>
        </div>
      </div>

      {/* Section Navigation */}
      <div className="flex space-x-1 mb-6 bg-gray-100 p-1 rounded-lg">
        {[
          { id: 'template', name: 'Template Builder', icon: EnvelopeIcon },
          { id: 'campaign', name: 'Send Campaign', icon: PaperAirplaneIcon },
          { id: 'analytics', name: 'Analytics', icon: ChartBarIcon },
        ].map((section) => {
          const Icon = section.icon;
          return (
            <button
              key={section.id}
              onClick={() => setActiveSection(section.id as any)}
              className={`flex items-center gap-2 px-4 py-2 rounded-md text-sm font-medium transition-colors ${
                activeSection === section.id
                  ? 'bg-white text-blue-700 shadow-sm'
                  : 'text-gray-600 hover:text-gray-900'
              }`}
            >
              <Icon className="h-4 w-4" />
              {section.name}
            </button>
