  private calculateOverallScore(systemData: any): number {
    const weights = {
      roas: 0.3,
      brandAlignment: 0.25,
      agentPerformance: 0.25,
      efficiency: 0.2
    };

    const avgROAS = systemData.campaigns.reduce((sum: number, c: any) => sum + c.roas, 0) / systemData.campaigns.length;
    const avgBrandAlignment = systemData.campaigns.reduce((sum: number, c: any) => sum + c.brandAlignmentScore, 0) / systemData.campaigns.length;
    const avgAgentPerformance = systemData.agentPerformance.reduce((sum: number, a: any) => sum + a.impactScore, 0) / systemData.agentPerformance.length;
    const avgEfficiency = systemData.agentPerformance.reduce((sum: number, a: any) => sum + a.successRate, 0) / systemData.agentPerformance.length;

    const score = (
      (Math.min(avgROAS / 4.0, 1.0) * weights.roas) +
      (avgBrandAlignment * weights.brandAlignment) +
      (avgAgentPerformance * weights.agentPerformance) +
      (avgEfficiency * weights.efficiency)
    ) * 100;

    return Math.round(score);
  }

