  private generateNotionData(report: BoardroomReport): any {
    return {
      title: report.title,
      properties: {
        "Report Type": { select: { name: report.reportType } },
        "Overall Score": { number: report.overallScore },
        "ROAS": { number: report.overallROAS },
        "Brand Health": { number: report.brandHealthScore },
        "Generated": { date: { start: report.createdAt } }
      },
      children: [
        {
          type: "heading_1",
          heading_1: { rich_text: [{ text: { content: "Executive Summary" } }] }
        },
        {
          type: "bulleted_list_item",
          bulleted_list_item: {
            rich_text: report.keyTakeaways.map(takeaway => ({
              text: { content: takeaway }
            }))
          }
        },
        {
          type: "heading_2",
          heading_2: { rich_text: [{ text: { content: "Strategic Recommendations" } }] }
        },
        ...report.strategicRecommendations.map(rec => ({
          type: "numbered_list_item",
          numbered_list_item: {
            rich_text: [{ text: { content: rec } }]
          }
        }))
      ]
    };
  }

  private logProgress(message: string, data?: any): void {
    console.log(`[BoardroomReportAgent] ${message}`, data || '');
  }

  private logError(message: string, error: any): void {
    console.error(`[BoardroomReportAgent] ${message}`, error);
  }
}

export default BoardroomReportAgent;

import { BaseAgent } from '../utils/BaseAgent';
import BoardroomReportAgent, { BoardroomReportConfig, BoardroomReport } from './boardroom-report-agent';
import ForecastInsightEngine from '../strategy/forecast-insight-engine';
import PresentationBuilder, { PresentationConfig, PresentationTheme, OutputFormat } from '../strategy/PresentationBuilder';

export interface ScheduleConfig {
  enabled: boolean;
  schedules: ScheduleDefinition[];
  deliverySettings: DeliverySettings;
  thresholds: TriggerThresholds;
  notifications: NotificationSettings;
  retryPolicy: RetryPolicy;
}

export interface ScheduleDefinition {
  id: string;
  name: string;
  type: ScheduleType;
  frequency: Frequency;
  time: { hour: number; minute: number; timezone: string };
  reportConfig: BoardroomReportConfig;
  presentationConfig: PresentationConfig;
  enabled: boolean;
  lastRun?: string;
  nextRun?: string;
}

export interface DeliverySettings {
  channels: DeliveryChannel[];
  recipients: Recipient[];
  formats: OutputFormat[];
  storage: StorageConfig;
}

export interface TriggerThresholds {
  campaignCompletion: {
    enabled: boolean;
    minimumBudget: number;
    minimumDuration: number; // days
    minimumROAS: number;
  };
  performanceAlert: {
    enabled: boolean;
    roasDropThreshold: number; // percentage
    brandAlignmentDropThreshold: number;
    agentFailureThreshold: number; // percentage
  };
  businessMilestone: {
    enabled: boolean;
    revenueThreshold: number;
    conversionThreshold: number;
  };
}

export interface NotificationSettings {
  channels: NotificationChannel[];
  escalation: EscalationPolicy;
  templates: NotificationTemplates;
  quietHours: { start: string; end: string; timezone: string };
}

export interface RetryPolicy {
  maxAttempts: number;
  backoffMultiplier: number;
  initialDelay: number; // milliseconds
  maxDelay: number; // milliseconds
}

export interface Recipient {
  id: string;
  name: string;
  email: string;
  role: RecipientRole;
  channels: string[]; // Which delivery channels for this recipient
  timezone: string;
  preferences: RecipientPreferences;
}

export interface RecipientPreferences {
  formats: OutputFormat[];
  frequency: Frequency;
  reportTypes: ScheduleType[];
  notificationMethods: string[];
}

export interface StorageConfig {
  retention: { days: number };
  archiving: { enabled: boolean; location: string };
  backup: { enabled: boolean; frequency: string };
}

export interface NotificationChannel {
  type: 'email' | 'slack' | 'teams' | 'webhook' | 'sms';
  config: any;
  enabled: boolean;
}

export interface EscalationPolicy {
  enabled: boolean;
  levels: EscalationLevel[];
  timeout: number; // minutes before escalation
}

export interface EscalationLevel {
  level: number;
  recipients: string[];
  channels: string[];
  delay: number; // minutes
}

export interface NotificationTemplates {
  reportGenerated: string;
  reportFailed: string;
  scheduleReminder: string;
  performanceAlert: string;
}

export interface ScheduledTask {
  id: string;
  scheduleId: string;
  type: TaskType;
  status: TaskStatus;
  scheduledAt: string;
  startedAt?: string;
  completedAt?: string;
  error?: string;
  retryCount: number;
  result?: TaskResult;
  metadata?: any;
}

export interface TaskResult {
  reportId?: string;
  presentationId?: string;
  deliveries: DeliveryResult[];
  notifications: NotificationResult[];
  metrics: TaskMetrics;
}

export interface DeliveryResult {
  channel: string;
  recipient: string;
  status: 'success' | 'failed' | 'pending';
  deliveredAt?: string;
  error?: string;
  trackingId?: string;
}

export interface NotificationResult {
  channel: string;
  recipient: string;
  status: 'sent' | 'failed' | 'pending';
  sentAt?: string;
  error?: string;
}

export interface TaskMetrics {
  executionTime: number; // milliseconds
  dataPoints: number;
  slidesGenerated: number;
  forecastsGenerated: number;
  confidenceScore: number;
}

export enum ScheduleType {
  MONTHLY_QBR = 'monthly_qbr',
  QUARTERLY_REVIEW = 'quarterly_review',
  WEEKLY_DIGEST = 'weekly_digest',
  CAMPAIGN_POSTMORTEM = 'campaign_postmortem',
  PERFORMANCE_ALERT = 'performance_alert',
  CUSTOM = 'custom'
}

export enum Frequency {
  DAILY = 'daily',
  WEEKLY = 'weekly',
  MONTHLY = 'monthly',
  QUARTERLY = 'quarterly',
  ON_DEMAND = 'on_demand',
  EVENT_TRIGGERED = 'event_triggered'
}

export enum DeliveryChannel {
  EMAIL = 'email',
  SLACK = 'slack',
  NOTION = 'notion',
  TEAMS = 'teams',
  WEBHOOK = 'webhook',
  FILE_STORAGE = 'file_storage'
}

export enum RecipientRole {
  CMO = 'cmo',
  MARKETING_MANAGER = 'marketing_manager',
  ANALYST = 'analyst',
  EXECUTIVE = 'executive',
  STAKEHOLDER = 'stakeholder'
}

export enum TaskType {
  SCHEDULED_REPORT = 'scheduled_report',
  TRIGGERED_REPORT = 'triggered_report',
  DELIVERY = 'delivery',
  NOTIFICATION = 'notification',
  CLEANUP = 'cleanup'
}

export enum TaskStatus {
  PENDING = 'pending',
  RUNNING = 'running',
  COMPLETED = 'completed',
  FAILED = 'failed',
  CANCELLED = 'cancelled',
  RETRYING = 'retrying'
}

export class BoardroomReportSchedulerAgent extends BaseAgent {
  private boardroomReportAgent: BoardroomReportAgent;
  private forecastEngine: ForecastInsightEngine;
  private presentationBuilder: PresentationBuilder;
  private scheduleConfig: ScheduleConfig;
  private activeTasks: Map<string, ScheduledTask>;
  private scheduleTimer?: NodeJS.Timeout;

  private readonly CHECK_INTERVAL = 60000; // 1 minute
  private readonly MAX_CONCURRENT_TASKS = 3;

  constructor(config?: Partial<ScheduleConfig>) {
    super('BoardroomReportSchedulerAgent', 'SCHEDULER');

    this.boardroomReportAgent = new BoardroomReportAgent();
    this.forecastEngine = new ForecastInsightEngine();
    this.presentationBuilder = new PresentationBuilder();
    this.activeTasks = new Map();

    this.scheduleConfig = {
      enabled: true,
      schedules: [],
      deliverySettings: {
