      memoryUsage: 75, // percentage
      activeTests: 35
    };
  }

  // Mock data methods
  private async getAllTests(): Promise<ABTest[]> {
    // Mock implementation - get all tests
    return [];
  }

  private async getAllCampaigns(): Promise<any[]> {
    // Mock implementation - get all campaigns
