  });
});

import OpenAI from 'openai';
import { AbstractAgent } from '../base-agent';
import type { AgentPayload, AgentResult } from '../base-agent';
import type { AgentContext, ContentResult } from '../types';
import { logger } from '@neon/utils';

// Define local interfaces for content generation
export interface ContentGenerationParams {
  topic: string;
  type: string;
  tone?: string;
  keywords?: string[];
  targetAudience?: string;
}

export interface ContentOptimizationParams {
  content: string;
  targetKeywords: string[];
  platform?: string;
}

export interface ContentAnalysisParams {
  content: string;
  metrics?: string[];
}

export interface ContentGenerationContext {
  type: 'blog' | 'social_post' | 'email' | 'caption' | 'copy';
  tone: 'professional' | 'casual' | 'friendly' | 'authoritative' | 'playful';
  audience: string;
  topic: string;
  keywords?: string[];
  length?: 'short' | 'medium' | 'long';
  platform?: 'facebook' | 'instagram' | 'twitter' | 'linkedin' | 'email';
}

export interface ContentGenerationResult extends AgentResult {
  content: string;
  suggestedTitle?: string;
        hashtags?: string[] | undefined;
  readingTime?: number;
  seoScore?: number | undefined;
}

export class ContentAgent extends AbstractAgent {
  private openai: OpenAI;

  constructor() {
    super('content-agent', 'ContentAgent', 'content', [
      'generate_content',
      'generate_blog',
      'generate_caption',
      'generate_post'
    ]);

    this.openai = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY,
    });

    if (!process.env.OPENAI_API_KEY) {
      logger.warn('OPENAI_API_KEY not found. ContentAgent will run in limited mode.', {}, 'ContentAgent');
    }
  }

  async execute(payload: AgentPayload): Promise<AgentResult> {
    return this.executeWithErrorHandling(payload, async () => {
      const context = payload.context as ContentGenerationContext;

      // Validate input
      if (!context.topic || !context.type || !context.audience) {
        throw new Error('Missing required context: topic, type, and audience are required');
      }

      // Generate content based on type
      const result = await this.generateContent(context);

      return result;
    });
  }

  private async generateContent(context: ContentGenerationContext): Promise<ContentGenerationResult> {
    // Try OpenAI first, fallback to template-based if unavailable
    let content: string;

    if (this.openai && process.env.OPENAI_API_KEY) {
      content = await this.generateAIContent(context);
    } else {
      content = await this.createContentTemplate(context);
    }

    const hashtags = context.type === 'social_post' ? this.generateHashtags(context) : undefined;
    const readingTime = this.calculateReadingTime(content);
    const seoScore = context.keywords ? this.calculateSEOScore(content, context.keywords) : undefined;

    return {
      content,
      suggestedTitle: this.generateTitle(context),
      hashtags,
      readingTime,
      seoScore,
      success: true
    };
  }

  private async generateAIContent(context: ContentGenerationContext): Promise<string> {
    try {
      const prompt = this.buildContentPrompt(context);

      const response = await this.openai.chat.completions.create({
        model: "gpt-4",
        messages: [
          {
            role: "system",
            content: "You are an expert content creator. Generate engaging, high-quality content that resonates with the target audience and achieves the specified goals."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        temperature: 0.7,
        max_tokens: this.getMaxTokensForType(context.type),
      });

      const aiContent = response.choices[0]?.message?.content;
      if (!aiContent) {
        throw new Error('No response from OpenAI');
      }

      return aiContent;
    } catch (error) {
      logger.error('OpenAI content generation failed, using template fallback', { error }, 'ContentAgent');
      return await this.createContentTemplate(context);
    }
  }

  private buildContentPrompt(context: ContentGenerationContext): string {
    const { type, topic, audience, tone, keywords = [], platform, length } = context;

    let prompt = `Generate ${length || 'appropriate'} ${type} content about "${topic}" for ${audience} with a ${tone} tone.`;

    if (keywords.length > 0) {
      prompt += ` Include these keywords naturally: ${keywords.join(', ')}.`;
    }

    if (platform) {
      prompt += ` Optimize for ${platform} platform.`;
    }

    switch (type) {
      case 'blog':
        prompt += ' Include an engaging introduction, structured main content with subheadings, and a compelling conclusion. Make it SEO-friendly and informative.';
        break;
      case 'social_post':
        prompt += ' Make it engaging, shareable, and include appropriate emojis. End with a call-to-action or question to encourage engagement.';
        break;
      case 'email':
        prompt += ' Create a compelling subject line and email body that drives action. Be personable and include a clear call-to-action.';
        break;
      case 'caption':
        prompt += ' Keep it concise, engaging, and include relevant hashtags. Perfect for accompanying visual content.';
        break;
      case 'copy':
        prompt += ' Focus on persuasive, conversion-oriented copy that clearly communicates value and drives action.';
        break;
    }

    return prompt;
  }

  private getMaxTokensForType(type: string): number {
    const tokenLimits = {
      blog: 2000,
      social_post: 300,
      email: 800,
      caption: 150,
      copy: 500
    };

    return tokenLimits[type as keyof typeof tokenLimits] || 500;
  }

  private async createContentTemplate(context: ContentGenerationContext): Promise<string> {
    const templates = {
      blog: this.generateBlogContent(context),
      social_post: this.generateSocialPost(context),
      email: this.generateEmailContent(context),
      caption: this.generateCaptionContent(context),
      copy: this.generateCopyContent(context)
    };

    return templates[context.type] || templates.copy;
  }

  private generateBlogContent(context: ContentGenerationContext): string {
    const { topic, audience, tone, keywords = [] } = context;

    return `# ${this.generateTitle(context)}

## Introduction

When it comes to ${topic}, ${audience} face unique challenges that require a ${tone} approach. ${keywords.length > 0 ? `Understanding ${keywords.join(', ')} is crucial for success.` : ''}

## Key Points

- **Strategic Insight**: ${topic} requires careful consideration of your target audience
- **Best Practices**: Implementing proven strategies that work for ${audience}
- **Action Items**: Clear steps you can take immediately

## Conclusion

By focusing on ${topic} with a ${tone} approach, you'll be able to connect more effectively with ${audience} and achieve your goals.

*This content was generated by NeonHub AI Content Agent*`;
  }

  private generateSocialPost(context: ContentGenerationContext): string {
    const { topic, audience, tone, platform } = context;

    const platformSpecific = {
      twitter: ` ${topic} insight for ${audience}:\n\n${this.getToneMessage(tone, topic)}\n\nWhat's your experience? `,
      instagram: ` ${topic} \n\n${this.getToneMessage(tone, topic)}\n\nPerfect for ${audience} looking to level up! `,
      linkedin: `Professional insight on ${topic} for ${audience}:\n\n${this.getToneMessage(tone, topic)}\n\nThoughts? Let's discuss in the comments.`,
      facebook: `Hey ${audience}! \n\nLet's talk about ${topic}:\n\n${this.getToneMessage(tone, topic)}\n\nWho else finds this helpful?`
    };

    return platformSpecific[platform as keyof typeof platformSpecific] || platformSpecific.instagram;
  }

  private generateEmailContent(context: ContentGenerationContext): string {
    const { topic, audience, tone } = context;

    return `Subject: ${this.generateTitle(context)}

Hi there!

I hope this email finds you well. I wanted to share some insights about ${topic} that I think you'll find valuable.

${this.getToneMessage(tone, topic)}

This is particularly relevant for ${audience} because it addresses the core challenges you face daily.

Here's what you can do next:
1. Apply these insights to your current projects
2. Share this with your team
3. Let me know how it works for you

Best regards,
The NeonHub Team

P.S. This email was personalized by our AI Content Agent to match your interests.`;
  }

  private generateCaptionContent(context: ContentGenerationContext): string {
    const { topic, audience, tone } = context;

    return `${this.getToneMessage(tone, topic)} Perfect for ${audience} ready to take action! `;
  }

  private generateCopyContent(context: ContentGenerationContext): string {
    const { topic, audience, tone } = context;

    return `${this.getToneMessage(tone, topic)}\n\nDesigned specifically for ${audience} who want results that matter.`;
  }

  private getToneMessage(tone: string, topic: string): string {
    const messages = {
      professional: `${topic} requires a strategic, data-driven approach that delivers measurable results.`,
      casual: `Here's the thing about ${topic} - it doesn't have to be complicated!`,
      friendly: `Let's chat about ${topic} and how it can make a real difference for you.`,
      authoritative: `Based on extensive research, ${topic} is a critical factor in achieving success.`,
      playful: `Ready to have some fun with ${topic}? Let's dive in and explore the possibilities! `
    };

    return messages[tone as keyof typeof messages] || messages.professional;
  }

  private generateTitle(context: ContentGenerationContext): string {
    const { topic, audience, type } = context;

    const titles = {
      blog: `The Complete Guide to ${topic} for ${audience}`,
      social_post: `${topic} Tips for ${audience}`,
      email: `Your ${topic} Strategy Update`,
      caption: `${topic} Made Simple`,
      copy: `Transform Your ${topic} Approach`
    };

    return titles[type] || `${topic} for ${audience}`;
  }

  private generateHashtags(context: ContentGenerationContext): string[] {
    const { topic, audience } = context;

    const baseHashtags = ['#AI', '#Marketing', '#NeonHub'];
    const topicHashtags = topic.split(' ').map(word => `#${word.charAt(0).toUpperCase() + word.slice(1)}`);
    const audienceHashtags = audience.split(' ').map(word => `#${word.charAt(0).toUpperCase() + word.slice(1)}`);

    return [...baseHashtags, ...topicHashtags.slice(0, 2), ...audienceHashtags.slice(0, 2)].slice(0, 8);
  }

  private calculateReadingTime(content: string): number {
    const wordsPerMinute = 200;
    const wordCount = content.split(/\s+/).length;
    return Math.ceil(wordCount / wordsPerMinute);
  }

  private calculateSEOScore(content: string, keywords: string[]): number {
    let score = 0;
    const contentLower = content.toLowerCase();

    keywords.forEach(keyword => {
      const keywordCount = (contentLower.match(new RegExp(keyword.toLowerCase(), 'g')) || []).length;
      if (keywordCount > 0) score += 20;
      if (keywordCount > 2) score += 10;
    });

    // Basic SEO checks
    if (content.length > 300) score += 20; // Good length
    if (content.includes('##') || content.includes('#')) score += 10; // Has headers

    return Math.min(score, 100);
  }

  // New methods for Phase 1 features
  async generatePost(context: ContentGenerationContext): Promise<AgentResult> {
    return this.execute({ 
      task: 'generate_post',
      context,
      priority: 'medium'
    });
  }

  async generateBlog(context: ContentGenerationContext): Promise<AgentResult> {
    return this.execute({ 
      task: 'generate_blog',
      context: { ...context, type: 'blog', length: 'long' },
      priority: 'medium'
    });
  }

  async generateCaption(context: ContentGenerationContext): Promise<AgentResult> {
    return this.execute({ 
      task: 'generate_caption',
      context: { ...context, type: 'caption', length: 'short' },
      priority: 'medium'
    });
  }
}

import OpenAI from 'openai';
import { AbstractAgent } from '../base-agent';
import type { AgentPayload, AgentResult } from '../base-agent';
import type { DesignAsset, BrandGuidelines, CreativeSpecification, DesignOptimization } from '../types';
import { logger } from '@neon/utils';

export interface DesignContext {
  projectId: string;
  brandGuidelines: BrandGuidelines;
  specifications: CreativeSpecification;
  platform: 'web' | 'mobile' | 'social' | 'print' | 'email' | 'ads' | 'packaging';
  dimensions: {
    width: number;
    height: number;
    aspectRatio?: string;
    dpi?: number;
  };
  contentType: 'logo' | 'banner' | 'social_post' | 'infographic' | 'brochure' | 'website' | 'app_ui' | 'presentation';
  targetAudience: {
    demographics: Record<string, any>;
    preferences: string[];
    behavioral_traits: string[];
  };
  objectives: string[];
  constraints?: {
    budget: number;
    timeline: string;
    technical_requirements: string[];
  };
}

export interface AssetGenerationContext {
  type: 'image' | 'vector' | 'video' | 'animation' | 'interactive';
  style: 'modern' | 'classic' | 'minimalist' | 'bold' | 'elegant' | 'playful' | 'professional';
  colorScheme: {
    primary: string;
    secondary: string;
    accent: string;
    neutral: string[];
  };
  typography: {
    primary: string;
    secondary: string;
    sizes: Record<string, number>;
  };
  content: {
    text?: string;
    images?: string[];
    data?: any;
  };
  variations: number;
}

export interface DesignOptimizationContext {
  existingAssets: DesignAsset[];
  performanceData?: any;
  testResults?: any;
  optimizationGoals: string[];
  constraints: string[];
}

export interface DesignGenerationResult extends AgentResult {
  assets: Array<{
    id: string;
    type: string;
    format: string;
    url?: string;
    base64?: string;
    metadata: {
      dimensions: { width: number; height: number };
      fileSize: number;
      colorProfile: string;
      brandCompliance: number;
    };
    variations: string[];
    optimizationScore: number;
  }>;
  brandCompliance: {
    score: number;
    analysis: string[];
    recommendations: string[];
  };
  recommendations: string[];
  alternatives: string[];
}

export interface BrandConsistencyResult extends AgentResult {
  complianceScore: number;
  violations: Array<{
    type: string;
    severity: 'low' | 'medium' | 'high';
    description: string;
    recommendation: string;
  }>;
  suggestions: string[];
  optimizedAssets: DesignAsset[];
}

export interface DesignVariationResult extends AgentResult {
  variations: Array<{
    id: string;
    variant: string;
    differences: string[];
    targetUseCase: string;
    performancePrediction: number;
  }>;
  recommended: string;
  reasoning: string;
}

export interface UIUXAnalysisResult extends AgentResult {
  usabilityScore: number;
  accessibility: {
    score: number;
    issues: string[];
    improvements: string[];
  };
  aesthetics: {
    score: number;
    strengths: string[];
    weaknesses: string[];
  };
  recommendations: Array<{
    category: string;
    priority: 'high' | 'medium' | 'low';
    suggestion: string;
    impact: string;
  }>;
}

export class DesignAgent extends AbstractAgent {
  private openai: OpenAI;
  private designTemplates: Map<string, any> = new Map();
  private brandGuidelinesCache: Map<string, BrandGuidelines> = new Map();
  private assetOptimizers: Map<string, Function> = new Map();
  private aiImageApiEndpoint?: string;

  constructor() {
    super('design-agent', 'DesignAgent', 'design', [
      'generate_creative',
      'optimize_design',
      'ensure_brand_consistency',
      'create_variations',
      'analyze_visual_performance',
      'generate_ui_mockup',
      'optimize_assets',
      'validate_accessibility',
      'generate_style_guide',
      'create_interactive_prototype'
    ]);

    this.openai = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY,
    });

    // Configure AI image generation (DALL-E, Midjourney, or Stable Diffusion)
    this.aiImageApiEndpoint = process.env.AI_IMAGE_API_ENDPOINT;

    if (!process.env.OPENAI_API_KEY) {
      logger.warn('OPENAI_API_KEY not found. DesignAgent will run in limited mode.', {}, 'DesignAgent');
    }

    this.initializeDesignTemplates();
    this.initializeAssetOptimizers();
  }

  async execute(payload: AgentPayload): Promise<AgentResult> {
    return this.executeWithErrorHandling(payload, async () => {
      const { task, context } = payload;

      switch (task) {
        case 'generate_creative':
          return await this.generateCreativeAssets(context as DesignContext);
        case 'optimize_design':
          return await this.optimizeDesignAssets(context as DesignOptimizationContext);
        case 'ensure_brand_consistency':
          return await this.validateBrandConsistency(context as { assets: DesignAsset[]; guidelines: BrandGuidelines });
        case 'create_variations':
          return await this.createDesignVariations(context as AssetGenerationContext);
        case 'analyze_visual_performance':
          return await this.analyzeVisualPerformance(context as { assets: DesignAsset[]; metrics: any[] });
        case 'generate_ui_mockup':
          return await this.generateUIMockup(context as DesignContext);
        case 'optimize_assets':
          return await this.optimizeAssetPerformance(context as { assets: DesignAsset[]; targetMetrics: string[] });
        case 'validate_accessibility':
          return await this.validateDesignAccessibility(context as { designs: DesignAsset[] });
        case 'generate_style_guide':
          return await this.generateStyleGuide(context as { brand: BrandGuidelines; examples: DesignAsset[] });
        case 'create_interactive_prototype':
          return await this.createInteractivePrototype(context as DesignContext);
        default:
          throw new Error(`Unknown task: ${task}`);
      }
    });
  }

  /**
   * AI-powered creative asset generation
   */
  private async generateCreativeAssets(context: DesignContext): Promise<DesignGenerationResult> {
    try {
      const designBrief = await this.createDesignBrief(context);
      const aiPrompts = await this.generateAIPrompts(context, designBrief);

      const assets = await Promise.all(
        aiPrompts.map(async (prompt, index) => {
          const asset = await this.generateAssetWithAI(prompt, context, index);
          const brandCompliance = await this.checkBrandCompliance(asset, context.brandGuidelines);
          const optimizationScore = await this.calculateOptimizationScore(asset, context);

          return {
            id: `asset_${Date.now()}_${index}`,
            type: context.contentType,
            format: this.getOptimalFormat(context.platform, context.contentType),
            metadata: {
              dimensions: context.dimensions,
              fileSize: this.estimateFileSize(context.dimensions, context.contentType),
              colorProfile: 'sRGB',
              brandCompliance
            },
            variations: await this.generateVariationIds(asset, 3),
            optimizationScore,
            ...asset
          };
        })
      );

      const overallBrandCompliance = await this.assessOverallBrandCompliance(assets, context.brandGuidelines);
      const recommendations = await this.generateDesignRecommendations(assets, context);
      const alternatives = await this.suggestAlternativeApproaches(context, assets);

      return {
        assets,
        brandCompliance: overallBrandCompliance,
        recommendations,
        alternatives,
        success: true
      };
    } catch (error) {
      logger.error('Creative asset generation failed', { error }, 'DesignAgent');
      return this.fallbackCreativeGeneration(context);
    }
  }

  /**
   * Design optimization with performance analysis
   */
  private async optimizeDesignAssets(context: DesignOptimizationContext): Promise<AgentResult> {
    try {
      const optimizations = await Promise.all(
        context.existingAssets.map(async (asset) => {
          const analysis = await this.analyzeAssetPerformance(asset, context.performanceData);
          const optimizationSuggestions = await this.generateOptimizationSuggestions(asset, analysis);
          const optimizedAsset = await this.applyOptimizations(asset, optimizationSuggestions);

          return {
            original: asset,
            optimized: optimizedAsset,
            improvements: optimizationSuggestions,
            expectedImpact: this.calculateExpectedImpact(optimizationSuggestions),
            confidence: analysis.confidence
          };
        })
      );

      const overallImpact = optimizations.reduce((sum, opt) => sum + opt.expectedImpact, 0) / optimizations.length;
      const prioritizedOptimizations = this.prioritizeOptimizations(optimizations, context.optimizationGoals);

      return {
        success: true,
        data: {
          optimizations: prioritizedOptimizations,
          overallImpact,
          recommendations: await this.generateOptimizationRecommendations(optimizations),
          implementationPlan: await this.createOptimizationImplementationPlan(prioritizedOptimizations)
        }
      };
    } catch (error) {
      logger.error('Design optimization failed', { error }, 'DesignAgent');
      return this.fallbackOptimization(context);
    }
  }

  /**
   * Brand consistency validation
   */
  private async validateBrandConsistency(context: { assets: DesignAsset[]; guidelines: BrandGuidelines }): Promise<BrandConsistencyResult> {
    try {
      const violations: any[] = [];
      const suggestions: string[] = [];
      let totalScore = 0;

      const assessments = await Promise.all(
        context.assets.map(async (asset) => {
          const colorCompliance = await this.validateColorCompliance(asset, context.guidelines);
          const typographyCompliance = await this.validateTypographyCompliance(asset, context.guidelines);
          const logoUsageCompliance = await this.validateLogoUsage(asset, context.guidelines);
          const overallCompliance = await this.validateOverallBrandConsistency(asset, context.guidelines);

          const assetViolations = [
            ...colorCompliance.violations,
            ...typographyCompliance.violations,
            ...logoUsageCompliance.violations,
            ...overallCompliance.violations
          ];

          const assetScore = (
            colorCompliance.score +
            typographyCompliance.score +
            logoUsageCompliance.score +
            overallCompliance.score
          ) / 4;

          violations.push(...assetViolations);
          suggestions.push(...this.generateComplianceSuggestions(assetViolations, asset));

          return {
            asset,
            score: assetScore,
            violations: assetViolations
          };
        })
      );

      totalScore = assessments.reduce((sum, assessment) => sum + assessment.score, 0) / assessments.length;

      const optimizedAssets = await Promise.all(
        assessments.map(async (assessment) => {
          if (assessment.score < 80) {
            return await this.optimizeForBrandCompliance(assessment.asset, context.guidelines, assessment.violations);
          }
          return assessment.asset;
        })
      );

      return {
        complianceScore: totalScore,
        violations,
        suggestions,
        optimizedAssets,
        success: true
      };
    } catch (error) {
      logger.error('Brand consistency validation failed', { error }, 'DesignAgent');
      return this.fallbackBrandConsistency(context);
    }
  }

  /**
   * AI-powered design variations
   */
  private async createDesignVariations(context: AssetGenerationContext): Promise<DesignVariationResult> {
    if (!this.openai) {
      return this.fallbackVariations(context);
    }

    try {
      const prompt = `
Create ${context.variations} design variations for a ${context.type} asset:

Style: ${context.style}
Color Scheme: Primary: ${context.colorScheme.primary}, Secondary: ${context.colorScheme.secondary}
Typography: ${context.typography.primary}
Content: ${JSON.stringify(context.content)}

Generate variations that:
1. Maintain brand consistency while exploring creative approaches
2. Target different use cases and audience preferences
3. Optimize for different performance metrics
4. Test different visual hierarchies and layouts
5. Explore color and typography variations within brand guidelines

For each variation, provide:
- Unique design approach
- Target use case
- Expected performance prediction
- Key differentiators

Return structured variation concepts with reasoning.
`;

      const response = await this.openai.chat.completions.create({
        model: "gpt-4",
        messages: [{ role: "user", content: prompt }],
        temperature: 0.7,
        max_tokens: 2000,
      });

      const aiSuggestions = response.choices[0]?.message?.content || '';
      const variations = await this.parseVariationSuggestions(aiSuggestions, context);
      const recommended = await this.selectRecommendedVariation(variations, context);

      return {
        variations,
        recommended: recommended.id,
        reasoning: recommended.reasoning,
        success: true
      };
    } catch (error) {
      logger.error('AI design variations failed', { error }, 'DesignAgent');
      return this.fallbackVariations(context);
    }
  }

  /**
   * UI/UX mockup generation
   */
  private async generateUIMockup(context: DesignContext): Promise<AgentResult> {
    try {
      const wireframe = await this.generateWireframe(context);
      const visualDesign = await this.applyVisualDesign(wireframe, context);
      const interactiveElements = await this.addInteractiveElements(visualDesign, context);
      const responsiveVariations = await this.createResponsiveVariations(interactiveElements, context);

      const usabilityScore = await this.assessUsability(interactiveElements);
      const accessibility = await this.checkAccessibility(interactiveElements);

      return {
        success: true,
        data: {
          mockup: {
            wireframe,
            visualDesign,
            interactive: interactiveElements,
            responsive: responsiveVariations
          },
          analysis: {
            usabilityScore,
            accessibility,
            brandCompliance: await this.checkBrandCompliance(visualDesign, context.brandGuidelines)
          },
          recommendations: await this.generateUIRecommendations(interactiveElements, usabilityScore, accessibility)
        }
      };
    } catch (error) {
      logger.error('UI mockup generation failed', { error }, 'DesignAgent');
      return this.fallbackUIMockup(context);
    }
  }

