  name: string;
  description: string;
  emails: Array<{
    templateId: string;
    delayDays: number;
    delayHours?: number;
    condition?: string;
    subject: string;
    content: string;
    htmlContent?: string;
  }>;
  triggerType: 'signup' | 'purchase' | 'abandonment' | 'manual' | 'behavior' | 'date_based';
  targetAudience?: string;
