export interface EmailRecipient {
  email: string;
  firstName?: string;
  lastName?: string;
  company?: string;
  customFields?: Record<string, any>;
  segmentTags?: string[];
  preferences?: {
    frequency?: 'daily' | 'weekly' | 'monthly';
    categories?: string[];
    unsubscribed?: boolean;
  };
  behaviorData?: {
    lastOpen?: Date;
    lastClick?: Date;
    totalOpens?: number;
    totalClicks?: number;
    avgEngagement?: number;
  };
}

export interface EmailSequenceInput {
  topic: string;
  audience: string;
  businessType?: string;
  sequenceLength?: number;
  tone?: 'professional' | 'casual' | 'friendly' | 'urgent';
  goals?: string[];
  industry?: string;
}

