  name: string;
  description: string;
  emails: Array<{
    step: number;
    subject: string;
    content: string;
    htmlContent?: string;
    delayDays: number;
    purpose: string;
    keyPoints: string[];
  }>;
  estimatedPerformance: {
    openRate: string;
    clickRate: string;
    conversionRate: string;
