  private async assessSystemRisks(campaigns: CampaignSummary[], agents: AgentPerformanceLog[]): Promise<RiskAssessmentReport> {
    const lowPerformingCampaigns = campaigns.filter(c => c.roas < 1.0);
    const underperformingAgents = agents.filter(a => a.goalSuccessRate < 0.7);

    const riskLevel = (lowPerformingCampaigns.length / campaigns.length > 0.3 || underperformingAgents.length > 0) 
      ? 'HIGH' : 'MEDIUM';

    return {
      riskLevel: riskLevel as any,
      identifiedRisks: [
        {
          type: 'PERFORMANCE_DECLINE',
          severity: 0.65,
          description: `${lowPerformingCampaigns.length} campaigns showing ROAS below 1.0, indicating potential performance issues`,
          mitigation: 'Review and optimize underperforming campaigns, adjust targeting and creative strategies'
        },
        {
          type: 'AGENT_UNDERPERFORMANCE',
          severity: underperformingAgents.length > 0 ? 0.7 : 0.3,
          description: `${underperformingAgents.length} agents showing below-target success rates`,
          mitigation: 'Investigate agent configurations, provide additional training data, optimize parameters'
        }
      ],
      budgetRisks: campaigns.filter(c => c.actualSpend && c.totalBudget && c.actualSpend > c.totalBudget * 0.9),
      performanceRisks: lowPerformingCampaigns
    };
  }

