    return dayOfMonth === 1 && 
           hour === 9 &&
           !this.hasGeneratedTodayReport('AGENT_PERFORMANCE');
  }

  private shouldGenerateQuarterlyBrandAudit(now: Date): boolean {
    const month = now.getMonth();
