      tiktok: 19     // 7 PM
    };

    const hour = baseHours[platform as keyof typeof baseHours] || 12;
    const optimalTime = new Date(date);
    optimalTime.setHours(hour, 0, 0, 0);

    return optimalTime;
  }

  private checkHoliday(date: Date): boolean {
