    const month = date.getMonth() + 1;
    const day = date.getDate();

    const holidays = [
      [1, 1],   // New Year
      [7, 4],   // July 4th
      [12, 25]  // Christmas
    ];

    return holidays.some(([m, d]) => m === month && d === day);
  }

