import { AbstractAgent } from '../base-agent';
import type { AgentResult, AgentPayload } from '../base-agent';
import OpenAI from 'openai';
import { logger } from '@neon/utils';
import * as fs from 'fs/promises';
import * as path from 'path';

// Core interfaces for customer support
export interface MessageClassificationInput {
  text: string;
  customer?: {
    id?: string;
    name?: string;
    email?: string;
    phone?: string;
    history?: Array<{
      message: string;
      timestamp: Date;
      channel: string;
    }>;
  };
  context?: {
    channel: 'whatsapp' | 'email' | 'chat' | 'phone' | 'social';
    previousInteractions?: number;
    customerTier?: 'basic' | 'premium' | 'enterprise';
  };
}

export interface MessageClassificationOutput {
  intent: 'inquiry' | 'complaint' | 'refund' | 'support' | 'compliment' | 'bug_report' | 'feature_request' | 'billing' | 'technical' | 'general';
  category: string;
  subcategory?: string;
  confidence: number;
  urgency: 'low' | 'medium' | 'high' | 'critical';
  requiresHuman: boolean;
  suggestedActions: string[];
  keywords: string[];
  entities: Array<{
    type: string;
    value: string;
    confidence: number;
  }>;
}

export interface ReplyGenerationInput {
  message: string;
  classification?: MessageClassificationOutput;
  tone: 'professional' | 'friendly' | 'empathetic' | 'apologetic' | 'informative';
  customer?: {
    name?: string;
    tier?: 'basic' | 'premium' | 'enterprise';
    language?: string;
    preferences?: string[];
  };
  context?: {
    ticketHistory?: Array<{
      message: string;
      response?: string;
      timestamp: Date;
    }>;
    relatedArticles?: Array<{
      title: string;
      url: string;
      relevance: number;
    }>;
    previousResolution?: string;
  };
  constraints?: {
    maxLength?: number;
    includeLinks?: boolean;
    escalationAvailable?: boolean;
  };
}

export interface ReplyGenerationOutput {
  reply: string;
  tone: string;
  confidence: number;
  suggestedFollowUps: string[];
  escalationRecommended: boolean;
  estimatedResolutionTime: number; // minutes
  requiredActions: Array<{
    action: string;
    priority: 'low' | 'medium' | 'high';
    assignee?: string;
  }>;
  relatedResources: Array<{
    type: 'article' | 'faq' | 'tutorial' | 'contact';
    title: string;
    url?: string;
    description?: string;
  }>;
}

export interface SentimentAnalysisInput {
  message: string;
  context?: {
    previousMessages?: string[];
    customerHistory?: string;
    interactionType?: string;
  };
}

export interface SentimentAnalysisOutput {
  sentiment: 'positive' | 'neutral' | 'negative';
  score: number; // -1 to 1
  confidence: number;
  emotions: Array<{
    emotion: string;
    intensity: number;
  }>;
  urgencyIndicators: string[];
  escalationTriggers: string[];
  customerSatisfactionRisk: 'low' | 'medium' | 'high';
}

export interface EscalationInput {
  message: string;
  ticketId?: string;
  classification?: MessageClassificationOutput;
  sentiment?: SentimentAnalysisOutput;
  reason?: string;
  customerTier?: 'basic' | 'premium' | 'enterprise';
  agentWorkload?: number;
}

export interface EscalationOutput {
  shouldEscalate: boolean;
  escalationLevel: 'supervisor' | 'specialist' | 'manager' | 'senior_management';
  reason: string;
  urgency: 'low' | 'medium' | 'high' | 'critical';
  suggestedAgent?: {
    id: string;
    name: string;
    skills: string[];
    availability: boolean;
  };
  estimatedWaitTime: number; // minutes
  alternativeActions: string[];
  escalationNotes: string;
}

export interface SupportTicket {
  id: string;
  customerId?: string;
  subject: string;
  message: string;
  channel: 'whatsapp' | 'email' | 'chat' | 'phone' | 'social';
  status: 'open' | 'in_progress' | 'pending_customer' | 'resolved' | 'closed';
  priority: 'low' | 'medium' | 'high' | 'critical';
  category?: string;
  assignedTo?: string;
  createdAt: Date;
  updatedAt: Date;
  resolvedAt?: Date;
  satisfactionScore?: number;
  tags: string[];
  metadata: Record<string, any>;
}

export interface WhatsAppMessage {
  recipient: string;
  message: {
    type: 'text' | 'image' | 'document' | 'template';
    content: string;
    media?: {
      url: string;
      caption?: string;
      filename?: string;
    };
    template?: {
      name: string;
      language: string;
      parameters?: string[];
    };
  };
  settings: {
    businessId?: string;
    accessToken?: string;
    webhookUrl?: string;
  };
}

export interface KnowledgeBaseArticle {
  id: string;
  title: string;
  content: string;
  category: string;
  tags: string[];
  views: number;
  helpful: number;
  lastUpdated: Date;
  author: string;
  status: 'draft' | 'published' | 'archived';
}

// Add Twilio import
interface TwilioClient {
  messages: {
    create: (options: {
      from: string;
      to: string;
      body: string;
    }) => Promise<{
      sid: string;
      status: string;
      errorCode?: string;
      errorMessage?: string;
    }>;
  };
}

let twilioClient: TwilioClient | null = null;

// Initialize Twilio client
try {
  if (process.env.TWILIO_ACCOUNT_SID && process.env.TWILIO_AUTH_TOKEN) {
    const twilio = require('twilio');
    twilioClient = twilio(process.env.TWILIO_ACCOUNT_SID, process.env.TWILIO_AUTH_TOKEN);
  }
} catch (error) {
  logger.warn('Twilio not available, WhatsApp will run in mock mode', { error }, 'CustomerSupportAgent');
}

export class CustomerSupportAgent extends AbstractAgent {
  private openai: OpenAI;
  private tickets: Map<string, SupportTicket> = new Map();
  private knowledgeBase: Map<string, KnowledgeBaseArticle> = new Map();

  constructor() {
    super('customer-support-agent', 'CustomerSupportAgent', 'support', [
      'classify_message',
      'generate_reply',
      'analyze_sentiment',
      'escalate_ticket',
      'create_ticket',
      'update_ticket',
      'send_whatsapp_message',
      'auto_respond',
      'manage_knowledge_base',
      'generate_summary',
      'track_satisfaction',
      'manage_queue'
    ]);

    this.openai = new OpenAI({
      apiKey: process.env.OPENAI_API_KEY,
    });

    if (!process.env.OPENAI_API_KEY) {
      logger.warn('OPENAI_API_KEY not found. CustomerSupportAgent will run in limited mode.', {}, 'CustomerSupportAgent');
    }

    this.initializeKnowledgeBase();
  }

  async execute(payload: AgentPayload): Promise<AgentResult> {
    return this.executeWithErrorHandling(payload, async () => {
      const { task, context } = payload;

      switch (task) {
        case 'classify_message':
          return await this.classifyMessageAI(context as MessageClassificationInput);
        case 'generate_reply':
          return await this.generateReplyAI(context as ReplyGenerationInput);
        case 'analyze_sentiment':
          return await this.analyzeSentimentAI(context as SentimentAnalysisInput);
        case 'escalate_ticket':
          return await this.escalateTicket(context as EscalationInput);
        case 'create_ticket':
          return await this.createTicket(context);
        case 'update_ticket':
          return await this.updateTicket(context);
        case 'send_whatsapp_message':
          return await this.sendWhatsAppMessage(context as WhatsAppMessage);
        case 'auto_respond':
          return await this.autoRespond(context);
        case 'manage_knowledge_base':
          return await this.manageKnowledgeBase(context);
        case 'generate_summary':
          return await this.generateTicketSummary(context);
        case 'track_satisfaction':
          return await this.trackCustomerSatisfaction(context);
        case 'manage_queue':
          return await this.manageTicketQueue(context);
        default:
          throw new Error(`Unknown task: ${task}`);
      }
    });
  }

  /**
   * Classify incoming support messages using AI
   */
  async classifyMessage(input: MessageClassificationInput): Promise<MessageClassificationOutput> {
    const { text, customer, context } = input;

    if (!this.openai) {
      return this.classifyMessageFallback(input);
    }

    try {
      const prompt = this.buildClassificationPrompt(text, customer, context);

      const response = await this.openai.chat.completions.create({
        model: "gpt-4",
        messages: [
          {
            role: "system",
            content: "You are an expert customer support message classifier. Analyze customer messages to determine intent, urgency, and required actions with high accuracy."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        temperature: 0.3,
        max_tokens: 1000,
      });

      const aiOutput = response.choices[0]?.message?.content;
      if (!aiOutput) {
        throw new Error('No response from OpenAI');
      }

      return this.parseClassificationOutput(aiOutput, input);
    } catch (error) {
      await this.logAIFallback('message_classification', error);
      logger.error('OpenAI message classification failed, using fallback', { error }, 'CustomerSupportAgent');
      return this.classifyMessageFallback(input);
    }
  }

  /**
   * Generate AI-powered support replies
   */
  async generateReply(input: ReplyGenerationInput): Promise<ReplyGenerationOutput> {
    const { message, classification, tone, customer, context, constraints } = input;

    if (!this.openai) {
      return this.generateReplyFallback(input);
    }

    try {
      const prompt = this.buildReplyPrompt(message, classification, tone, customer, context, constraints);

      const response = await this.openai.chat.completions.create({
        model: "gpt-4",
        messages: [
          {
            role: "system",
            content: "You are an expert customer support representative. Generate helpful, empathetic, and professional responses that resolve customer issues effectively."
          },
          {
            role: "user",
            content: prompt
          }
        ],
        temperature: 0.7,
        max_tokens: 1500,
      });

      const aiOutput = response.choices[0]?.message?.content;
      if (!aiOutput) {
        throw new Error('No response from OpenAI');
      }

      return this.parseReplyOutput(aiOutput, input);
    } catch (error) {
      await this.logAIFallback('reply_generation', error);
      logger.error('OpenAI reply generation failed, using fallback', { error }, 'CustomerSupportAgent');
      return this.generateReplyFallback(input);
    }
  }

  /**
   * Analyze customer sentiment using AI
   */
  async analyzeSentiment(input: SentimentAnalysisInput): Promise<SentimentAnalysisOutput> {
    const { message, context } = input;

    if (!this.openai) {
      return this.analyzeSentimentFallback(input);
    }

    try {
      const prompt = this.buildSentimentPrompt(message, context);

      const response = await this.openai.chat.completions.create({
