import { PrismaClient } from '@prisma/client';

export interface CampaignPattern {
  id: string;
  summary: string;
  winningVariants: {
    contentStyles: string[];
    subjects: string[];
    ctaTypes: string[];
    timingWindows: string[];
    agentSequences: string[];
  };
