  updatedAt: Date;
}

export interface PerformanceInsight {
  agentType: string;
  goalType: string;
  successRate: number;
  avgPerformance: number;
  bestCollaborations: string[];
  timeCorrelations: Record<string, number>;
  segmentAffinities: Record<string, number>;
}

export interface VariantStructure {
  type: 'subject' | 'copy' | 'visual' | 'cta' | 'timing';
  structure: string;
  performanceScore: number;
  usageCount: number;
  segments: string[];
}

export class CrossCampaignMemoryStore {
  private prisma: PrismaClient;
  private readonly SIMILARITY_THRESHOLD = 0.75;
  private readonly PATTERN_SCORE_THRESHOLD = 85;

  constructor() {
    this.prisma = new PrismaClient();
  }

  // Aggregate campaign performance across multiple runs
  async aggregatePerformanceData(campaignIds: string[]): Promise<PerformanceInsight[]> {
    const insights: PerformanceInsight[] = [];

    try {
      // Get all campaign executions for the specified campaigns
      const executions = await this.prisma.agentExecution.findMany({
        where: {
          campaignId: { in: campaignIds },
          status: 'COMPLETED',
          performance: { gt: 0 }
        },
        include: {
          agent: true,
          campaign: true
        }
      });

      // Group by agent type and goal
      const groupedData = new Map<string, any[]>();

      for (const execution of executions) {
        const key = `${execution.agent.type}_${execution.campaign?.type}`;
        if (!groupedData.has(key)) {
          groupedData.set(key, []);
        }
        groupedData.get(key)!.push(execution);
      }

      // Calculate insights for each group
      for (const [key, data] of groupedData) {
        const [agentType, goalType] = key.split('_');

        const totalExecutions = data.length;
        const successfulExecutions = data.filter(e => (e.performance || 0) >= 70).length;
        const avgPerformance = data.reduce((sum, e) => sum + (e.performance || 0), 0) / totalExecutions;

        // Find best collaborations (agents that performed well together)
        const collaborations = await this.findSuccessfulCollaborations(data);

        // Time correlations
        const timeCorrelations = this.calculateTimeCorrelations(data);

        // Segment affinities
        const segmentAffinities = await this.calculateSegmentAffinities(data);

        insights.push({
          agentType,
          goalType,
          successRate: (successfulExecutions / totalExecutions) * 100,
          avgPerformance,
          bestCollaborations: collaborations.slice(0, 3),
          timeCorrelations,
          segmentAffinities
        });
      }

      return insights;
    } catch (error) {
      console.error('Error aggregating performance data:', error);
      return [];
    }
  }

  // Detect most successful agents per goal
  async detectSuccessfulAgents(goalType: string): Promise<PerformanceInsight[]> {
    try {
      const executions = await this.prisma.agentExecution.findMany({
        where: {
          campaign: {
            type: goalType as any
          },
          status: 'COMPLETED',
          performance: { gt: 0 }
        },
        include: {
          agent: true
        }
      });

      // Group by agent type
      const agentPerformance = new Map<string, number[]>();

      for (const execution of executions) {
        const agentType = execution.agent.type;
        if (!agentPerformance.has(agentType)) {
          agentPerformance.set(agentType, []);
        }
        agentPerformance.get(agentType)!.push(execution.performance || 0);
      }

      // Calculate average performance for each agent type
      const insights: PerformanceInsight[] = [];
      for (const [agentType, scores] of agentPerformance) {
        const avgScore = scores.reduce((sum, score) => sum + score, 0) / scores.length;
        const successRate = (scores.filter(s => s >= 70).length / scores.length) * 100;

        insights.push({
          agentType,
          goalType,
          successRate,
          avgPerformance: avgScore,
          bestCollaborations: [],
          timeCorrelations: {},
          segmentAffinities: {}
        });
      }

      return insights.sort((a, b) => b.avgPerformance - a.avgPerformance);
    } catch (error) {
      console.error('Error detecting successful agents:', error);
      return [];
    }
  }

  // Detect high-performing variant structures
  async detectVariantStructures(): Promise<VariantStructure[]> {
    try {
      const abTests = await this.prisma.aBTest.findMany({
        where: {
          status: 'COMPLETED',
          confidence: { gt: 95 }
        }
      });

      const structures: VariantStructure[] = [];

      for (const test of abTests) {
        const variants = test.variants as any;
        const results = test.results as any;
        const winner = test.winner;

        if (winner && variants[winner]) {
          const winningVariant = variants[winner];

          // Analyze the structure of winning variants
          if (winningVariant.subject) {
            structures.push({
              type: 'subject',
              structure: this.extractSubjectStructure(winningVariant.subject),
              performanceScore: results[winner]?.performance || 0,
              usageCount: 1,
              segments: results[winner]?.segments || []
            });
          }

          if (winningVariant.copy) {
            structures.push({
              type: 'copy',
              structure: this.extractCopyStructure(winningVariant.copy),
              performanceScore: results[winner]?.performance || 0,
              usageCount: 1,
              segments: results[winner]?.segments || []
            });
          }

          if (winningVariant.cta) {
            structures.push({
              type: 'cta',
              structure: winningVariant.cta,
              performanceScore: results[winner]?.performance || 0,
              usageCount: 1,
              segments: results[winner]?.segments || []
            });
          }
        }
      }

      // Consolidate similar structures
      return this.consolidateStructures(structures);
    } catch (error) {
      console.error('Error detecting variant structures:', error);
      return [];
    }
  }

  // Store aggregated results with timestamps
  async storeCampaignPattern(pattern: Omit<CampaignPattern, 'id' | 'createdAt' | 'updatedAt'>): Promise<string> {
    try {
      const stored = await this.prisma.crossCampaignMemory.create({
        data: {
          summary: pattern.summary,
          winningVariants: pattern.winningVariants,
          patternScore: pattern.patternScore,
          segments: pattern.segments
        }
      });

      return stored.id;
    } catch (error) {
      console.error('Error storing campaign pattern:', error);
      throw error;
    }
  }

  // Retrieve patterns for analysis
  async getPatternsByScore(minScore: number = 80): Promise<CampaignPattern[]> {
    try {
      const patterns = await this.prisma.crossCampaignMemory.findMany({
        where: {
          patternScore: { gte: minScore }
        },
        orderBy: {
          patternScore: 'desc'
        }
      });

      return patterns.map(p => ({
        id: p.id,
        summary: p.summary,
        winningVariants: p.winningVariants as any,
        patternScore: p.patternScore,
        segments: p.segments as any,
        createdAt: p.createdAt,
        updatedAt: p.updatedAt
      }));
    } catch (error) {
      console.error('Error retrieving patterns by score:', error);
      return [];
    }
  }

  // Get trending patterns (recent high-performers)
  async getTrendingPatterns(daysBack: number = 30): Promise<CampaignPattern[]> {
    try {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - daysBack);

      const patterns = await this.prisma.crossCampaignMemory.findMany({
        where: {
          createdAt: { gte: cutoffDate },
          patternScore: { gte: this.PATTERN_SCORE_THRESHOLD }
        },
        orderBy: [
          { patternScore: 'desc' },
          { createdAt: 'desc' }
        ]
      });

      return patterns.map(p => ({
        id: p.id,
        summary: p.summary,
        winningVariants: p.winningVariants as any,
        patternScore: p.patternScore,
        segments: p.segments as any,
        createdAt: p.createdAt,
        updatedAt: p.updatedAt
      }));
    } catch (error) {
      console.error('Error retrieving trending patterns:', error);
      return [];
    }
  }

  // Calculate similarity between patterns using cosine similarity
  calculatePatternSimilarity(pattern1: CampaignPattern, pattern2: CampaignPattern): number {
    try {
      // Create feature vectors from patterns
      const features1 = this.extractPatternFeatures(pattern1);
      const features2 = this.extractPatternFeatures(pattern2);

      // Calculate cosine similarity
      let dotProduct = 0;
      let magnitude1 = 0;
      let magnitude2 = 0;

      for (let i = 0; i < Math.max(features1.length, features2.length); i++) {
        const val1 = features1[i] || 0;
        const val2 = features2[i] || 0;

        dotProduct += val1 * val2;
        magnitude1 += val1 * val1;
        magnitude2 += val2 * val2;
      }

      const magnitude = Math.sqrt(magnitude1) * Math.sqrt(magnitude2);
      return magnitude === 0 ? 0 : dotProduct / magnitude;
    } catch (error) {
      console.error('Error calculating pattern similarity:', error);
      return 0;
    }
  }

  // Private helper methods
  private async findSuccessfulCollaborations(executions: any[]): Promise<string[]> {
    // Find agent types that frequently appear together in successful campaigns
    const collaborations = new Map<string, number>();

    // Group by campaign
    const campaignGroups = new Map<string, any[]>();
    for (const exec of executions) {
      if (!campaignGroups.has(exec.campaignId)) {
        campaignGroups.set(exec.campaignId, []);
      }
      campaignGroups.get(exec.campaignId)!.push(exec);
    }

    // Find co-occurring agent types in successful campaigns
    for (const [campaignId, campaignExecs] of campaignGroups) {
      const avgPerformance = campaignExecs.reduce((sum, e) => sum + (e.performance || 0), 0) / campaignExecs.length;

      if (avgPerformance >= 70) { // Successful campaign
        const agentTypes = campaignExecs.map(e => e.agent.type);

        // Count all pairs
        for (let i = 0; i < agentTypes.length; i++) {
          for (let j = i + 1; j < agentTypes.length; j++) {
            const pair = [agentTypes[i], agentTypes[j]].sort().join('-');
            collaborations.set(pair, (collaborations.get(pair) || 0) + 1);
          }
        }
      }
    }

    return Array.from(collaborations.entries())
      .sort((a, b) => b[1] - a[1])
      .slice(0, 5)
      .map(([pair, count]) => pair);
  }

  private calculateTimeCorrelations(executions: any[]): Record<string, number> {
    const hourlyPerformance = new Map<number, number[]>();

    for (const exec of executions) {
      const hour = exec.startedAt.getHours();
      if (!hourlyPerformance.has(hour)) {
        hourlyPerformance.set(hour, []);
      }
      hourlyPerformance.get(hour)!.push(exec.performance || 0);
    }

    const correlations: Record<string, number> = {};
    for (const [hour, performances] of hourlyPerformance) {
      const avgPerformance = performances.reduce((sum, p) => sum + p, 0) / performances.length;
      correlations[`hour_${hour}`] = avgPerformance;
    }

    return correlations;
  }

  private async calculateSegmentAffinities(executions: any[]): Promise<Record<string, number>> {
    // This would analyze campaign metadata to find segment patterns
    // For now, return mock data
    return {
      'young_adults': 85,
      'professionals': 78,
      'tech_enthusiasts': 92
    };
  }

  private extractSubjectStructure(subject: string): string {
    // Analyze subject line structure
    const hasEmoji = /[\u{1F600}-\u{1F64F}]|[\u{1F300}-\u{1F5FF}]|[\u{1F680}-\u{1F6FF}]|[\u{1F1E0}-\u{1F1FF}]/u.test(subject);
    const hasNumbers = /\d/.test(subject);
    const hasQuestion = subject.includes('?');
    const hasUrgency = /urgent|now|today|limited|hurry/i.test(subject);
    const length = subject.length;

    let structure = '';
    if (hasEmoji) structure += 'emoji_';
    if (hasNumbers) structure += 'numbers_';
    if (hasQuestion) structure += 'question_';
    if (hasUrgency) structure += 'urgency_';

    if (length < 30) structure += 'short';
    else if (length < 50) structure += 'medium';
    else structure += 'long';

    return structure;
  }

  private extractCopyStructure(copy: string): string {
    // Analyze copy structure
    const wordCount = copy.split(' ').length;
    const hasCallout = /hey|hi|hello/i.test(copy);
    const hasSocial = /share|like|follow|tag/i.test(copy);
    const hasAction = /click|buy|get|download|sign up|join/i.test(copy);

    let structure = '';
    if (hasCallout) structure += 'personal_';
    if (hasSocial) structure += 'social_';
    if (hasAction) structure += 'action_';

    if (wordCount < 20) structure += 'short';
    else if (wordCount < 50) structure += 'medium';
    else structure += 'long';

    return structure;
  }

  private consolidateStructures(structures: VariantStructure[]): VariantStructure[] {
    const consolidated = new Map<string, VariantStructure>();

    for (const structure of structures) {
      const key = `${structure.type}_${structure.structure}`;

      if (consolidated.has(key)) {
        const existing = consolidated.get(key)!;
        existing.performanceScore = (existing.performanceScore + structure.performanceScore) / 2;
        existing.usageCount += structure.usageCount;
        existing.segments = [...new Set([...existing.segments, ...structure.segments])];
      } else {
        consolidated.set(key, { ...structure });
      }
    }

    return Array.from(consolidated.values())
      .sort((a, b) => b.performanceScore - a.performanceScore);
  }

  private extractPatternFeatures(pattern: CampaignPattern): number[] {
    const features: number[] = [];

    // Pattern score
    features.push(pattern.patternScore);

    // Winning variants features
    const variants = pattern.winningVariants;
    features.push(variants.contentStyles?.length || 0);
    features.push(variants.subjects?.length || 0);
    features.push(variants.ctaTypes?.length || 0);
    features.push(variants.timingWindows?.length || 0);
    features.push(variants.agentSequences?.length || 0);

    // Segment features
    const segments = pattern.segments;
    features.push(Object.keys(segments.demographics || {}).length);
    features.push(Object.keys(segments.behavioral || {}).length);
    features.push(Object.keys(segments.performance || {}).length);

    return features;
  }

  // Cleanup old patterns
  async cleanupOldPatterns(daysToKeep: number = 90): Promise<number> {
    try {
      const cutoffDate = new Date();
      cutoffDate.setDate(cutoffDate.getDate() - daysToKeep);

      const result = await this.prisma.crossCampaignMemory.deleteMany({
        where: {
          createdAt: { lt: cutoffDate },
          patternScore: { lt: 60 } // Only delete low-performing old patterns
        }
      });

      return result.count;
    } catch (error) {
      console.error('Error cleaning up old patterns:', error);
      return 0;
    }
  }

  async disconnect(): Promise<void> {
    await this.prisma.$disconnect();
  }
}

export default CrossCampaignMemoryStore;

/**
 * CrossAgentMemoryIndex - Advanced Memory Federation System
 * Provides contextual knowledge retrieval across all agents for Multi-Agent Reasoning Mesh
 */

import { PrismaClient } from '@prisma/client';
import { AgentType } from '@prisma/client';
import { CrossCampaignMemoryStore } from './CrossCampaignMemoryStore';
import { AgentMemoryStore } from './AgentMemoryStore';

const prisma = new PrismaClient();

export interface MemoryEntry {
  id: string;
  agentId: string;
  agentType: AgentType;
  sessionId: string;
  goalPlanId?: string;
  campaignId?: string;
  content: {
    input: any;
    output: any;
    context: any;
  };
  tags: string[];
  categories: string[];
  outcome: 'SUCCESS' | 'FAILURE' | 'PARTIAL' | 'UNKNOWN';
  confidence: number; // 0-1
  relevanceScore?: number; // 0-1, calculated during retrieval
  performance: {
    executionTime: number; // milliseconds
    tokensUsed: number;
    cost: number;
    successMetrics: any;
  };
  relationships: {
    dependencies: string[]; // Other memory IDs this depends on
    influences: string[]; // Memory IDs this influenced
    conflicts: string[]; // Memory IDs this conflicts with
  };
  temporal: {
    createdAt: Date;
    lastAccessed: Date;
    accessCount: number;
    decayScore: number; // 0-1, decreases over time
  };
  metadata: any;
}

export interface MemoryQuery {
  goalType?: string;
  agentTypes?: AgentType[];
  categories?: string[];
  tags?: string[];
  outcomeFilter?: ('SUCCESS' | 'FAILURE' | 'PARTIAL')[];
  timeRange?: {
    start: Date;
    end: Date;
  };
  confidenceThreshold?: number;
  limit?: number;
  includeRelated?: boolean;
  similarityThreshold?: number;
}

export interface MemoryInsight {
  type: 'PATTERN' | 'ANOMALY' | 'TREND' | 'CORRELATION' | 'BEST_PRACTICE';
  title: string;
  description: string;
  evidence: string[];
  confidence: number;
  actionable: boolean;
  recommendations: string[];
  affectedAgents: AgentType[];
  metadata: any;
}

export interface KnowledgeGraph {
  nodes: Array<{
    id: string;
    type: 'AGENT' | 'GOAL' | 'PATTERN' | 'OUTCOME';
    label: string;
    properties: any;
  }>;
  edges: Array<{
    source: string;
    target: string;
    type: 'DEPENDS_ON' | 'INFLUENCES' | 'CONFLICTS_WITH' | 'SIMILAR_TO';
    weight: number;
    properties: any;
  }>;
}

export class CrossAgentMemoryIndex {
  private static instance: CrossAgentMemoryIndex;
  private crossCampaignStore: CrossCampaignMemoryStore;
  private agentMemoryStore: AgentMemoryStore;
  private memoryCache: Map<string, MemoryEntry> = new Map();
  private indexMap: Map<string, string[]> = new Map(); // category/tag -> memory IDs

  constructor() {
    this.crossCampaignStore = new CrossCampaignMemoryStore();
    this.agentMemoryStore = new AgentMemoryStore();
  }

  static getInstance(): CrossAgentMemoryIndex {
    if (!CrossAgentMemoryIndex.instance) {
      CrossAgentMemoryIndex.instance = new CrossAgentMemoryIndex();
    }
    return CrossAgentMemoryIndex.instance;
  }

  /**
   * Ingest memory from agents and index it for retrieval
   */
  async ingestMemory(memoryData: {
    agentId: string;
    agentType: AgentType;
    sessionId: string;
    goalPlanId?: string;
    campaignId?: string;
    input: any;
    output: any;
    context?: any;
    outcome: 'SUCCESS' | 'FAILURE' | 'PARTIAL';
    performance: {
      executionTime: number;
      tokensUsed: number;
      cost: number;
      successMetrics?: any;
    };
    metadata?: any;
  }): Promise<string> {
    try {
      // Generate unique ID
      const memoryId = `mem_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

      // Extract semantic content and categories
      const { tags, categories } = await this.extractSemanticContent(memoryData);

      // Calculate confidence based on outcome and performance
      const confidence = this.calculateConfidence(memoryData);

      // Create indexed memory entry
      const memoryEntry: MemoryEntry = {
        id: memoryId,
        agentId: memoryData.agentId,
        agentType: memoryData.agentType,
        sessionId: memoryData.sessionId,
        goalPlanId: memoryData.goalPlanId,
        campaignId: memoryData.campaignId,
        content: {
          input: memoryData.input,
          output: memoryData.output,
          context: memoryData.context || {},
        },
        tags,
        categories,
        outcome: memoryData.outcome,
        confidence,
        performance: memoryData.performance,
        relationships: {
          dependencies: [],
          influences: [],
          conflicts: [],
        },
        temporal: {
          createdAt: new Date(),
          lastAccessed: new Date(),
          accessCount: 0,
          decayScore: 1.0,
        },
        metadata: memoryData.metadata || {},
      };

      // Update indexes
      await this.updateIndexes(memoryEntry);

      // Store in cache
      this.memoryCache.set(memoryEntry.id, memoryEntry);

      // Analyze relationships with existing memories
      await this.analyzeRelationships(memoryEntry);

      // Update cross-campaign patterns if this is campaign-related
      if (memoryData.campaignId) {
        await this.updateCrossCampaignPatterns(memoryEntry);
      }

      console.log(` [CrossAgentMemoryIndex] Memory ingested: ${memoryData.agentType} -> ${memoryId}`);
      return memoryId;
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error ingesting memory:', error);
      throw error;
    }
  }

  /**
   * Retrieve contextual memories based on query
   */
  async retrieveMemories(query: MemoryQuery): Promise<MemoryEntry[]> {
    try {
      console.log(` [CrossAgentMemoryIndex] Retrieving memories for query:`, query);

      let candidateIds: Set<string> = new Set();

      // Get candidate memory IDs from indexes
      if (query.categories) {
        query.categories.forEach(category => {
          const ids = this.indexMap.get(`category:${category}`) || [];
          ids.forEach(id => candidateIds.add(id));
        });
      }

      if (query.tags) {
        query.tags.forEach(tag => {
          const ids = this.indexMap.get(`tag:${tag}`) || [];
          ids.forEach(id => candidateIds.add(id));
        });
      }

      // If no specific filters, get all recent successful memories
      if (candidateIds.size === 0) {
        const recentMemories = await prisma.agentMemory.findMany({
          where: {
            success: true,
            timestamp: query.timeRange ? {
              gte: query.timeRange.start,
              lte: query.timeRange.end,
            } : { gte: new Date(Date.now() - 7 * 24 * 60 * 60 * 1000) }, // Last 7 days
          },
          orderBy: { timestamp: 'desc' },
          take: 100,
        });

        recentMemories.forEach(memory => candidateIds.add(memory.id));
      }

      // Load and filter memories
      const memories: MemoryEntry[] = [];
      for (const id of candidateIds) {
        let memory = this.memoryCache.get(id);

        if (!memory) {
          memory = await this.loadMemoryFromDatabase(id);
          if (memory) {
            this.memoryCache.set(id, memory);
          }
        }

        if (memory && this.passesFilters(memory, query)) {
          // Calculate relevance score
          memory.relevanceScore = await this.calculateRelevanceScore(memory, query);
          memories.push(memory);
        }
      }

      // Sort by relevance and confidence
      memories.sort((a, b) => {
        const scoreA = (a.relevanceScore || 0) * a.confidence * a.temporal.decayScore;
        const scoreB = (b.relevanceScore || 0) * b.confidence * b.temporal.decayScore;
        return scoreB - scoreA;
      });

      // Apply limit
      const limit = query.limit || 10;
      const results = memories.slice(0, limit);

      // Include related memories if requested
      if (query.includeRelated) {
        const relatedMemories = await this.getRelatedMemories(results);
        results.push(...relatedMemories);
      }

      // Update access statistics
      results.forEach(memory => {
        memory.temporal.lastAccessed = new Date();
        memory.temporal.accessCount++;
      });

      console.log(` [CrossAgentMemoryIndex] Retrieved ${results.length} memories`);
      return results;
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error retrieving memories:', error);
      return [];
    }
  }

  /**
   * Get contextual prompts for agent planning
   */
  async getContextualPrompts(goalType: string, agentType: AgentType): Promise<{
    successPatterns: string[];
    pitfallsToAvoid: string[];
    bestPractices: string[];
    relatedExperiences: string[];
  }> {
    try {
      const query: MemoryQuery = {
        goalType,
        agentTypes: [agentType],
        outcomeFilter: ['SUCCESS'],
        confidenceThreshold: 0.7,
        limit: 20,
      };

      const successfulMemories = await this.retrieveMemories(query);

      const failureQuery: MemoryQuery = {
        ...query,
        outcomeFilter: ['FAILURE'],
        limit: 10,
      };

      const failureMemories = await this.retrieveMemories(failureQuery);

      // Extract patterns and insights
      const successPatterns = this.extractPatterns(successfulMemories, 'success');
      const pitfalls = this.extractPatterns(failureMemories, 'failure');
      const bestPractices = await this.extractBestPractices(successfulMemories);
      const relatedExperiences = this.extractExperiences(successfulMemories);

      return {
        successPatterns,
        pitfallsToAvoid: pitfalls,
        bestPractices,
        relatedExperiences,
      };
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error getting contextual prompts:', error);
      return {
        successPatterns: [],
        pitfallsToAvoid: [],
        bestPractices: [],
        relatedExperiences: [],
      };
    }
  }

  /**
   * Generate insights from memory patterns
   */
  async generateInsights(agentType?: AgentType): Promise<MemoryInsight[]> {
    try {
      const insights: MemoryInsight[] = [];

      // Pattern analysis
      const patterns = await this.analyzeMemoryPatterns(agentType);
      insights.push(...patterns);

      // Anomaly detection
      const anomalies = await this.detectAnomalies(agentType);
      insights.push(...anomalies);

      // Trend analysis
      const trends = await this.analyzeTrends(agentType);
      insights.push(...trends);

      // Correlation analysis
      const correlations = await this.findCorrelations(agentType);
      insights.push(...correlations);

      // Sort by confidence and actionability
      insights.sort((a, b) => {
        const scoreA = a.confidence * (a.actionable ? 1.5 : 1);
        const scoreB = b.confidence * (b.actionable ? 1.5 : 1);
        return scoreB - scoreA;
      });

      console.log(` [CrossAgentMemoryIndex] Generated ${insights.length} insights`);
      return insights;
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error generating insights:', error);
      return [];
    }
  }

  /**
   * Build knowledge graph of agent relationships and patterns
   */
  async buildKnowledgeGraph(): Promise<KnowledgeGraph> {
    try {
      const nodes: KnowledgeGraph['nodes'] = [];
      const edges: KnowledgeGraph['edges'] = [];

      // Get all agent types and their memories
      const agentTypes = Object.values(AgentType);

      for (const agentType of agentTypes) {
        nodes.push({
          id: `agent_${agentType}`,
          type: 'AGENT',
          label: agentType,
          properties: { type: agentType },
        });

        // Get memories for this agent
        const memories = await this.retrieveMemories({
          agentTypes: [agentType],
          limit: 50,
        });

        // Add goal nodes and connections
        const goalTypes = new Set(memories.map(m => m.goalPlanId).filter(Boolean));
        goalTypes.forEach(goalId => {
          if (goalId) {
            nodes.push({
              id: `goal_${goalId}`,
              type: 'GOAL',
              label: `Goal ${goalId}`,
              properties: { goalId },
            });

            edges.push({
              source: `agent_${agentType}`,
              target: `goal_${goalId}`,
              type: 'INFLUENCES',
              weight: 1.0,
              properties: {},
            });
          }
        });
      }

      // Add cross-agent relationships
      for (const memory of this.memoryCache.values()) {
        memory.relationships.influences.forEach(influencedId => {
          const influencedMemory = this.memoryCache.get(influencedId);
          if (influencedMemory && memory.agentType !== influencedMemory.agentType) {
            edges.push({
              source: `agent_${memory.agentType}`,
              target: `agent_${influencedMemory.agentType}`,
              type: 'INFLUENCES',
              weight: memory.confidence,
              properties: { memoryId: memory.id },
            });
          }
        });
      }

      console.log(` [CrossAgentMemoryIndex] Built knowledge graph: ${nodes.length} nodes, ${edges.length} edges`);
      return { nodes, edges };
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error building knowledge graph:', error);
      return { nodes: [], edges: [] };
    }
  }

  /**
   * Clean up old and low-value memories
   */
  async cleanup(): Promise<void> {
    try {
      const cutoffDate = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000); // 30 days ago

      // Calculate decay scores for all memories
      for (const memory of this.memoryCache.values()) {
        memory.temporal.decayScore = this.calculateDecayScore(memory);
      }

      // Remove low-value memories from cache
      const memoriesToRemove: string[] = [];
      for (const [id, memory] of this.memoryCache.entries()) {
        const totalScore = memory.confidence * memory.temporal.decayScore;
        if (totalScore < 0.1 || memory.temporal.createdAt < cutoffDate) {
          memoriesToRemove.push(id);
        }
      }

      memoriesToRemove.forEach(id => this.memoryCache.delete(id));

      // Update indexes
      await this.rebuildIndexes();

      console.log(` [CrossAgentMemoryIndex] Cleanup completed: removed ${memoriesToRemove.length} memories`);
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error during cleanup:', error);
    }
  }

  // Private helper methods
  private async extractSemanticContent(memoryData: any): Promise<{
    tags: string[];
    categories: string[];
  }> {
    const tags: string[] = [];
    const categories: string[] = [];

    // Extract from input/output content (simplified semantic analysis)
    const contentText = JSON.stringify(memoryData.input) + JSON.stringify(memoryData.output);
    const words = contentText.toLowerCase().match(/\b\w+\b/g) || [];

    // Category detection
    if (words.includes('content') || words.includes('post')) categories.push('content_creation');
    if (words.includes('seo') || words.includes('optimization')) categories.push('seo_optimization');
    if (words.includes('campaign') || words.includes('marketing')) categories.push('campaign_management');
    if (words.includes('brand') || words.includes('voice')) categories.push('brand_management');
    if (words.includes('trend') || words.includes('analysis')) categories.push('trend_analysis');

    // Tag extraction
    if (words.includes('success') || words.includes('good')) tags.push('high_performance');
    if (words.includes('error') || words.includes('fail')) tags.push('error_prone');
    if (words.includes('fast') || words.includes('quick')) tags.push('time_efficient');
    if (words.includes('cost') || words.includes('budget')) tags.push('cost_sensitive');

    return { tags, categories };
  }

  private calculateConfidence(memoryData: any): number {
    let confidence = 0.5; // Base confidence

    // Adjust based on outcome
    switch (memoryData.outcome) {
      case 'SUCCESS': confidence = 0.9; break;
      case 'PARTIAL': confidence = 0.6; break;
      case 'FAILURE': confidence = 0.3; break;
    }

    // Adjust based on performance metrics
    if (memoryData.performance.executionTime < 5000) confidence += 0.1; // Fast execution
    if (memoryData.performance.cost < 0.01) confidence += 0.05; // Low cost
    if (memoryData.performance.successMetrics?.score > 0.8) confidence += 0.1; // High score

    return Math.max(0, Math.min(1, confidence));
  }

  private async updateIndexes(memory: MemoryEntry): Promise<void> {
    // Index by categories
    memory.categories.forEach(category => {
      const key = `category:${category}`;
      if (!this.indexMap.has(key)) {
        this.indexMap.set(key, []);
      }
      this.indexMap.get(key)!.push(memory.id);
    });

    // Index by tags
    memory.tags.forEach(tag => {
      const key = `tag:${tag}`;
      if (!this.indexMap.has(key)) {
        this.indexMap.set(key, []);
      }
      this.indexMap.get(key)!.push(memory.id);
    });

    // Index by agent type
    const agentKey = `agent:${memory.agentType}`;
    if (!this.indexMap.has(agentKey)) {
      this.indexMap.set(agentKey, []);
    }
    this.indexMap.get(agentKey)!.push(memory.id);
  }

  private passesFilters(memory: MemoryEntry, query: MemoryQuery): boolean {
    if (query.agentTypes && !query.agentTypes.includes(memory.agentType)) return false;
    if (query.outcomeFilter && !query.outcomeFilter.includes(memory.outcome)) return false;
    if (query.confidenceThreshold && memory.confidence < query.confidenceThreshold) return false;
    if (query.timeRange) {
      if (memory.temporal.createdAt < query.timeRange.start || 
          memory.temporal.createdAt > query.timeRange.end) return false;
    }
    return true;
  }

  private async calculateRelevanceScore(memory: MemoryEntry, query: MemoryQuery): Promise<number> {
    let score = 0;

    // Category relevance
    if (query.categories) {
      const matchingCategories = memory.categories.filter(cat => query.categories!.includes(cat));
      score += (matchingCategories.length / query.categories.length) * 0.4;
    }

    // Tag relevance
    if (query.tags) {
      const matchingTags = memory.tags.filter(tag => query.tags!.includes(tag));
      score += (matchingTags.length / query.tags.length) * 0.3;
    }

    // Recency boost
    const daysSinceCreation = (Date.now() - memory.temporal.createdAt.getTime()) / (1000 * 60 * 60 * 24);
    const recencyScore = Math.max(0, 1 - daysSinceCreation / 30); // Decay over 30 days
    score += recencyScore * 0.2;

    // Access frequency boost
    const accessScore = Math.min(1, memory.temporal.accessCount / 10);
    score += accessScore * 0.1;

    return Math.max(0, Math.min(1, score));
  }

  private async loadMemoryFromDatabase(id: string): Promise<MemoryEntry | null> {
    try {
      const dbMemory = await prisma.agentMemory.findUnique({
        where: { id },
      });

      if (!dbMemory) return null;

      // Convert database memory to MemoryEntry format
      const memory: MemoryEntry = {
        id: dbMemory.id,
        agentId: dbMemory.agentId,
        agentType: AgentType.CONTENT, // Default, would need to derive from agentId
        sessionId: dbMemory.sessionId,
        content: {
          input: dbMemory.input,
          output: dbMemory.output,
          context: {},
        },
        tags: [], // Would extract from metadata
        categories: [], // Would extract from metadata
        outcome: dbMemory.success ? 'SUCCESS' : 'FAILURE',
        confidence: 0.8, // Default
        performance: {
          executionTime: dbMemory.executionTime,
          tokensUsed: dbMemory.tokensUsed,
          cost: dbMemory.cost,
          successMetrics: {},
        },
        relationships: {
          dependencies: [],
          influences: [],
          conflicts: [],
        },
        temporal: {
          createdAt: dbMemory.timestamp,
          lastAccessed: new Date(),
          accessCount: 0,
          decayScore: 1.0,
        },
        metadata: dbMemory.metadata || {},
      };

      return memory;
    } catch (error) {
      console.error('[CrossAgentMemoryIndex] Error loading memory from database:', error);
      return null;
    }
  }

  private extractPatterns(memories: MemoryEntry[], type: 'success' | 'failure'): string[] {
    // Simplified pattern extraction
    const patterns: string[] = [];

    if (type === 'success') {
      patterns.push('High confidence scores (>0.8) correlate with success');
      patterns.push('Fast execution times improve outcomes');
      patterns.push('Content-creation tasks benefit from brand alignment');
    } else {
      patterns.push('Complex goals without proper decomposition tend to fail');
      patterns.push('Resource conflicts lead to execution failures');
      patterns.push('Insufficient context often results in poor outcomes');
    }

