}

export interface TrendAnalysis {
  name: string;
  direction: 'upward' | 'downward' | 'stable' | 'volatile';
  strength: 'weak' | 'moderate' | 'strong';
  magnitude: number; // Percentage change
  confidence: number; // 0-1
  timeSeriesData: { period: string; value: number }[];
  significantEvents?: SignificantEvent[];
}

export interface TrendInsight {
  type: 'opportunity' | 'risk' | 'pattern' | 'anomaly';
  description: string;
  impact: 'low' | 'medium' | 'high';
  actionable: boolean;
  recommendation?: string;
}

export interface TrendPrediction {
  projectedDirection: 'upward' | 'downward' | 'stable';
  projectedMagnitude: number;
  timeHorizon: string;
  confidence: number;
  assumptions: string[];
}

export interface SignificantEvent {
  date: string;
  description: string;
  impact: number; // Impact magnitude
  type: 'campaign_launch' | 'market_change' | 'competitive_action' | 'seasonal' | 'external';
}

export class TrendSlideTemplate {
  static create(
    data: TrendSlideData,
    theme: PresentationTheme = PresentationTheme.NEON_GLASS
  ): StrategySlide {

    const slide: StrategySlide = {
      slideNumber: 0,
      slideType: 'TREND',
      title: data.title,
      subtitle: data.subtitle,
      mainContent: {
        primaryTrend: data.primaryTrend,
        secondaryTrends: data.secondaryTrends || [],
        timeframe: data.timeframe,
        dataPoints: data.dataPoints,
        insights: data.insights,
        predictions: data.predictions,
        layout: this.determineLayout(data)
      },
      visualConfig: this.generateTrendVisualization(data, theme),
      keyTakeaway: this.generateKeyTakeaway(data),
      businessContext: this.generateBusinessContext(data),
      recommendation: this.generateRecommendation(data),
      sourceMetrics: {
        type: 'trend_analysis',
        timeframe: data.timeframe,
        dataPoints: data.dataPoints,
        timestamp: new Date().toISOString()
      },
      theme,
      layout: this.determineLayout(data)
    };

    return slide;
  }

  private static determineLayout(data: TrendSlideData): string {
    if (data.secondaryTrends && data.secondaryTrends.length > 2) {
      return 'multi_trend'; // Multiple trend comparison
    } else if (data.predictions) {
      return 'predictive'; // Trend with predictions
    } else if (data.primaryTrend.significantEvents && data.primaryTrend.significantEvents.length > 0) {
      return 'annotated'; // Trend with event annotations
    }
    return 'standard'; // Single trend focus
  }

  private static generateTrendVisualization(data: TrendSlideData, theme: PresentationTheme): any {
    const themeColors = this.getThemeColors(theme);
    const primaryData = data.primaryTrend.timeSeriesData;

    const datasets = [{
      label: data.primaryTrend.name,
      data: primaryData.map(d => d.value),
      borderColor: this.getTrendColor(data.primaryTrend.direction, themeColors),
      backgroundColor: this.getTrendColor(data.primaryTrend.direction, themeColors, 0.1),
      borderWidth: 3,
      fill: true,
      tension: 0.4,
      pointRadius: 4,
      pointHoverRadius: 6
    }];

    // Add secondary trends if present
    if (data.secondaryTrends) {
      data.secondaryTrends.forEach((trend, index) => {
        datasets.push({
          label: trend.name,
          data: trend.timeSeriesData.map(d => d.value),
          borderColor: themeColors.secondary[index % themeColors.secondary.length],
          backgroundColor: `${themeColors.secondary[index % themeColors.secondary.length]}20`,
          borderWidth: 2,
          fill: false,
          tension: 0.4,
          pointRadius: 3
        });
      });
    }

    // Add prediction line if present
    if (data.predictions) {
      const lastValue = primaryData[primaryData.length - 1].value;
      const projectedValue = lastValue * (1 + data.predictions.projectedMagnitude / 100);

      datasets.push({
        label: 'Prediction',
        data: [...new Array(primaryData.length - 1).fill(null), lastValue, projectedValue],
        borderColor: themeColors.accent,
        backgroundColor: 'transparent',
        borderWidth: 2,
        borderDash: [5, 5],
        fill: false,
        pointRadius: [0, 0, 0, 6],
        pointBackgroundColor: themeColors.accent
      });
    }

    return {
      type: 'line',
      data: {
        labels: primaryData.map(d => d.period),
        datasets
      },
      options: {
        responsive: true,
        maintainAspectRatio: false,
        interaction: {
          intersect: false,
          mode: 'index'
        },
        plugins: {
          legend: {
            display: true,
            position: 'top',
            labels: { color: themeColors.text }
          },
          tooltip: {
            backgroundColor: themeColors.background,
            titleColor: themeColors.text,
            bodyColor: themeColors.text,
            borderColor: themeColors.border,
            borderWidth: 1,
            displayColors: true
          },
          annotation: data.primaryTrend.significantEvents ? {
            annotations: this.createEventAnnotations(data.primaryTrend.significantEvents, themeColors)
          } : undefined
        },
        scales: {
          x: {
            display: true,
            title: {
              display: true,
              text: 'Time Period',
              color: themeColors.text
            },
            ticks: { color: themeColors.text },
            grid: { color: themeColors.border }
          },
          y: {
            display: true,
            title: {
              display: true,
              text: 'Value',
              color: themeColors.text
            },
            ticks: { color: themeColors.text },
            grid: { color: themeColors.border }
          }
        }
      }
    };
  }

  private static createEventAnnotations(events: SignificantEvent[], colors: any): any[] {
    return events.map(event => ({
      type: 'line',
      scaleID: 'x',
      value: event.date,
      borderColor: colors.warning,
      borderWidth: 2,
      borderDash: [3, 3],
      label: {
        enabled: true,
        content: event.description,
        position: 'top',
        backgroundColor: colors.warning,
        color: colors.text
      }
    }));
  }

  private static getTrendColor(direction: string, colors: any, alpha: number = 1): string {
    const colorMap = {
      upward: colors.success,
      downward: colors.danger,
      stable: colors.neutral,
      volatile: colors.warning
    };

    const color = colorMap[direction] || colors.primary;
    return alpha < 1 ? `${color}${Math.round(alpha * 255).toString(16).padStart(2, '0')}` : color;
  }

  private static getThemeColors(theme: PresentationTheme): any {
    const themes = {
      [PresentationTheme.NEON_GLASS]: {
        primary: '#00ff88',
        accent: '#6366f1',
        text: '#ffffff',
        background: 'rgba(15, 20, 25, 0.9)',
        border: 'rgba(255, 255, 255, 0.1)',
        success: '#00ff88',
        danger: '#ff4757',
        warning: '#ffa502',
        neutral: '#747d8c',
        secondary: ['#8b5cf6', '#ec4899', '#f59e0b', '#10b981']
      },
      [PresentationTheme.EXECUTIVE_DARK]: {
        primary: '#6366f1',
        accent: '#8b5cf6',
        text: '#ffffff',
        background: '#1a1a2e',
        border: '#2d3748',
        success: '#48bb78',
        danger: '#f56565',
        warning: '#ed8936',
        neutral: '#a0aec0',
        secondary: ['#00ff88', '#ec4899', '#f59e0b', '#10b981']
      },
      [PresentationTheme.CMO_LITE]: {
        primary: '#6366f1',
        accent: '#8b5cf6',
        text: '#1a202c',
        background: '#ffffff',
        border: '#e2e8f0',
        success: '#48bb78',
        danger: '#f56565',
        warning: '#ed8936',
        neutral: '#718096',
        secondary: ['#00ff88', '#ec4899', '#f59e0b', '#10b981']
      }
    };

    return themes[theme] || themes[PresentationTheme.NEON_GLASS];
  }

  private static generateKeyTakeaway(data: TrendSlideData): string {
    const trend = data.primaryTrend;
    const directionText = this.getDirectionText(trend.direction);
    const strengthText = trend.strength;
    const magnitudeText = trend.magnitude > 0 ? `+${trend.magnitude.toFixed(1)}%` : `${trend.magnitude.toFixed(1)}%`;

    return `${trend.name} shows ${strengthText} ${directionText} trend with ${magnitudeText} change over ${data.timeframe}`;
  }

  private static generateBusinessContext(data: TrendSlideData): string {
    const highImpactInsights = data.insights.filter(i => i.impact === 'high').length;
    const opportunities = data.insights.filter(i => i.type === 'opportunity').length;
    const risks = data.insights.filter(i => i.type === 'risk').length;

    if (highImpactInsights > 2) {
      return 'Multiple high-impact trends identified requiring strategic attention and resource allocation';
    } else if (opportunities > risks) {
      return 'Trend analysis reveals significant growth opportunities outweighing potential risks';
    } else if (risks > opportunities) {
      return 'Risk mitigation strategies needed to address concerning trend patterns';
    } else {
      return 'Balanced trend landscape with mixed opportunities and challenges requiring selective focus';
    }
  }

  private static generateRecommendation(data: TrendSlideData): string {
    const actionableInsights = data.insights.filter(i => i.actionable);
    const primaryTrend = data.primaryTrend;

    if (actionableInsights.length > 0) {
      const topAction = actionableInsights.find(i => i.impact === 'high')?.recommendation;
      if (topAction) return topAction;
    }

    if (primaryTrend.direction === 'upward' && primaryTrend.strength === 'strong') {
      return 'Capitalize on positive trend momentum with increased investment and expansion';
    } else if (primaryTrend.direction === 'downward' && primaryTrend.strength === 'strong') {
      return 'Immediate intervention required to reverse declining trend pattern';
    } else if (primaryTrend.direction === 'volatile') {
      return 'Implement stabilization strategies to reduce trend volatility and improve predictability';
    }

    return 'Monitor trend developments closely and prepare adaptive strategies for emerging patterns';
  }

  private static getDirectionText(direction: string): string {
    const directionMap = {
      upward: 'upward',
      downward: 'downward',
      stable: 'stable',
      volatile: 'volatile'
    };
    return directionMap[direction] || direction;
  }

  // Utility methods for common trend slide patterns
  static createROASTrendSlide(roasTrendData: {
    timeSeriesData: { period: string; value: number }[];
    events: SignificantEvent[];
    prediction: TrendPrediction;
  }, theme?: PresentationTheme): StrategySlide {

    const magnitude = this.calculateMagnitude(roasTrendData.timeSeriesData);
    const direction = magnitude > 5 ? 'upward' : magnitude < -5 ? 'downward' : 'stable';

    const slideData: TrendSlideData = {
      title: 'ROAS Trend Analysis',
      subtitle: 'Return on Ad Spend Performance Over Time',
      primaryTrend: {
        name: 'ROAS',
        direction,
        strength: Math.abs(magnitude) > 10 ? 'strong' : Math.abs(magnitude) > 5 ? 'moderate' : 'weak',
        magnitude,
        confidence: 0.85,
        timeSeriesData: roasTrendData.timeSeriesData,
        significantEvents: roasTrendData.events
      },
      timeframe: '12 months',
      dataPoints: roasTrendData.timeSeriesData.length,
      insights: [
        {
          type: magnitude > 0 ? 'opportunity' : 'risk',
          description: `ROAS trend ${magnitude > 0 ? 'improving' : 'declining'} by ${Math.abs(magnitude).toFixed(1)}%`,
          impact: Math.abs(magnitude) > 10 ? 'high' : 'medium',
          actionable: true,
          recommendation: magnitude > 0 ? 
            'Scale high-performing campaigns to maximize positive trend' :
            'Investigate declining performance and optimize underperforming campaigns'
        }
      ],
      predictions: roasTrendData.prediction
    };

    return this.create(slideData, theme);
  }

  static createBrandTrendSlide(brandTrendData: {
    alignmentTrend: { period: string; value: number }[];
    consistencyTrend: { period: string; value: number }[];
    events: SignificantEvent[];
  }, theme?: PresentationTheme): StrategySlide {

    const alignmentMagnitude = this.calculateMagnitude(brandTrendData.alignmentTrend);

    const slideData: TrendSlideData = {
      title: 'Brand Health Trends',
      subtitle: 'Alignment & Consistency Performance',
      primaryTrend: {
        name: 'Brand Alignment Score',
        direction: alignmentMagnitude > 2 ? 'upward' : alignmentMagnitude < -2 ? 'downward' : 'stable',
        strength: Math.abs(alignmentMagnitude) > 5 ? 'strong' : 'moderate',
        magnitude: alignmentMagnitude,
        confidence: 0.78,
        timeSeriesData: brandTrendData.alignmentTrend,
        significantEvents: brandTrendData.events
      },
      secondaryTrends: [
        {
          name: 'Consistency Score',
          direction: 'stable',
          strength: 'moderate',
          magnitude: this.calculateMagnitude(brandTrendData.consistencyTrend),
          confidence: 0.82,
          timeSeriesData: brandTrendData.consistencyTrend
        }
      ],
      timeframe: '6 months',
      dataPoints: brandTrendData.alignmentTrend.length,
      insights: [
        {
          type: 'pattern',
          description: 'Brand alignment improving across all channels',
          impact: 'medium',
          actionable: true,
          recommendation: 'Continue brand voice optimization and expand to new channels'
        }
      ]
    };

    return this.create(slideData, theme);
  }

  private static calculateMagnitude(timeSeriesData: { period: string; value: number }[]): number {
    if (timeSeriesData.length < 2) return 0;

    const firstValue = timeSeriesData[0].value;
    const lastValue = timeSeriesData[timeSeriesData.length - 1].value;

    return ((lastValue - firstValue) / firstValue) * 100;
  }
}

export default TrendSlideTemplate;

/**
 * A/B Testing Manager - Launch, Track & Compare Campaign Variants
 * Manages the complete A/B testing lifecycle with statistical significance
 */

import { AgentMemoryStore } from '../memory/AgentMemoryStore';
import { ContentVariant, VariantCombination } from './campaign-variant-generator';

export interface ABTest {
  id: string;
  campaignId: string;
  name: string;
  status: 'draft' | 'running' | 'paused' | 'completed' | 'winner_declared';
  variants: TestVariant[];
  config: ABTestConfig;
  results: ABTestResults;
  winner?: string;
  createdAt: Date;
  startedAt?: Date;
  completedAt?: Date;
}

export interface TestVariant {
  id: string;
  name: string;
  combination: VariantCombination;
  trafficAllocation: number; // Percentage of traffic (0-100)
  status: 'active' | 'paused' | 'winner' | 'loser';
  metrics: VariantMetrics;
}

export interface ABTestConfig {
  testType: 'split' | 'multivariate' | 'sequential';
  duration: number; // Minutes
  minSampleSize: number;
  confidenceLevel: number; // 0.95 for 95% confidence
  statisticalPower: number; // 0.8 for 80% power
  primaryMetric: 'open_rate' | 'click_rate' | 'conversion_rate' | 'revenue';
  secondaryMetrics: string[];
  autoWinner: boolean; // Automatically declare winner when statistically significant
  maxDuration: number; // Maximum test duration in minutes
  trafficSplit: 'equal' | 'weighted' | 'adaptive';
}

export interface VariantMetrics {
  impressions: number;
  opens: number;
  clicks: number;
  conversions: number;
  revenue: number;
  bounces: number;
  unsubscribes: number;
  openRate: number;
  clickRate: number;
  conversionRate: number;
  revenuePerUser: number;
  lastUpdated: Date;
}

export interface ABTestResults {
  totalImpressions: number;
  totalConversions: number;
  testProgress: number; // 0-100
  statisticalSignificance: StatisticalSignificance;
  recommendation: TestRecommendation;
  insights: string[];
  performance: PerformanceComparison[];
}

export interface StatisticalSignificance {
  isSignificant: boolean;
  pValue: number;
  confidenceInterval: [number, number];
  sampleSizeReached: boolean;
  powerAchieved: boolean;
  timeToSignificance?: number; // Estimated minutes
}

export interface TestRecommendation {
  action: 'continue' | 'declare_winner' | 'stop_test' | 'extend_duration' | 'adjust_traffic';
  reason: string;
  confidence: number;
  expectedLift: number;
  estimatedRevenue: number;
}

export interface PerformanceComparison {
  variantId: string;
  variantName: string;
  primaryMetricValue: number;
  lift: number; // Percentage improvement over control
  significance: number; // P-value
  rank: number;
  isWinner: boolean;
  isLoser: boolean;
}

export interface ABTestCreationRequest {
  campaignId: string;
  name: string;
  variants: VariantCombination[];
  config: Partial<ABTestConfig>;
  targetAudience: {
    size: number;
    segments: string[];
    filters: Record<string, any>;
  };
}

export class ABTestingManager {
  private memoryStore: AgentMemoryStore;
  private activeTests: Map<string, ABTest> = new Map();
  private updateInterval: NodeJS.Timeout | null = null;

  constructor(memoryStore: AgentMemoryStore) {
    this.memoryStore = memoryStore;
    this.startPeriodicUpdates();
  }

  /**
   * Create and launch a new A/B test
   */
  async createTest(request: ABTestCreationRequest): Promise<ABTest> {
    try {
      console.log(` Creating A/B test for campaign ${request.campaignId}`);

      // Generate default config
      const config: ABTestConfig = {
        testType: 'split',
        duration: 2880, // 48 hours default
        minSampleSize: 1000,
        confidenceLevel: 0.95,
        statisticalPower: 0.8,
        primaryMetric: 'conversion_rate',
        secondaryMetrics: ['open_rate', 'click_rate', 'revenue'],
        autoWinner: true,
        maxDuration: 10080, // 7 days max
        trafficSplit: 'equal',
        ...request.config
      };

      // Create test variants with equal traffic allocation
      const trafficPerVariant = 100 / request.variants.length;
      const testVariants: TestVariant[] = request.variants.map((combination, index) => ({
        id: `variant_${index}`,
        name: combination.name,
        combination,
        trafficAllocation: trafficPerVariant,
        status: 'active',
        metrics: this.initializeMetrics()
      }));

      // Create the test
      const test: ABTest = {
        id: `abtest_${Date.now()}`,
        campaignId: request.campaignId,
        name: request.name,
        status: 'draft',
        variants: testVariants,
        config,
        results: this.initializeResults(),
        createdAt: new Date()
      };

      // Store in memory for quick access
      this.activeTests.set(test.id, test);

      // Persist to memory store
      await this.memoryStore.store(
        `ab_test_${test.id}`,
        test,
        ['ab_testing', 'campaign', request.campaignId]
      );

      console.log(` A/B test created: ${test.id}`);
      return test;

    } catch (error) {
      console.error(' A/B test creation failed:', error);
      throw new Error(`A/B test creation failed: ${error}`);
    }
  }

  /**
   * Start an A/B test
   */
  async startTest(testId: string): Promise<void> {
    const test = this.activeTests.get(testId);
    if (!test) {
      throw new Error(`Test ${testId} not found`);
    }

    test.status = 'running';
    test.startedAt = new Date();

    // Calculate sample size requirements
    const requiredSampleSize = this.calculateSampleSize(test.config);
    test.config.minSampleSize = Math.max(test.config.minSampleSize, requiredSampleSize);

    console.log(` A/B test started: ${testId} (Sample size: ${requiredSampleSize})`);

    // Update stored version
    await this.updateStoredTest(test);
  }

  /**
   * Update test metrics (called by campaign execution)
   */
  async updateTestMetrics(testId: string, variantId: string, metrics: Partial<VariantMetrics>): Promise<void> {
    const test = this.activeTests.get(testId);
    if (!test || test.status !== 'running') {
      return;
    }

    const variant = test.variants.find(v => v.id === variantId);
    if (!variant) {
      return;
    }

    // Update variant metrics
    Object.assign(variant.metrics, metrics);
    variant.metrics.lastUpdated = new Date();

    // Recalculate derived metrics
    this.calculateDerivedMetrics(variant.metrics);

    // Update test results
    this.updateTestResults(test);

    // Check if test should be concluded
    if (test.config.autoWinner) {
      await this.checkTestCompletion(test);
    }
  }

  /**
   * Check if test should be completed and declare winner
   */
  private async checkTestCompletion(test: ABTest): Promise<void> {
    const significance = this.calculateStatisticalSignificance(test);
    test.results.statisticalSignificance = significance;

    const recommendation = this.generateRecommendation(test);
    test.results.recommendation = recommendation;

    // Auto-declare winner if conditions are met
    if (recommendation.action === 'declare_winner' && test.config.autoWinner) {
      await this.declareWinner(test.id);
    }

    // Auto-stop if test runs too long
    const testDuration = Date.now() - (test.startedAt?.getTime() || Date.now());
    if (testDuration > test.config.maxDuration * 60 * 1000) {
      await this.stopTest(test.id, 'max_duration_reached');
    }
  }

  /**
   * Declare the winning variant
   */
  async declareWinner(testId: string): Promise<string> {
    const test = this.activeTests.get(testId);
    if (!test) {
      throw new Error(`Test ${testId} not found`);
    }

    // Find the best performing variant
    const performance = this.calculatePerformanceComparison(test);
    const winner = performance.find(p => p.isWinner);

    if (!winner) {
      throw new Error('No clear winner found');
    }

    test.status = 'winner_declared';
    test.winner = winner.variantId;
    test.completedAt = new Date();

    // Mark winner and losers
    test.variants.forEach(variant => {
      variant.status = variant.id === winner.variantId ? 'winner' : 'loser';
    });

    console.log(` Winner declared for test ${testId}: ${winner.variantName} (${winner.lift.toFixed(2)}% lift)`);

    // Store learnings for future tests
    await this.storeLearnings(test);

    // Update stored version
    await this.updateStoredTest(test);

    return winner.variantId;
  }

  /**
   * Stop an A/B test
   */
  async stopTest(testId: string, reason: string = 'manual_stop'): Promise<void> {
    const test = this.activeTests.get(testId);
    if (!test) {
      throw new Error(`Test ${testId} not found`);
    }

    test.status = 'completed';
    test.completedAt = new Date();

    console.log(` A/B test stopped: ${testId} (Reason: ${reason})`);

    // Store final results
    await this.updateStoredTest(test);
  }

  /**
   * Get test results and current status
   */
  async getTestResults(testId: string): Promise<ABTestResults> {
    const test = this.activeTests.get(testId);
    if (!test) {
      throw new Error(`Test ${testId} not found`);
    }

    // Update real-time calculations
    this.updateTestResults(test);

    return test.results;
  }

  /**
   * Calculate statistical significance
   */
  private calculateStatisticalSignificance(test: ABTest): StatisticalSignificance {
    if (test.variants.length < 2) {
      return {
        isSignificant: false,
        pValue: 1.0,
        confidenceInterval: [0, 0],
        sampleSizeReached: false,
        powerAchieved: false
      };
    }

    // Get control and treatment variants
    const control = test.variants[0];
    const treatment = test.variants[1];

    // Calculate conversion rates
    const controlRate = control.metrics.conversionRate;
    const treatmentRate = treatment.metrics.conversionRate;

    // Calculate sample sizes
    const controlSample = control.metrics.impressions;
    const treatmentSample = treatment.metrics.impressions;

    // Simple z-test for proportions
    const pooledRate = (control.metrics.conversions + treatment.metrics.conversions) / 
                      (controlSample + treatmentSample);

    const standardError = Math.sqrt(pooledRate * (1 - pooledRate) * (1/controlSample + 1/treatmentSample));
    const zScore = Math.abs(treatmentRate - controlRate) / standardError;

    // Calculate p-value (approximate)
    const pValue = 2 * (1 - this.normalCDF(Math.abs(zScore)));

    // Check significance
    const isSignificant = pValue < (1 - test.config.confidenceLevel);
    const sampleSizeReached = Math.min(controlSample, treatmentSample) >= test.config.minSampleSize;

    return {
      isSignificant,
      pValue,
      confidenceInterval: this.calculateConfidenceInterval(treatmentRate, controlRate, standardError, test.config.confidenceLevel),
      sampleSizeReached,
      powerAchieved: isSignificant && sampleSizeReached,
      timeToSignificance: this.estimateTimeToSignificance(test)
    };
  }

  /**
   * Generate test recommendation
   */
  private generateRecommendation(test: ABTest): TestRecommendation {
    const significance = test.results.statisticalSignificance;
    const performance = this.calculatePerformanceComparison(test);
    const winner = performance.find(p => p.isWinner);

    if (significance.isSignificant && significance.sampleSizeReached && winner) {
      return {
        action: 'declare_winner',
        reason: 'Statistical significance achieved with sufficient sample size',
        confidence: 1 - significance.pValue,
        expectedLift: winner.lift,
        estimatedRevenue: this.estimateRevenue(test, winner.lift)
      };
    }

    if (!significance.sampleSizeReached) {
      return {
        action: 'continue',
        reason: 'Insufficient sample size, continue testing',
        confidence: 0.5,
        expectedLift: winner?.lift || 0,
        estimatedRevenue: 0
      };
    }

    if (significance.timeToSignificance && significance.timeToSignificance > test.config.maxDuration) {
      return {
        action: 'stop_test',
        reason: 'Unlikely to reach significance within time limit',
        confidence: 0.3,
        expectedLift: 0,
        estimatedRevenue: 0
      };
    }

    return {
      action: 'continue',
      reason: 'Test in progress, monitoring for significance',
      confidence: 0.7,
      expectedLift: winner?.lift || 0,
      estimatedRevenue: 0
    };
  }

  /**
   * Calculate performance comparison between variants
   */
  private calculatePerformanceComparison(test: ABTest): PerformanceComparison[] {
    const control = test.variants[0];
    const controlMetric = this.getMetricValue(control.metrics, test.config.primaryMetric);

    const comparisons: PerformanceComparison[] = test.variants.map((variant, index) => {
      const metricValue = this.getMetricValue(variant.metrics, test.config.primaryMetric);
      const lift = index === 0 ? 0 : ((metricValue - controlMetric) / controlMetric) * 100;

      return {
        variantId: variant.id,
        variantName: variant.name,
        primaryMetricValue: metricValue,
        lift,
        significance: 0.5, // Simplified - would need proper calculation
        rank: 0,
        isWinner: false,
        isLoser: false
      };
    });

    // Sort by performance and assign ranks
    comparisons.sort((a, b) => b.primaryMetricValue - a.primaryMetricValue);
    comparisons.forEach((comp, index) => {
      comp.rank = index + 1;
      comp.isWinner = index === 0 && comp.lift > 0;
      comp.isLoser = index === comparisons.length - 1 && comparisons.length > 2;
    });

    return comparisons;
  }

  /**
   * Helper methods
   */
  private initializeMetrics(): VariantMetrics {
    return {
      impressions: 0,
      opens: 0,
      clicks: 0,
      conversions: 0,
      revenue: 0,
      bounces: 0,
      unsubscribes: 0,
      openRate: 0,
      clickRate: 0,
      conversionRate: 0,
      revenuePerUser: 0,
      lastUpdated: new Date()
    };
  }

  private initializeResults(): ABTestResults {
    return {
      totalImpressions: 0,
      totalConversions: 0,
      testProgress: 0,
      statisticalSignificance: {
        isSignificant: false,
        pValue: 1.0,
        confidenceInterval: [0, 0],
        sampleSizeReached: false,
        powerAchieved: false
      },
      recommendation: {
        action: 'continue',
        reason: 'Test just started',
        confidence: 0.5,
        expectedLift: 0,
        estimatedRevenue: 0
      },
      insights: [],
      performance: []
    };
  }

  private calculateDerivedMetrics(metrics: VariantMetrics): void {
    metrics.openRate = metrics.impressions > 0 ? (metrics.opens / metrics.impressions) * 100 : 0;
    metrics.clickRate = metrics.opens > 0 ? (metrics.clicks / metrics.opens) * 100 : 0;
    metrics.conversionRate = metrics.clicks > 0 ? (metrics.conversions / metrics.clicks) * 100 : 0;
    metrics.revenuePerUser = metrics.impressions > 0 ? metrics.revenue / metrics.impressions : 0;
  }

  private updateTestResults(test: ABTest): void {
    test.results.totalImpressions = test.variants.reduce((sum, v) => sum + v.metrics.impressions, 0);
    test.results.totalConversions = test.variants.reduce((sum, v) => sum + v.metrics.conversions, 0);

    const elapsed = test.startedAt ? Date.now() - test.startedAt.getTime() : 0;
    test.results.testProgress = Math.min((elapsed / (test.config.duration * 60 * 1000)) * 100, 100);

    test.results.performance = this.calculatePerformanceComparison(test);
    test.results.insights = this.generateInsights(test);
  }

  private generateInsights(test: ABTest): string[] {
    const insights: string[] = [];
    const performance = test.results.performance;

    if (performance.length >= 2) {
      const best = performance[0];
      const worst = performance[performance.length - 1];

      if (best.lift > 10) {
        insights.push(`${best.variantName} shows strong performance with ${best.lift.toFixed(1)}% lift`);
      }

      if (worst.lift < -5) {
        insights.push(`${worst.variantName} underperforming by ${Math.abs(worst.lift).toFixed(1)}%`);
      }
    }

    return insights;
  }

  private getMetricValue(metrics: VariantMetrics, metricName: string): number {
    switch (metricName) {
      case 'open_rate': return metrics.openRate;
      case 'click_rate': return metrics.clickRate;
      case 'conversion_rate': return metrics.conversionRate;
      case 'revenue': return metrics.revenuePerUser;
      default: return metrics.conversionRate;
    }
  }

  private calculateSampleSize(config: ABTestConfig): number {
    // Simplified sample size calculation
    const alpha = 1 - config.confidenceLevel;
    const beta = 1 - config.statisticalPower;
    const effectSize = 0.05; // 5% relative improvement

    // Rough approximation
    return Math.ceil(16 / (effectSize * effectSize));
  }

  private normalCDF(x: number): number {
    // Approximation of normal CDF
    return 0.5 * (1 + this.erf(x / Math.sqrt(2)));
  }

  private erf(x: number): number {
    // Approximation of error function
    const a1 =  0.254829592;
    const a2 = -0.284496736;
    const a3 =  1.421413741;
    const a4 = -1.453152027;
    const a5 =  1.061405429;
    const p  =  0.3275911;

    const sign = x < 0 ? -1 : 1;
    x = Math.abs(x);

    const t = 1.0 / (1.0 + p * x);
    const y = 1.0 - (((((a5 * t + a4) * t) + a3) * t + a2) * t + a1) * t * Math.exp(-x * x);

    return sign * y;
  }

  private calculateConfidenceInterval(
    treatmentRate: number, 
    controlRate: number, 
    standardError: number, 
    confidenceLevel: number
  ): [number, number] {
    const z = this.getZScore(confidenceLevel);
    const diff = treatmentRate - controlRate;
    const margin = z * standardError;

    return [diff - margin, diff + margin];
  }

  private getZScore(confidenceLevel: number): number {
    // Z-scores for common confidence levels
    const zScores: Record<number, number> = {
      0.90: 1.645,
      0.95: 1.96,
      0.99: 2.576
    };

    return zScores[confidenceLevel] || 1.96;
  }

  private estimateTimeToSignificance(test: ABTest): number {
    // Simplified estimation - would need more sophisticated modeling
    const currentProgress = test.results.testProgress;
    const currentPValue = test.results.statisticalSignificance.pValue;

    if (currentPValue < 0.1) {
      return test.config.duration * 0.5; // Likely to reach significance soon
    }

    return test.config.duration * 1.5; // May need more time
  }

  private estimateRevenue(test: ABTest, lift: number): number {
    const totalRevenue = test.variants.reduce((sum, v) => sum + v.metrics.revenue, 0);
    return totalRevenue * (lift / 100);
  }

  private async storeLearnings(test: ABTest): Promise<void> {
    const learnings = {
      winningVariant: test.variants.find(v => v.status === 'winner'),
      performance: test.results.performance,
      insights: test.results.insights,
      testDuration: test.completedAt && test.startedAt ? 
        test.completedAt.getTime() - test.startedAt.getTime() : 0
    };

    await this.memoryStore.store(
      `ab_test_learnings_${test.id}`,
      learnings,
      ['learnings', 'ab_testing', test.campaignId]
    );
  }

  private async updateStoredTest(test: ABTest): Promise<void> {
    await this.memoryStore.store(
      `ab_test_${test.id}`,
      test,
      ['ab_testing', 'campaign', test.campaignId]
    );
  }

  private startPeriodicUpdates(): void {
    // Update tests every 5 minutes
    this.updateInterval = setInterval(() => {
      this.activeTests.forEach(test => {
        if (test.status === 'running') {
          this.updateTestResults(test);
        }
      });
    }, 5 * 60 * 1000);
  }

  /**
   * Cleanup
   */
  destroy(): void {
    if (this.updateInterval) {
      clearInterval(this.updateInterval);
    }
  }
}

import CrossCampaignMemoryStore, { CampaignPattern } from '../memory/CrossCampaignMemoryStore';
import { SmartScheduler } from './smart-scheduler';
import { PredictiveCampaignGenerator, PredictiveCampaignPlan } from './predictive-campaign-generator';
import { ContentAgent } from '../agents/content-agent';
import { BrandVoiceAgent } from '../agents/brand-voice-agent';

export interface AutoReplayConfiguration {
  confidenceThreshold: number;
  maxConcurrentReplays: number;
  minimumTimeBetweenReplays: number; // in hours
  budgetAllocation: number;
  enableContentRefresh: boolean;
  enableTimingOptimization: boolean;
  enableBrandValidation: boolean;
  testMode: boolean;
}

export interface ReplayExecution {
  id: string;
  originalPatternId: string;
  campaignPlan: PredictiveCampaignPlan;
  status: 'queued' | 'running' | 'completed' | 'failed' | 'cancelled';
  startedAt: Date;
  completedAt?: Date;
  performance?: {
    actualROI: number;
    predictedROI: number;
    variance: number;
    keyMetrics: Record<string, number>;
  };
  modifications: ReplayModification[];
  learnings: string[];
  errorLog?: string[];
}

export interface ReplayModification {
  type: 'content' | 'timing' | 'audience' | 'budget' | 'agent_sequence';
  description: string;
  originalValue: any;
  newValue: any;
  reason: string;
  confidence: number;
}

export interface ReplayAnalytics {
  totalReplays: number;
  successfulReplays: number;
  averageROI: number;
  topPerformingPatterns: string[];
  commonModifications: ReplayModification[];
  learningInsights: string[];
  recommendedOptimizations: string[];
}

export class AutoReplayEngine {
  private crossCampaignMemory: CrossCampaignMemoryStore;
  private smartScheduler: SmartScheduler;
  private predictiveGenerator: PredictiveCampaignGenerator;
  private contentAgent: ContentAgent;
  private brandVoiceAgent: BrandVoiceAgent;
  private configuration: AutoReplayConfiguration;
  private activeReplays: Map<string, ReplayExecution> = new Map();
  private replayHistory: ReplayExecution[] = [];
  private isRunning: boolean = false;

  constructor(config?: Partial<AutoReplayConfiguration>) {
    this.crossCampaignMemory = new CrossCampaignMemoryStore();
    this.smartScheduler = new SmartScheduler();
    this.predictiveGenerator = new PredictiveCampaignGenerator();
    this.contentAgent = new ContentAgent('auto-replay-content');
    this.brandVoiceAgent = new BrandVoiceAgent('auto-replay-brand');

    this.configuration = {
      confidenceThreshold: 85,
      maxConcurrentReplays: 3,
      minimumTimeBetweenReplays: 24,
      budgetAllocation: 10000,
      enableContentRefresh: true,
      enableTimingOptimization: true,
      enableBrandValidation: true,
      testMode: false,
      ...config
    };
  }

  async startAutoReplay(): Promise<void> {
    if (this.isRunning) {
      throw new Error('Auto-replay engine is already running');
    }

    this.isRunning = true;
    console.log(' Auto-replay engine started');

    // Start the main monitoring loop
    this.startMonitoringLoop();
  }

  async stopAutoReplay(): Promise<void> {
    this.isRunning = false;

    // Cancel any queued replays
    for (const [id, replay] of this.activeReplays) {
      if (replay.status === 'queued') {
        replay.status = 'cancelled';
        replay.completedAt = new Date();
      }
    }

    console.log(' Auto-replay engine stopped');
  }

  async triggerManualReplay(patternId: string, overrides?: Partial<AutoReplayConfiguration>): Promise<string> {
    try {
      const pattern = await this.crossCampaignMemory.getPatternsByScore(0).then(patterns => 
        patterns.find(p => p.id === patternId)
      );

      if (!pattern) {
        throw new Error(`Pattern ${patternId} not found`);
      }

      const tempConfig = { ...this.configuration, ...overrides };
      const replayId = await this.executeReplay(pattern, tempConfig);

      return replayId;

    } catch (error) {
      throw new Error(`Failed to trigger manual replay: ${error.message}`);
    }
  }

  async getReplayStatus(replayId: string): Promise<ReplayExecution | null> {
    return this.activeReplays.get(replayId) || 
           this.replayHistory.find(r => r.id === replayId) || 
           null;
  }

  async getReplayAnalytics(daysBack: number = 30): Promise<ReplayAnalytics> {
    const cutoffDate = new Date();
    cutoffDate.setDate(cutoffDate.getDate() - daysBack);

    const relevantReplays = this.replayHistory.filter(r => r.startedAt >= cutoffDate);
    const successfulReplays = relevantReplays.filter(r => r.status === 'completed' && r.performance);

    const analytics: ReplayAnalytics = {
      totalReplays: relevantReplays.length,
      successfulReplays: successfulReplays.length,
      averageROI: 0,
      topPerformingPatterns: [],
      commonModifications: [],
      learningInsights: [],
      recommendedOptimizations: []
    };

    if (successfulReplays.length > 0) {
      // Calculate average ROI
      analytics.averageROI = successfulReplays.reduce((sum, r) => 
        sum + (r.performance?.actualROI || 0), 0) / successfulReplays.length;

      // Find top performing patterns
      const patternPerformance = new Map<string, number[]>();
      for (const replay of successfulReplays) {
        if (!patternPerformance.has(replay.originalPatternId)) {
          patternPerformance.set(replay.originalPatternId, []);
        }
        patternPerformance.get(replay.originalPatternId)!.push(replay.performance!.actualROI);
      }

      analytics.topPerformingPatterns = Array.from(patternPerformance.entries())
        .map(([id, rois]) => ({ id, avgROI: rois.reduce((a, b) => a + b, 0) / rois.length }))
        .sort((a, b) => b.avgROI - a.avgROI)
        .slice(0, 5)
        .map(p => p.id);

      // Analyze common modifications
      const modificationCounts = new Map<string, number>();
      for (const replay of relevantReplays) {
        for (const mod of replay.modifications) {
          const key = `${mod.type}:${mod.description}`;
          modificationCounts.set(key, (modificationCounts.get(key) || 0) + 1);
        }
      }

      analytics.commonModifications = Array.from(modificationCounts.entries())
        .sort((a, b) => b[1] - a[1])
        .slice(0, 5)
        .map(([key, count]) => {
          const [type, description] = key.split(':');
          return {
            type: type as any,
            description,
            originalValue: null,
            newValue: null,
            reason: `Applied in ${count} replays`,
            confidence: (count / relevantReplays.length) * 100
          };
        });

      // Generate learning insights
      analytics.learningInsights = this.generateLearningInsights(successfulReplays);

      // Generate optimization recommendations
      analytics.recommendedOptimizations = this.generateOptimizationRecommendations(analytics);
    }

    return analytics;
  }

  private startMonitoringLoop(): void {
    const checkInterval = 60 * 60 * 1000; // Check every hour

    const monitoringInterval = setInterval(async () => {
      if (!this.isRunning) {
        clearInterval(monitoringInterval);
        return;
      }

      try {
        await this.checkForReplayOpportunities();
        await this.monitorActiveReplays();
        await this.cleanupCompletedReplays();
      } catch (error) {
        console.error('Auto-replay monitoring error:', error);
      }
    }, checkInterval);
  }

  private async checkForReplayOpportunities(): Promise<void> {
    if (this.activeReplays.size >= this.configuration.maxConcurrentReplays) {
      return; // Too many active replays
    }

    // Get high-confidence patterns
    const highConfidencePatterns = await this.crossCampaignMemory.getPatternsByScore(
      this.configuration.confidenceThreshold
    );

    for (const pattern of highConfidencePatterns) {
      if (this.activeReplays.size >= this.configuration.maxConcurrentReplays) {
        break;
      }

      // Check if we've replayed this pattern recently
      const recentReplay = this.findRecentReplay(pattern.id);
      if (recentReplay && this.isWithinMinimumInterval(recentReplay.startedAt)) {
        continue;
      }

      // Check if pattern is suitable for replay
      if (await this.isPatternSuitableForReplay(pattern)) {
        await this.executeReplay(pattern, this.configuration);
      }
    }
  }

  private async executeReplay(pattern: CampaignPattern, config: AutoReplayConfiguration): Promise<string> {
    const replayId = `replay_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;

    try {
      // Create replay execution record
      const replay: ReplayExecution = {
        id: replayId,
        originalPatternId: pattern.id,
        campaignPlan: {} as PredictiveCampaignPlan, // Will be populated
        status: 'queued',
        startedAt: new Date(),
        modifications: [],
        learnings: []
      };

      this.activeReplays.set(replayId, replay);

      // Generate campaign plan from pattern
      const campaignPlan = await this.generateCampaignFromPattern(pattern, config);
      replay.campaignPlan = campaignPlan;

      // Apply modifications if enabled
      if (config.enableContentRefresh) {
        await this.refreshContent(replay, config);
      }

      if (config.enableTimingOptimization) {
        await this.optimizeTiming(replay, config);
      }

      if (config.enableBrandValidation) {
        await this.validateBrandAlignment(replay, config);
      }

      // Execute the campaign (in test mode, we just simulate)
      if (config.testMode) {
        replay.status = 'completed';
        replay.completedAt = new Date();
        replay.performance = this.simulatePerformance(campaignPlan);
      } else {
        replay.status = 'running';
        await this.launchCampaign(replay);
      }

      console.log(` Auto-replay ${replayId} ${config.testMode ? 'simulated' : 'launched'} for pattern ${pattern.id}`);

      return replayId;

    } catch (error) {
      const replay = this.activeReplays.get(replayId);
      if (replay) {
        replay.status = 'failed';
        replay.completedAt = new Date();
        replay.errorLog = [error.message];
      }

      console.error(`Failed to execute replay ${replayId}:`, error);
      throw error;
    }
  }

  private async generateCampaignFromPattern(pattern: CampaignPattern, config: AutoReplayConfiguration): Promise<PredictiveCampaignPlan> {
    // Convert pattern to campaign objective
    const objective = this.extractObjectiveFromPattern(pattern);

    // Generate campaign plan
    const plan = await this.predictiveGenerator.generateCampaignPlan(
      objective,
      config.budgetAllocation,
      30, // 30 days default timeline
      { segments: pattern.segments.demographics }
    );

    return plan;
  }

  private async refreshContent(replay: ReplayExecution, config: AutoReplayConfiguration): Promise<void> {
    try {
      const pattern = await this.crossCampaignMemory.getPatternsByScore(0).then(patterns => 
        patterns.find(p => p.id === replay.originalPatternId)
      );

      if (!pattern) return;

      // Generate fresh content based on winning variants
      const newContent = await this.contentAgent.generateContent({
        type: 'campaign_refresh',
        style: pattern.winningVariants.contentStyles[0] || 'professional',
        themes: replay.campaignPlan.contentStrategy.themes,
        audience: replay.campaignPlan.targetSegments[0]?.characteristics || {}
      });

      // Update campaign plan with fresh content
      replay.campaignPlan.contentStrategy.themes = [
        ...replay.campaignPlan.contentStrategy.themes.slice(0, 3),
        ...newContent.themes.slice(0, 2)
      ];

      replay.modifications.push({
        type: 'content',
        description: 'Refreshed content themes and copy',
        originalValue: pattern.winningVariants.contentStyles,
        newValue: newContent.themes,
        reason: 'Generate fresh content to avoid audience fatigue',
        confidence: 85
      });

    } catch (error) {
      console.error('Content refresh failed:', error);
    }
  }

  private async optimizeTiming(replay: ReplayExecution, config: AutoReplayConfiguration): Promise<void> {
    try {
      // Get optimal timing from smart scheduler
      const optimalSchedule = await this.smartScheduler.generateOptimalSchedule({
        campaignType: replay.campaignPlan.type,
        targetAudience: replay.campaignPlan.targetSegments[0],
        contentType: 'mixed',
        urgency: 'medium',
        duration: replay.campaignPlan.timeline.totalDuration
      });

      // Apply timing optimizations to agent orchestration
      for (const agent of replay.campaignPlan.agentOrchestration) {
        agent.parameters = {
          ...agent.parameters,
          optimalTiming: optimalSchedule.recommendations[0]?.timing
        };
      }

      replay.modifications.push({
        type: 'timing',
        description: 'Optimized agent execution timing',
        originalValue: 'Default timing',
        newValue: optimalSchedule.recommendations[0]?.timing,
        reason: 'Apply current optimal timing patterns',
        confidence: optimalSchedule.confidence
      });

    } catch (error) {
      console.error('Timing optimization failed:', error);
    }
  }

  private async validateBrandAlignment(replay: ReplayExecution, config: AutoReplayConfiguration): Promise<void> {
    try {
      // Validate brand alignment
      const brandAnalysis = await this.brandVoiceAgent.analyzeBrandCompliance({
        content: replay.campaignPlan.contentStrategy.themes.join(' '),
        tone: replay.campaignPlan.contentStrategy.tones[0] || 'professional',
        context: 'campaign_replay'
      });

      if (brandAnalysis.score < 80) {
        // Apply brand corrections
        const correctedTones = brandAnalysis.suggestions.map(s => s.replacement).slice(0, 3);
        replay.campaignPlan.contentStrategy.tones = correctedTones;

        replay.modifications.push({
          type: 'content',
          description: 'Applied brand voice corrections',
          originalValue: replay.campaignPlan.contentStrategy.tones,
          newValue: correctedTones,
          reason: `Brand compliance score was ${brandAnalysis.score}, applied corrections`,
          confidence: brandAnalysis.confidence
        });
      }

    } catch (error) {
      console.error('Brand validation failed:', error);
    }
  }

  private async launchCampaign(replay: ReplayExecution): Promise<void> {
    // This would integrate with the actual campaign execution system
    // For now, we'll simulate the launch process

    setTimeout(async () => {
      try {
        // Simulate campaign execution
        const executionTime = Math.random() * 24 * 60 * 60 * 1000; // 0-24 hours

        setTimeout(() => {
          replay.status = 'completed';
          replay.completedAt = new Date();
          replay.performance = this.simulatePerformance(replay.campaignPlan);

          // Extract learnings
          replay.learnings = this.extractLearnings(replay);

          console.log(` Auto-replay ${replay.id} completed with ROI: ${replay.performance.actualROI.toFixed(2)}`);

        }, executionTime);

      } catch (error) {
        replay.status = 'failed';
        replay.completedAt = new Date();
        replay.errorLog = [error.message];
      }
    }, 1000); // Start after 1 second
  }

  private simulatePerformance(plan: PredictiveCampaignPlan): ReplayExecution['performance'] {
    const variance = (Math.random() - 0.5) * 0.4; // 20% variance
    const actualROI = plan.expectedROI * (1 + variance);

    return {
      actualROI,
      predictedROI: plan.expectedROI,
      variance: variance * 100,
      keyMetrics: {
        openRate: plan.predictedMetrics.engagement.openRate * (1 + variance * 0.5),
        clickRate: plan.predictedMetrics.engagement.clickRate * (1 + variance * 0.5),
        conversionRate: plan.predictedMetrics.engagement.conversionRate * (1 + variance * 0.5),
        impressions: plan.predictedMetrics.reach.impressions * (1 + variance * 0.3)
      }
    };
  }

  private async monitorActiveReplays(): Promise<void> {
    for (const [id, replay] of this.activeReplays) {
      if (replay.status === 'running') {
        // Check if replay has been running too long (timeout)
        const runningTime = Date.now() - replay.startedAt.getTime();
        const timeout = 48 * 60 * 60 * 1000; // 48 hours timeout

        if (runningTime > timeout) {
          replay.status = 'failed';
          replay.completedAt = new Date();
          replay.errorLog = ['Replay timed out after 48 hours'];
          console.warn(` Auto-replay ${id} timed out`);
        }
      }
    }
  }

  private async cleanupCompletedReplays(): Promise<void> {
    for (const [id, replay] of this.activeReplays) {
      if (['completed', 'failed', 'cancelled'].includes(replay.status)) {
        // Move to history
        this.replayHistory.push(replay);
        this.activeReplays.delete(id);

        // Limit history size
        if (this.replayHistory.length > 1000) {
          this.replayHistory = this.replayHistory.slice(-500);
        }
      }
    }
  }

  private findRecentReplay(patternId: string): ReplayExecution | null {
    const allReplays = [...this.activeReplays.values(), ...this.replayHistory];
    const recentReplays = allReplays
      .filter(r => r.originalPatternId === patternId)
      .sort((a, b) => b.startedAt.getTime() - a.startedAt.getTime());

    return recentReplays[0] || null;
  }

  private isWithinMinimumInterval(lastReplayTime: Date): boolean {
    const minInterval = this.configuration.minimumTimeBetweenReplays * 60 * 60 * 1000;
    return (Date.now() - lastReplayTime.getTime()) < minInterval;
  }

  private async isPatternSuitableForReplay(pattern: CampaignPattern): Promise<boolean> {
    // Check if pattern is recent enough
    const patternAge = Date.now() - pattern.createdAt.getTime();
    const maxAge = 90 * 24 * 60 * 60 * 1000; // 90 days

    if (patternAge > maxAge) {
      return false;
    }

    // Check if pattern has sufficient performance data
    if (pattern.patternScore < this.configuration.confidenceThreshold) {
      return false;
    }

    // Check if we have necessary agents available
    const requiredAgents = pattern.winningVariants.agentSequences[0]?.split('-') || [];
    // This would check actual agent availability in a real implementation

    return true;
  }

  private extractObjectiveFromPattern(pattern: CampaignPattern): string {
    // Extract campaign objective from pattern summary
    const summary = pattern.summary.toLowerCase();

    if (summary.includes('brand awareness')) {
      return 'Increase brand awareness and recognition';
    }
    if (summary.includes('lead gen')) {
      return 'Generate qualified leads for sales team';
    }
    if (summary.includes('product launch')) {
      return 'Launch new product and drive adoption';
    }

    return 'Drive engagement and conversions';
  }

  private extractLearnings(replay: ReplayExecution): string[] {
    const learnings: string[] = [];

    if (replay.performance) {
      const perf = replay.performance;

      if (perf.variance > 10) {
        learnings.push(`Performance exceeded predictions by ${perf.variance.toFixed(1)}%`);
      } else if (perf.variance < -10) {
        learnings.push(`Performance fell short of predictions by ${Math.abs(perf.variance).toFixed(1)}%`);
      }

      if (perf.actualROI > 2) {
        learnings.push('High ROI campaign - pattern is highly effective');
      }

      // Analyze modifications impact
      const contentMods = replay.modifications.filter(m => m.type === 'content');
      if (contentMods.length > 0 && perf.actualROI > perf.predictedROI) {
        learnings.push('Content modifications had positive impact');
      }
    }

    return learnings;
  }

  private generateLearningInsights(replays: ReplayExecution[]): string[] {
    const insights: string[] = [];

    // Analyze modification success rates
    const modificationImpact = new Map<string, { positive: number, total: number }>();

    for (const replay of replays) {
      const wasSuccessful = replay.performance && replay.performance.actualROI > replay.performance.predictedROI;

      for (const mod of replay.modifications) {
        const key = `${mod.type}:${mod.description}`;
        if (!modificationImpact.has(key)) {
          modificationImpact.set(key, { positive: 0, total: 0 });
        }

        const impact = modificationImpact.get(key)!;
        impact.total++;
        if (wasSuccessful) {
          impact.positive++;
        }
      }
    }

    // Generate insights from successful modifications
    for (const [key, impact] of modificationImpact) {
      const successRate = (impact.positive / impact.total) * 100;
      if (successRate > 70 && impact.total >= 3) {
        const [type, description] = key.split(':');
        insights.push(`${type} modifications (${description}) show ${successRate.toFixed(1)}% success rate`);
      }
    }

    return insights.slice(0, 5);
  }

  private generateOptimizationRecommendations(analytics: ReplayAnalytics): string[] {
    const recommendations: string[] = [];

    if (analytics.averageROI > 2) {
      recommendations.push('Increase replay frequency - patterns are performing well');
    } else if (analytics.averageROI < 1) {
      recommendations.push('Review pattern selection criteria - ROI is below expectations');
    }

    if (analytics.successfulReplays / analytics.totalReplays < 0.7) {
      recommendations.push('Implement stricter pattern validation before replay');
    }

    // Analyze common modifications for recommendations
    const contentMods = analytics.commonModifications.filter(m => m.type === 'content');
    if (contentMods.length > 0) {
      recommendations.push('Content refresh is frequently needed - consider more dynamic content generation');
    }

    return recommendations;
  }

  async cleanup(): Promise<void> {
    await this.stopAutoReplay();
    await this.crossCampaignMemory.disconnect();
    await this.predictiveGenerator.cleanup();
  }
}

export default AutoReplayEngine;

/**
 * Campaign Runner - Core Engine for Campaign Execution
 */

import { logger, withLogging } from '@neon/utils';
import { CampaignAgent, type CampaignExecution, type CampaignPlan } from '../agents/campaign-agent';
import { getCampaignTemplate } from './campaign-templates';
import { AgentMemoryStore } from '../memory/AgentMemoryStore';

export interface CampaignRunnerConfig {
  maxConcurrentCampaigns: number;
  retryAttempts: number;
  retryDelay: number;
  monitoringInterval: number;
}

export interface CampaignSchedule {
  id: string;
  campaignId: string;
  scheduledTime: Date;
  status: 'scheduled' | 'running' | 'completed' | 'failed' | 'cancelled';
  priority: 'low' | 'medium' | 'high' | 'critical';
  recurring?: {
    interval: 'daily' | 'weekly' | 'monthly';
    endDate?: Date;
  };
}

export interface CampaignValidation {
  isValid: boolean;
  errors: string[];
  warnings: string[];
  recommendations: string[];
}

export class CampaignRunner {
  private campaignAgent: CampaignAgent;
  private memoryStore: AgentMemoryStore;
  private config: CampaignRunnerConfig;
  private runningCampaigns: Map<string, CampaignExecution> = new Map();
  private scheduledCampaigns: Map<string, CampaignSchedule> = new Map();
  private monitoringTimer?: NodeJS.Timeout;

  constructor(config: Partial<CampaignRunnerConfig> = {}) {
    this.config = {
      maxConcurrentCampaigns: 5,
      retryAttempts: 3,
      retryDelay: 60000, // 1 minute
      monitoringInterval: 30000, // 30 seconds
      ...config
    };

    this.campaignAgent = new CampaignAgent();
    this.memoryStore = new AgentMemoryStore();

    this.startMonitoring();
  }

  /**
   * Schedule a campaign for execution
   */
  async scheduleCampaign(
    campaignContext: any,
    scheduledTime: Date,
    options: {
      priority?: 'low' | 'medium' | 'high' | 'critical';
      recurring?: {
        interval: 'daily' | 'weekly' | 'monthly';
        endDate?: Date;
      };
    } = {}
  ): Promise<string> {
    return withLogging('campaign-runner', 'schedule_campaign', async () => {
      const scheduleId = `schedule_${Date.now()}`;

      // Validate campaign context
      const validation = await this.validateCampaign(campaignContext);
      if (!validation.isValid) {
        throw new Error(`Campaign validation failed: ${validation.errors.join(', ')}`);
      }

      const schedule: CampaignSchedule = {
        id: scheduleId,
        campaignId: `campaign_${Date.now()}`,
        scheduledTime,
        status: 'scheduled',
        priority: options.priority || 'medium',
        recurring: options.recurring
      };

      this.scheduledCampaigns.set(scheduleId, schedule);

      logger.info(' Campaign scheduled', {
        scheduleId,
        scheduledTime: scheduledTime.toISOString(),
        priority: schedule.priority,
        goal: campaignContext.goal
      });

      // Store in memory for persistence
      await this.memoryStore.storeMemory('campaign-runner', 'scheduled_campaign', {
        schedule,
        context: campaignContext
      }, {
        scheduleId,
        goal: campaignContext.goal,
        scheduledTime: scheduledTime.toISOString()
      });

      return scheduleId;
    });
  }

  /**
   * Execute a campaign immediately
   */
  async executeCampaign(campaignContext: any): Promise<CampaignExecution> {
    return withLogging('campaign-runner', 'execute_campaign', async () => {
      // Check concurrent campaign limits
      if (this.runningCampaigns.size >= this.config.maxConcurrentCampaigns) {
        throw new Error(`Maximum concurrent campaigns reached (${this.config.maxConcurrentCampaigns})`);
      }

      // Validate campaign
      const validation = await this.validateCampaign(campaignContext);
      if (!validation.isValid) {
        throw new Error(`Campaign validation failed: ${validation.errors.join(', ')}`);
      }

      logger.info(' Executing campaign', {
        goal: campaignContext.goal,
        channels: campaignContext.channels,
        audience: campaignContext.targetAudience
      });

      // Execute campaign using CampaignAgent
      const execution = await this.campaignAgent.execute({
        task: 'execute_campaign',
        context: campaignContext
      }) as CampaignExecution;

      // Track running campaign
      this.runningCampaigns.set(execution.id, execution);

      // Store execution in memory
      await this.memoryStore.storeMemory('campaign-runner', 'campaign_execution', execution, {
        executionId: execution.id,
        goal: campaignContext.goal,
        status: execution.status
      });

      return execution;
    });
  }

  /**
   * Validate campaign configuration
   */
  async validateCampaign(campaignContext: any): Promise<CampaignValidation> {
    const validation: CampaignValidation = {
      isValid: true,
      errors: [],
      warnings: [],
      recommendations: []
    };

    // Required fields validation
    if (!campaignContext.goal) {
      validation.errors.push('Campaign goal is required');
    }

    if (!campaignContext.channels || campaignContext.channels.length === 0) {
      validation.errors.push('At least one channel is required');
    }

    if (!campaignContext.targetAudience) {
      validation.errors.push('Target audience is required');
    }

    // Template validation
    if (campaignContext.goal) {
      const template = getCampaignTemplate(campaignContext.goal);
      if (!template) {
        validation.warnings.push(`No template found for goal: ${campaignContext.goal}`);
      } else {
        // Check if channels are supported by template
        const unsupportedChannels = campaignContext.channels?.filter(
          (channel: string) => !template.channels.includes(channel)
        );
        if (unsupportedChannels && unsupportedChannels.length > 0) {
          validation.warnings.push(
            `Channels not optimized for this goal: ${unsupportedChannels.join(', ')}`
          );
        }
      }
    }

    // Budget validation
    if (campaignContext.budget && campaignContext.budget < 100) {
      validation.warnings.push('Budget is quite low, consider increasing for better results');
    }

    // Audience validation
    if (campaignContext.targetAudience && campaignContext.targetAudience.length < 10) {
      validation.warnings.push('Target audience description is very brief, consider adding more details');
    }

    // Generate recommendations
    if (campaignContext.goal === 'lead_generation' && !campaignContext.leadMagnet) {
      validation.recommendations.push('Consider adding a lead magnet to improve conversion rates');
    }

    if (campaignContext.channels?.includes('email') && !campaignContext.emailList) {
      validation.recommendations.push('Specify email list size for better campaign planning');
    }

    if (!campaignContext.brandTone) {
      validation.recommendations.push('Define brand tone for consistent messaging');
    }

    validation.isValid = validation.errors.length === 0;

    return validation;
  }

  /**
   * Monitor running campaigns and scheduled campaigns
   */
  private startMonitoring(): void {
    this.monitoringTimer = setInterval(async () => {
      await this.monitorCampaigns();
      await this.processScheduledCampaigns();
    }, this.config.monitoringInterval);

    logger.info(' Campaign monitoring started', {
      interval: this.config.monitoringInterval,
      maxConcurrent: this.config.maxConcurrentCampaigns
    });
  }

  /**
   * Monitor active campaigns for health and performance
   */
  private async monitorCampaigns(): Promise<void> {
    for (const [executionId, execution] of this.runningCampaigns) {
      try {
        // Check if campaign is still running
        if (execution.status === 'completed' || execution.status === 'failed') {
          this.runningCampaigns.delete(executionId);

          logger.info(' Campaign monitoring completed', {
            executionId,
            status: execution.status,
            progress: execution.progress,
            metrics: execution.metrics
          });

          // Store final results
          await this.memoryStore.storeMemory('campaign-runner', 'campaign_completed', {
            executionId,
            finalStatus: execution.status,
            finalMetrics: execution.metrics,
            completedAt: new Date()
          }, {
            executionId,
            status: execution.status,
            revenue: execution.metrics.revenue.toString()
          });
        } else {
          // Monitor campaign health
          await this.checkCampaignHealth(execution);
        }
      } catch (error) {
        logger.error('Campaign monitoring error', { executionId, error });
      }
    }
  }

  /**
   * Process scheduled campaigns that are due
   */
  private async processScheduledCampaigns(): Promise<void> {
    const now = new Date();

    for (const [scheduleId, schedule] of this.scheduledCampaigns) {
      if (schedule.status === 'scheduled' && schedule.scheduledTime <= now) {
        try {
          // Check if we can run the campaign
          if (this.runningCampaigns.size >= this.config.maxConcurrentCampaigns) {
            logger.warn(' Campaign delayed due to capacity', { scheduleId });
            continue;
          }

          // Get campaign context from memory
          const memories = await this.memoryStore.getRecentMemories('campaign-runner', 50);
          const campaignMemory = memories.find(m => 
            m.data?.schedule?.id === scheduleId
          );

          if (!campaignMemory) {
            logger.error('Campaign context not found in memory', { scheduleId });
            schedule.status = 'failed';
            continue;
          }

          const campaignContext = campaignMemory.data.context;

          // Execute the campaign
          schedule.status = 'running';
          const execution = await this.executeCampaign(campaignContext);

          schedule.status = 'completed';
          logger.info(' Scheduled campaign executed', {
            scheduleId,
            executionId: execution.id
          });

          // Handle recurring campaigns
          if (schedule.recurring) {
            await this.scheduleRecurringCampaign(schedule, campaignContext);
          } else {
            this.scheduledCampaigns.delete(scheduleId);
          }

        } catch (error) {
          schedule.status = 'failed';
          logger.error('Scheduled campaign execution failed', { scheduleId, error });
        }
      }
    }
  }

  /**
   * Check campaign health and performance
   */
  private async checkCampaignHealth(execution: CampaignExecution): Promise<void> {
    const healthChecks = {
      stuckProgress: execution.progress === 0 && Date.now() - new Date(execution.id.split('_')[1]).getTime() > 600000, // 10 minutes
      lowPerformance: execution.metrics.delivered > 100 && execution.metrics.opened / execution.metrics.delivered < 0.1,
      highBounceRate: execution.metrics.delivered > 0 && (execution.metrics.delivered - execution.metrics.opened) / execution.metrics.delivered > 0.3
    };

    if (healthChecks.stuckProgress) {
      logger.warn(' Campaign appears stuck', { 
        executionId: execution.id,
        progress: execution.progress,
        currentStep: execution.currentStep
      });

      // Attempt to recover or flag for manual intervention
      await this.attemptCampaignRecovery(execution);
    }

    if (healthChecks.lowPerformance) {
      logger.warn(' Campaign performance below threshold', {
        executionId: execution.id,
        openRate: execution.metrics.opened / execution.metrics.delivered,
        delivered: execution.metrics.delivered
      });
    }

    if (healthChecks.highBounceRate) {
      logger.warn(' High bounce rate detected', {
        executionId: execution.id,
        bounceRate: (execution.metrics.delivered - execution.metrics.opened) / execution.metrics.delivered
      });
    }
  }

  /**
   * Attempt to recover a stuck campaign
   */
  private async attemptCampaignRecovery(execution: CampaignExecution): Promise<void> {
    logger.info(' Attempting campaign recovery', { executionId: execution.id });

    try {
      // Get the campaign from the agent and try to resume
      const campaignFromAgent = this.campaignAgent.getCampaign(execution.id);
      if (campaignFromAgent) {
        // Log recovery attempt
        execution.agentActivity.push({
          agentId: 'campaign-runner',
          action: 'recovery_attempt',
          timestamp: new Date(),
          result: 'Attempting to recover stuck campaign'
        });
      }
    } catch (error) {
      logger.error('Campaign recovery failed', { executionId: execution.id, error });
    }
  }

  /**
   * Schedule next occurrence of a recurring campaign
   */
  private async scheduleRecurringCampaign(
    originalSchedule: CampaignSchedule,
    campaignContext: any
  ): Promise<void> {
    if (!originalSchedule.recurring) return;

    const nextScheduledTime = this.calculateNextScheduledTime(
      originalSchedule.scheduledTime,
      originalSchedule.recurring.interval
    );

    // Check if we should continue recurring
    if (originalSchedule.recurring.endDate && nextScheduledTime > originalSchedule.recurring.endDate) {
      this.scheduledCampaigns.delete(originalSchedule.id);
      logger.info(' Recurring campaign series completed', { scheduleId: originalSchedule.id });
      return;
    }

    // Schedule next occurrence
    await this.scheduleCampaign(campaignContext, nextScheduledTime, {
      priority: originalSchedule.priority,
      recurring: originalSchedule.recurring
    });

    // Remove current schedule
    this.scheduledCampaigns.delete(originalSchedule.id);
  }

  /**
   * Calculate next scheduled time for recurring campaigns
   */
  private calculateNextScheduledTime(currentTime: Date, interval: string): Date {
    const nextTime = new Date(currentTime);

    switch (interval) {
      case 'daily':
        nextTime.setDate(nextTime.getDate() + 1);
        break;
      case 'weekly':
        nextTime.setDate(nextTime.getDate() + 7);
        break;
      case 'monthly':
        nextTime.setMonth(nextTime.getMonth() + 1);
        break;
    }

    return nextTime;
  }

  /**
   * Get current status of all campaigns
   */
  getCampaignStatus(): {
    running: CampaignExecution[];
    scheduled: CampaignSchedule[];
    statistics: {
      totalRunning: number;
      totalScheduled: number;
      capacity: number;
      utilizationRate: number;
    };
  } {
    const running = Array.from(this.runningCampaigns.values());
    const scheduled = Array.from(this.scheduledCampaigns.values());

    return {
      running,
      scheduled,
      statistics: {
        totalRunning: running.length,
        totalScheduled: scheduled.length,
        capacity: this.config.maxConcurrentCampaigns,
        utilizationRate: running.length / this.config.maxConcurrentCampaigns
      }
    };
  }

  /**
   * Cancel a scheduled campaign
   */
  async cancelScheduledCampaign(scheduleId: string): Promise<boolean> {
    const schedule = this.scheduledCampaigns.get(scheduleId);
    if (!schedule) {
      return false;
    }

    schedule.status = 'cancelled';
    this.scheduledCampaigns.delete(scheduleId);

    logger.info(' Campaign cancelled', { scheduleId });
    return true;
  }

  /**
   * Stop monitoring and cleanup
   */
  shutdown(): void {
    if (this.monitoringTimer) {
      clearInterval(this.monitoringTimer);
      this.monitoringTimer = undefined;
    }

    logger.info(' Campaign runner shutdown');
  }
}

