  });

  // Phase 6: Monitoring & Optimization
  subgoals.push({
    id: 'monitoring_optimization',
    title: 'Performance Monitoring & Optimization',
    description: 'Monitor results and continuously optimize performance',
    priority: 6,
    estimatedTime: 180, // 3 hours spread over campaign duration
    requiredCapabilities: ['performance_monitoring', 'data_analysis', 'optimization_tuning'],
    successCriteria: [
      'Performance dashboard active',
      'Optimization recommendations implemented',
      'ROI tracking operational',
      'Success metrics achieved'
    ],
  });

  return subgoals;
}

/**
 * Create agent assignment sequence
 */
async function createAgentSequence(subgoals: SubGoal[], analysis: GoalAnalysis): Promise<AgentAssignment[]> {
  const assignments: AgentAssignment[] = [];

  // Phase 1: Research (Trend + Insight Agents)
  assignments.push({
    agentType: AgentType.TREND,
    phase: 1,
    tasks: [
      'Analyze current market trends',
      'Identify trending topics and hashtags',
      'Research competitor activities',
    ],
    dependencies: [],
    estimatedDuration: 30,
    fallbackAgents: [AgentType.INSIGHT],
  });

  assignments.push({
    agentType: AgentType.INSIGHT,
    phase: 1,
    tasks: [
      'Generate strategic insights',
      'Analyze historical performance data',
      'Identify optimization opportunities',
    ],
    dependencies: ['trend_analysis_complete'],
    estimatedDuration: 30,
  });

  // Phase 2: Planning (Brand Voice + Goal Planner)
  assignments.push({
    agentType: AgentType.BRAND_VOICE,
    phase: 2,
    tasks: [
      'Validate brand alignment',
      'Ensure voice consistency',
      'Review messaging guidelines',
    ],
    dependencies: ['insights_complete'],
    estimatedDuration: 20,
  });

  assignments.push({
    agentType: AgentType.GOAL_PLANNER,
    phase: 2,
    tasks: [
      'Finalize strategic plan',
      'Allocate resources',
      'Set success criteria',
    ],
    dependencies: ['brand_validation_complete'],
    estimatedDuration: 40,
  });

  // Phase 3: Content Creation (category-specific)
  if (analysis.category === 'AWARENESS' || analysis.category === 'ENGAGEMENT') {
    assignments.push({
      agentType: AgentType.CONTENT,
      phase: 3,
      tasks: [
        'Generate content concepts',
        'Create platform-specific content',
        'Optimize for engagement',
      ],
      dependencies: ['strategic_plan_complete'],
      estimatedDuration: 60,
      fallbackAgents: [AgentType.DESIGN],
    });

    assignments.push({
      agentType: AgentType.DESIGN,
      phase: 3,
      tasks: [
        'Create visual assets',
        'Ensure design consistency',
        'Optimize for platforms',
      ],
      dependencies: ['content_concepts_ready'],
      estimatedDuration: 45,
    });
  }

  // Phase 4: Technical Setup
  if (analysis.category === 'CONVERSION') {
    assignments.push({
      agentType: AgentType.AD,
      phase: 4,
      tasks: [
        'Set up ad campaigns',
        'Configure targeting parameters',
        'Implement tracking pixels',
      ],
      dependencies: ['content_ready'],
      estimatedDuration: 45,
    });

    assignments.push({
      agentType: AgentType.SEO,
      phase: 4,
      tasks: [
        'Optimize landing pages',
        'Implement SEO best practices',
        'Set up conversion tracking',
      ],
      dependencies: ['ad_setup_complete'],
      estimatedDuration: 30,
    });
  }

  // Phase 5: Execution (platform-specific agents)
  const executionAgents = getExecutionAgents(analysis);
  executionAgents.forEach((agentType, index) => {
    assignments.push({
      agentType,
      phase: 5,
      tasks: getPlatformTasks(agentType),
      dependencies: index === 0 ? ['setup_complete'] : [`${executionAgents[index - 1]}_launched`],
      estimatedDuration: 30,
    });
  });

  return assignments;
}

// Helper functions
function extractIntent(goal: string): string {
  // Simple intent extraction - in production, would use NLP
  const goalLower = goal.toLowerCase();

  if (goalLower.includes('increase') || goalLower.includes('boost') || goalLower.includes('improve')) {
    return goal.split(/increase|boost|improve/i)[1]?.trim() || 'performance improvement';
  } else if (goalLower.includes('generate') || goalLower.includes('create')) {
    return goal.split(/generate|create/i)[1]?.trim() || 'content generation';
  } else if (goalLower.includes('launch') || goalLower.includes('start')) {
    return goal.split(/launch|start/i)[1]?.trim() || 'campaign launch';
  }

  return goal.length > 50 ? goal.substring(0, 50) + '...' : goal;
}

function extractRequiredSkills(goal: string, category: GoalAnalysis['category']): string[] {
  const skills: string[] = [];
  const goalLower = goal.toLowerCase();

  // Base skills by category
  switch (category) {
    case 'AWARENESS':
      skills.push('brand_building', 'content_creation', 'social_media');
      break;
    case 'ENGAGEMENT':
      skills.push('community_management', 'content_optimization', 'interactive_design');
      break;
    case 'CONVERSION':
      skills.push('funnel_optimization', 'ad_management', 'analytics');
      break;
    case 'RETENTION':
      skills.push('email_marketing', 'customer_success', 'loyalty_programs');
      break;
    case 'GROWTH':
      skills.push('scaling_strategies', 'automation', 'data_analysis');
      break;
  }

  // Add specific skills based on goal content
  if (goalLower.includes('seo')) skills.push('search_optimization');
  if (goalLower.includes('email')) skills.push('email_marketing');
  if (goalLower.includes('social')) skills.push('social_media_management');
  if (goalLower.includes('video')) skills.push('video_production');
  if (goalLower.includes('design')) skills.push('graphic_design');

  return [...new Set(skills)]; // Remove duplicates
}

function getExecutionTime(analysis: GoalAnalysis): number {
  const baseTime = 60; // 1 hour base

  let multiplier = 1;
  switch (analysis.urgency) {
    case 'CRITICAL': multiplier = 0.7; break;  // Faster execution
    case 'HIGH': multiplier = 0.8; break;
    case 'MEDIUM': multiplier = 1; break;
    case 'LOW': multiplier = 1.5; break;      // More thorough execution
  }

  return Math.round(baseTime * multiplier);
}

function getExecutionCapabilities(analysis: GoalAnalysis): string[] {
  const capabilities = ['campaign_execution', 'quality_assurance'];

  switch (analysis.category) {
    case 'AWARENESS':
      capabilities.push('brand_amplification', 'reach_optimization');
      break;
    case 'ENGAGEMENT':
      capabilities.push('community_engagement', 'interaction_optimization');
      break;
    case 'CONVERSION':
      capabilities.push('conversion_optimization', 'funnel_management');
      break;
    case 'RETENTION':
      capabilities.push('relationship_building', 'customer_nurturing');
      break;
    case 'GROWTH':
      capabilities.push('scale_management', 'growth_hacking');
      break;
  }

  return capabilities;
}

function getExecutionAgents(analysis: GoalAnalysis): AgentType[] {
  const agents: AgentType[] = [];

  switch (analysis.category) {
    case 'AWARENESS':
    case 'ENGAGEMENT':
      agents.push(AgentType.SOCIAL_POSTING, AgentType.CONTENT);
      break;
    case 'CONVERSION':
      agents.push(AgentType.AD, AgentType.EMAIL_MARKETING);
      break;
    case 'RETENTION':
      agents.push(AgentType.EMAIL_MARKETING, AgentType.CUSTOMER_SUPPORT);
      break;
    case 'GROWTH':
      agents.push(AgentType.AD, AgentType.SEO, AgentType.SOCIAL_POSTING);
      break;
  }

  return agents;
}

function getPlatformTasks(agentType: AgentType): string[] {
  switch (agentType) {
    case AgentType.SOCIAL_POSTING:
      return ['Schedule social posts', 'Monitor engagement', 'Respond to interactions'];
    case AgentType.CONTENT:
      return ['Publish content', 'Update content calendar', 'Track content performance'];
    case AgentType.AD:
      return ['Launch ad campaigns', 'Monitor ad performance', 'Adjust targeting'];
    case AgentType.EMAIL_MARKETING:
      return ['Send email campaigns', 'Track open rates', 'Segment audiences'];
    case AgentType.SEO:
      return ['Optimize content', 'Monitor rankings', 'Update meta data'];
    default:
      return ['Execute assigned tasks', 'Monitor performance', 'Report results'];
  }
}

function calculateTotalTime(subgoals: SubGoal[], agentSequence: AgentAssignment[]): number {
  const subgoalTime = subgoals.reduce((total, subgoal) => total + subgoal.estimatedTime, 0);
  const agentTime = agentSequence.reduce((total, assignment) => total + assignment.estimatedDuration, 0);

  // Return the maximum of the two (assuming some parallelization)
  return Math.max(subgoalTime, agentTime);
}

function determineComplexity(subgoals: SubGoal[], agentSequence: AgentAssignment[]): DecomposedGoal['complexity'] {
  const totalSubgoals = subgoals.length;
  const totalAgents = agentSequence.length;
  const totalTime = calculateTotalTime(subgoals, agentSequence);

  if (totalSubgoals <= 3 && totalAgents <= 3 && totalTime <= 180) return 'LOW';
  if (totalSubgoals <= 5 && totalAgents <= 6 && totalTime <= 360) return 'MEDIUM';
  if (totalSubgoals <= 8 && totalAgents <= 10 && totalTime <= 600) return 'HIGH';
  return 'CRITICAL';
}

function identifyRiskFactors(analysis: GoalAnalysis, subgoals: SubGoal[]): string[] {
  const risks: string[] = [];

  if (analysis.urgency === 'CRITICAL') {
    risks.push('Tight timeline may impact quality');
  }

  if (analysis.category === 'CONVERSION' && !analysis.targetMetrics.length) {
    risks.push('No specific conversion targets defined');
  }

  if (subgoals.length > 6) {
    risks.push('Complex goal with many dependencies');
  }

  if (analysis.resourceRequirements.humanOverSight) {
    risks.push('Requires human oversight for critical decisions');
  }

  return risks;
}

function extractDependencies(subgoals: SubGoal[], agentSequence: AgentAssignment[]): string[] {
  const dependencies = new Set<string>();

  agentSequence.forEach(assignment => {
    assignment.dependencies.forEach(dep => dependencies.add(dep));
  });

  return Array.from(dependencies);
}

function generateSuccessMetrics(analysis: GoalAnalysis): string[] {
  const metrics = ['Goal completion rate', 'Time to completion', 'Resource efficiency'];

  analysis.targetMetrics.forEach(target => {
    metrics.push(`${target.metric}: ${target.target}${target.unit} in ${target.timeframe}`);
  });

  return metrics;
}

/**
 * Reasoning Protocol - Multi-Agent Planning and Consensus System
 * Handles plan proposals, evaluations, and consensus rounds
 */

import { PrismaClient } from '@prisma/client';
import { AgentType, ConsensusResult } from '@prisma/client';
import { SharedIntentModel, AgentIntent } from '../shared/shared-intent-model';

const prisma = new PrismaClient();

export interface ProposedPlan {
  id?: string;
  goalId: string;
  proposingAgent: string;
  agentType: AgentType;
  title: string;
  description: string;
  subgoals: SubGoal[];
  agentSequence: AgentAssignment[];
  estimatedTime: number; // minutes
  estimatedCost?: number;
  brandAlignment: number; // 0-1
  feasibility: number; // 0-1
  confidence: number; // 0-1
  riskFactors: string[];
  dependencies: string[];
  metadata?: any;
}

export interface SubGoal {
  id: string;
  title: string;
  description: string;
  priority: number;
  estimatedTime: number;
  requiredCapabilities: string[];
  successCriteria: string[];
}

export interface AgentAssignment {
  agentType: AgentType;
  phase: number;
  tasks: string[];
  dependencies: string[];
  estimatedDuration: number;
  fallbackAgents?: AgentType[];
}

export interface PlanEvaluation {
  evaluatorAgent: string;
  agentType: AgentType;
  score: number; // 0-1
  reasoning: string;
  alignment: {
    brand: number;
    feasibility: number;
    efficiency: number;
    riskLevel: number;
  };
  suggestions?: string[];
  blockers?: string[];
  timestamp: Date;
}

export interface ConsensusRound {
  id?: string;
  goalPlanId: string;
  roundNumber: number;
  proposedPlan: ProposedPlan;
  participantAgents: string[];
  evaluations: PlanEvaluation[];
  quorum: number; // 0-1 (e.g., 0.7 = 70% agreement)
  result?: ConsensusResult;
  finalScore?: number;
  winningPlan?: ProposedPlan;
  completedAt?: Date;
}

/**
 * Agent proposes a plan for a goal
 */
export async function proposePlan(
  goalId: string,
  proposingAgent: string,
  agentType: AgentType,
  planDetails: Partial<ProposedPlan>
): Promise<ProposedPlan> {
  try {
    const sharedIntent = SharedIntentModel.getInstance();

    // Check if agent is available to propose
    const availability = await sharedIntent.getAgentAvailability(proposingAgent);
    if (!availability.isAvailable) {
      throw new Error(`Agent ${proposingAgent} is not available for planning`);
    }

    // Create the proposed plan with intelligent defaults
    const proposedPlan: ProposedPlan = {
      goalId,
      proposingAgent,
      agentType,
      title: planDetails.title || `Plan by ${agentType}`,
      description: planDetails.description || '',
      subgoals: planDetails.subgoals || [],
      agentSequence: planDetails.agentSequence || [],
      estimatedTime: planDetails.estimatedTime || 0,
      estimatedCost: planDetails.estimatedCost,
      brandAlignment: planDetails.brandAlignment || 0.8,
      feasibility: planDetails.feasibility || 0.7,
      confidence: planDetails.confidence || 0.6,
      riskFactors: planDetails.riskFactors || [],
      dependencies: planDetails.dependencies || [],
      metadata: planDetails.metadata || {},
    };

    // Validate plan structure
    await validatePlan(proposedPlan);

    // Store intention to propose
    await sharedIntent.broadcastIntent({
      agentId: proposingAgent,
      agentType,
      intention: `propose_plan_for_goal_${goalId}`,
      resources: {
        timeRequired: 10, // Planning time
        dependencies: planDetails.dependencies || [],
      },
      priority: 8,
      status: 'PROPOSED' as any,
      confidence: proposedPlan.confidence,
      estimatedDuration: 15,
      metadata: { goalId, planTitle: proposedPlan.title },
    });

    console.log(` [ReasoningProtocol] Plan proposed by ${agentType}: ${proposedPlan.title}`);
    return proposedPlan;
  } catch (error) {
    console.error('[ReasoningProtocol] Error proposing plan:', error);
    throw error;
  }
}

/**
 * Agent evaluates a proposed plan
 */
export async function evaluatePlan(
  plan: ProposedPlan,
  evaluatorAgent: string,
  evaluatorType: AgentType
): Promise<PlanEvaluation> {
  try {
    const sharedIntent = SharedIntentModel.getInstance();

    // Get similar plans for context
    const similarIntentions = await sharedIntent.getSimilarIntentions(
      plan.description,
      evaluatorType
    );

    // Calculate evaluation scores based on agent expertise
    const evaluation = await calculateEvaluationScore(plan, evaluatorType, similarIntentions);

    const planEvaluation: PlanEvaluation = {
      evaluatorAgent,
      agentType: evaluatorType,
      score: evaluation.overallScore,
      reasoning: evaluation.reasoning,
      alignment: evaluation.alignment,
      suggestions: evaluation.suggestions,
      blockers: evaluation.blockers,
      timestamp: new Date(),
    };

    // Broadcast evaluation intent
    await sharedIntent.broadcastIntent({
      agentId: evaluatorAgent,
      agentType: evaluatorType,
      intention: `evaluate_plan_${plan.id}`,
      resources: {
        timeRequired: 5,
        dependencies: [],
      },
      priority: 7,
      status: 'EXECUTING' as any,
      confidence: evaluation.confidenceInEvaluation,
      estimatedDuration: 10,
      metadata: { 
        planId: plan.id, 
        evaluationScore: evaluation.overallScore 
      },
    });

    console.log(` [ReasoningProtocol] Plan evaluated by ${evaluatorType}: Score ${evaluation.overallScore.toFixed(2)}`);
    return planEvaluation;
  } catch (error) {
    console.error('[ReasoningProtocol] Error evaluating plan:', error);
    throw error;
  }
}

/**
 * Conduct a consensus round for plan selection
 */
export async function consensusRound(
  goalPlanId: string,
  proposedPlans: ProposedPlan[],
  participantAgents: Array<{ agentId: string; agentType: AgentType }>,
  quorum: number = 0.7
): Promise<ConsensusRound> {
  try {
    if (proposedPlans.length === 0) {
      throw new Error('No plans provided for consensus');
    }

    const roundNumber = await getNextRoundNumber(goalPlanId);

    // Initialize consensus round
    const consensusRound: ConsensusRound = {
      goalPlanId,
      roundNumber,
      proposedPlan: proposedPlans[0], // Primary plan being voted on
      participantAgents: participantAgents.map(a => a.agentId),
      evaluations: [],
      quorum,
    };

    // Collect evaluations from all participating agents
    for (const participant of participantAgents) {
      try {
        const evaluation = await evaluatePlan(
          proposedPlans[0],
          participant.agentId,
          participant.agentType
        );
        consensusRound.evaluations.push(evaluation);
      } catch (error) {
        console.warn(`[ReasoningProtocol] Agent ${participant.agentId} failed to evaluate:`, error);
        // Continue with other evaluations
      }
    }

    // Calculate consensus result
    const result = await calculateConsensus(consensusRound);

    // Save consensus round to database
    const savedConsensus = await prisma.agentConsensus.create({
      data: {
        goalPlanId,
        roundNumber,
        proposedPlan: proposedPlans[0],
        participantAgents: participantAgents.map(a => a.agentId),
        votes: consensusRound.evaluations.reduce((acc, eval) => {
          acc[eval.evaluatorAgent] = {
            score: eval.score,
            reasoning: eval.reasoning,
            alignment: eval.alignment,
          };
          return acc;
        }, {} as any),
        quorum,
        result: result.result,
        finalScore: result.finalScore,
        winningPlan: result.winningPlan,
        completedAt: result.result !== ConsensusResult.PENDING ? new Date() : null,
      },
    });

    consensusRound.id = savedConsensus.id;
    consensusRound.result = result.result;
    consensusRound.finalScore = result.finalScore;
    consensusRound.winningPlan = result.winningPlan;
    consensusRound.completedAt = result.result !== ConsensusResult.PENDING ? new Date() : savedConsensus.completedAt || undefined;

    console.log(` [ReasoningProtocol] Consensus round ${roundNumber} completed: ${result.result}`);
    console.log(`   Final Score: ${result.finalScore?.toFixed(2)}, Quorum: ${quorum * 100}%`);

    return consensusRound;
  } catch (error) {
    console.error('[ReasoningProtocol] Error in consensus round:', error);
    throw error;
  }
}

/**
 * Get learning insights from previous consensus rounds
 */
export async function getConsensusInsights(agentType?: AgentType): Promise<{
  averageScore: number;
  successfulPlans: ProposedPlan[];
  commonFailureReasons: string[];
  bestPractices: string[];
}> {
  try {
    const consensusRounds = await prisma.agentConsensus.findMany({
      where: {
        result: ConsensusResult.APPROVED,
        completedAt: { not: null },
      },
      orderBy: { createdAt: 'desc' },
      take: 50,
    });

    const scores = consensusRounds
      .filter(round => round.finalScore !== null)
      .map(round => round.finalScore!);

    const averageScore = scores.length > 0 ? 
      scores.reduce((sum, score) => sum + score, 0) / scores.length : 0;

    const successfulPlans = consensusRounds
      .filter(round => round.finalScore && round.finalScore > 0.8)
      .map(round => round.winningPlan as ProposedPlan)
      .filter(plan => plan !== null);

    return {
      averageScore,
      successfulPlans,
      commonFailureReasons: [
        'Insufficient brand alignment',
        'Resource conflicts',
        'Unrealistic timeline',
        'Missing dependencies'
      ],
      bestPractices: [
        'Include fallback agents in sequences',
        'Validate resource availability early',
        'Maintain brand alignment > 0.8',
        'Break down complex goals into smaller subgoals'
      ],
    };
  } catch (error) {
    console.error('[ReasoningProtocol] Error getting consensus insights:', error);
    return {
      averageScore: 0,
      successfulPlans: [],
      commonFailureReasons: [],
      bestPractices: [],
    };
  }
}

// Helper functions
async function validatePlan(plan: ProposedPlan): Promise<void> {
  if (!plan.title || plan.title.trim().length === 0) {
    throw new Error('Plan must have a title');
  }

  if (plan.subgoals.length === 0) {
    throw new Error('Plan must have at least one subgoal');
  }

  if (plan.agentSequence.length === 0) {
    throw new Error('Plan must have at least one agent assignment');
  }

  if (plan.brandAlignment < 0 || plan.brandAlignment > 1) {
    throw new Error('Brand alignment must be between 0 and 1');
  }
}

async function calculateEvaluationScore(
  plan: ProposedPlan,
  evaluatorType: AgentType,
  similarIntentions: AgentIntent[]
): Promise<{
  overallScore: number;
  reasoning: string;
  alignment: PlanEvaluation['alignment'];
  suggestions: string[];
  blockers: string[];
  confidenceInEvaluation: number;
}> {
  // Agent-specific evaluation logic
  const weights = getEvaluationWeights(evaluatorType);

  const brandScore = plan.brandAlignment;
  const feasibilityScore = plan.feasibility;
  const efficiencyScore = Math.min(1, 1 / (plan.estimatedTime / 60)); // Efficiency based on time
  const riskScore = Math.max(0, 1 - (plan.riskFactors.length * 0.1));

  const overallScore = (
    brandScore * weights.brand +
    feasibilityScore * weights.feasibility +
    efficiencyScore * weights.efficiency +
    riskScore * weights.risk
  );

  // Generate reasoning based on evaluation
  const reasoningParts = [];
  if (brandScore < 0.7) reasoningParts.push('Brand alignment could be improved');
  if (feasibilityScore < 0.6) reasoningParts.push('Feasibility concerns exist');
  if (plan.riskFactors.length > 3) reasoningParts.push('High risk factors identified');
  if (plan.estimatedTime > 480) reasoningParts.push('Timeline may be too aggressive'); // 8 hours

  const reasoning = reasoningParts.length > 0 ? 
    reasoningParts.join('; ') : 
    'Plan meets quality standards across all evaluation criteria';

  // Generate suggestions based on similar successful plans
  const suggestions: string[] = [];
  if (similarIntentions.length > 0) {
    const avgSuccessfulTime = similarIntentions
      .filter(intent => intent.confidence > 0.8)
      .reduce((sum, intent) => sum + (intent.estimatedDuration || 30), 0) / 
      similarIntentions.length;

    if (plan.estimatedTime < avgSuccessfulTime * 0.8) {
      suggestions.push('Consider allocating more time based on similar successful plans');
    }
  }

  return {
    overallScore: Math.max(0, Math.min(1, overallScore)),
    reasoning,
    alignment: {
      brand: brandScore,
      feasibility: feasibilityScore,
      efficiency: efficiencyScore,
      riskLevel: 1 - riskScore,
    },
    suggestions,
    blockers: plan.riskFactors.filter(risk => 
      risk.toLowerCase().includes('blocker') || 
      risk.toLowerCase().includes('critical')
    ),
    confidenceInEvaluation: 0.8 + (similarIntentions.length * 0.02), // Higher confidence with more context
  };
}

function getEvaluationWeights(agentType: AgentType): {
  brand: number;
  feasibility: number;
  efficiency: number;
  risk: number;
} {
  // Different agents prioritize different aspects
  switch (agentType) {
    case AgentType.BRAND_VOICE:
      return { brand: 0.5, feasibility: 0.2, efficiency: 0.1, risk: 0.2 };
    case AgentType.TREND:
      return { brand: 0.2, feasibility: 0.3, efficiency: 0.3, risk: 0.2 };
    case AgentType.SEO:
      return { brand: 0.3, feasibility: 0.3, efficiency: 0.2, risk: 0.2 };
    default:
      return { brand: 0.25, feasibility: 0.25, efficiency: 0.25, risk: 0.25 };
  }
}

async function getNextRoundNumber(goalPlanId: string): Promise<number> {
  const lastRound = await prisma.agentConsensus.findFirst({
    where: { goalPlanId },
    orderBy: { roundNumber: 'desc' },
  });

  return (lastRound?.roundNumber || 0) + 1;
}

async function calculateConsensus(consensusRound: ConsensusRound): Promise<{
  result: ConsensusResult;
  finalScore?: number;
  winningPlan?: ProposedPlan;
}> {
  const evaluations = consensusRound.evaluations;

  if (evaluations.length === 0) {
    return { result: ConsensusResult.QUORUM_NOT_MET };
  }

  // Calculate weighted score (all agents equal weight for now)
  const totalScore = evaluations.reduce((sum, eval) => sum + eval.score, 0);
  const finalScore = totalScore / evaluations.length;

  // Check if we have enough participants
  const participationRate = evaluations.length / consensusRound.participantAgents.length;
  if (participationRate < consensusRound.quorum) {
    return { result: ConsensusResult.QUORUM_NOT_MET, finalScore };
  }

  // Check for approval (consensus threshold)
  const approvalCount = evaluations.filter(eval => eval.score >= 0.7).length;
  const approvalRate = approvalCount / evaluations.length;

  if (approvalRate >= consensusRound.quorum && finalScore >= 0.7) {
    return {
      result: ConsensusResult.APPROVED,
      finalScore,
      winningPlan: consensusRound.proposedPlan,
    };
  } else if (finalScore < 0.4) {
    return { result: ConsensusResult.REJECTED, finalScore };
  } else {
    return { result: ConsensusResult.PENDING, finalScore };
  }
}

